#!/usr/bin/env ts-node

import * as XLSX from 'xlsx';
import { createClient } from '@supabase/supabase-js';
import { config } from 'dotenv';
import { resolve } from 'path';

// Load environment variables
config();

// Types
interface GS1Row {
  GTIN?: string;
  'GPC Brick Code'?: string;
  Brand?: string;
  'Product Name'?: string;
  'Net Content'?: string;
  'Net Content UOM'?: string;
  'Gross Weight'?: number;
  'Net Weight'?: number;
  'Base Unit'?: boolean | string;
  'Orderable Unit'?: boolean | string;
  'Despatch Unit'?: boolean | string;
  'Country of Origin'?: string;
  'Effective From'?: string | Date;
  'Effective To'?: string | Date;
  'Product Lifecycle Status'?: string;
  Description?: string;
  Category?: string;
  SKU?: string;
  Price?: number;
  Unit?: string;
}

interface ProductInsert {
  gtin: string;
  sku: string;
  name: string;
  gpc_brick_code?: string | undefined;
  brand?: string | undefined;
  description?: string | undefined;
  category?: string | undefined;
  price?: number | undefined;
  unit?: string | undefined;
  net_content_value?: number | undefined;
  net_content_uom?: string | undefined;
  gross_weight?: number | undefined;
  net_weight?: number | undefined;
  base_unit_indicator?: boolean | undefined;
  orderable_unit_indicator?: boolean | undefined;
  despatch_unit_indicator?: boolean | undefined;
  country_of_origin?: string | undefined;
  effective_from_date?: string | undefined;
  effective_to_date?: string | undefined;
  product_lifecycle_status?: string | undefined;
  active: boolean;
}

interface ImportStats {
  totalRows: number;
  processed: number;
  inserted: number;
  updated: number;
  skipped: number;
  errors: string[];
}

// Initialize Supabase client
const supabaseUrl = process.env.SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseKey) {
  console.error(
    '‚ùå Missing required environment variables: SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY'
  );
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseKey);

/**
 * Validates GTIN format (8, 12, 13, or 14 digits)
 */
function validateGTIN(gtin: string): boolean {
  if (!gtin) return false;

  // Remove any non-numeric characters
  const cleanGtin = gtin.toString().replace(/\D/g, '');

  // Check if it's a valid length (8, 12, 13, or 14 digits)
  const validLengths = [8, 12, 13, 14];
  return validLengths.includes(cleanGtin.length);
}

/**
 * Converts string or boolean values to boolean
 */
function parseBoolean(value: any): boolean {
  if (typeof value === 'boolean') return value;
  if (typeof value === 'string') {
    const lower = value.toLowerCase().trim();
    return (
      lower === 'true' || lower === 'yes' || lower === '1' || lower === 'y'
    );
  }
  return false;
}

/**
 * Parses date strings to ISO format
 */
function parseDate(dateValue: any): string | undefined {
  if (!dateValue) return undefined;

  try {
    let date: Date;

    if (dateValue instanceof Date) {
      date = dateValue;
    } else if (typeof dateValue === 'number') {
      // Excel date serial number
      date = XLSX.SSF.parse_date_code(dateValue);
    } else {
      date = new Date(dateValue);
    }

    if (isNaN(date.getTime())) return undefined;

    return date.toISOString().split('T')[0]; // Return YYYY-MM-DD format
  } catch {
    return undefined;
  }
}

/**
 * Parses net content value from string (e.g., "250g" -> 250)
 */
function parseNetContent(netContent: string): { value?: number; uom?: string } {
  if (!netContent) return {};

  const contentStr = netContent.toString().trim();
  const match = contentStr.match(/^(\d+(?:\.\d+)?)\s*([a-zA-Z]+)?$/);

  if (match && match[1]) {
    return {
      value: parseFloat(match[1]),
      uom: match[2] ? match[2].toLowerCase() : undefined,
    };
  }

  // Try to extract just the number if no unit
  const numberMatch = contentStr.match(/(\d+(?:\.\d+)?)/);
  if (numberMatch && numberMatch[1]) {
    return { value: parseFloat(numberMatch[1]) };
  }

  return {};
}

/**
 * Maps GS1 row data to Product insert format
 */
function mapGS1ToProduct(row: GS1Row, rowIndex: number): ProductInsert | null {
  // Validate required fields
  if (!row.GTIN) {
    console.warn(`‚ö†Ô∏è  Row ${rowIndex}: Missing GTIN, skipping`);
    return null;
  }

  if (!validateGTIN(row.GTIN)) {
    console.warn(
      `‚ö†Ô∏è  Row ${rowIndex}: Invalid GTIN format (${row.GTIN}), skipping`
    );
    return null;
  }

  if (!row['Product Name']) {
    console.warn(`‚ö†Ô∏è  Row ${rowIndex}: Missing Product Name, skipping`);
    return null;
  }

  // Parse net content
  const netContent = parseNetContent(row['Net Content'] || '');
  const netContentUom = row['Net Content UOM'] || netContent.uom;

  // Clean and format GTIN
  const cleanGtin = row.GTIN.replace(/\D/g, '');

  // Generate SKU if not provided
  const sku = row.SKU || `GS1-${cleanGtin}`;

  const product: ProductInsert = {
    gtin: cleanGtin,
    sku,
    name: row['Product Name'],
    gpc_brick_code: row['GPC Brick Code'],
    brand: row.Brand,
    description: row.Description,
    category: row.Category,
    price: row.Price,
    unit: row.Unit,
    net_content_value: netContent.value,
    net_content_uom: netContentUom,
    gross_weight: row['Gross Weight'],
    net_weight: row['Net Weight'],
    base_unit_indicator:
      row['Base Unit'] !== undefined ? parseBoolean(row['Base Unit']) : true,
    orderable_unit_indicator:
      row['Orderable Unit'] !== undefined
        ? parseBoolean(row['Orderable Unit'])
        : true,
    despatch_unit_indicator:
      row['Despatch Unit'] !== undefined
        ? parseBoolean(row['Despatch Unit'])
        : true,
    country_of_origin: row['Country of Origin'],
    effective_from_date: parseDate(row['Effective From']),
    effective_to_date: parseDate(row['Effective To']),
    product_lifecycle_status: row['Product Lifecycle Status'],
    active: true,
  };

  return product;
}

/**
 * Upserts a product into the Supabase products table
 */
async function upsertProduct(
  product: ProductInsert
): Promise<{ success: boolean; isUpdate: boolean; error?: string }> {
  try {
    // Check if product exists
    const { data: existing } = await supabase
      .from('products')
      .select('id')
      .eq('gtin', product.gtin)
      .single();

    const isUpdate = !!existing;

    // Upsert the product
    const { error } = await supabase.from('products').upsert(product, {
      onConflict: 'gtin',
      ignoreDuplicates: false,
    });

    if (error) {
      return { success: false, isUpdate, error: error.message };
    }

    return { success: true, isUpdate };
  } catch (error) {
    return {
      success: false,
      isUpdate: false,
      error: error instanceof Error ? error.message : 'Unknown error',
    };
  }
}

/**
 * Main import function
 */
async function importGS1Excel(
  filePath: string,
  sheetName: string = 'NL'
): Promise<ImportStats> {
  const stats: ImportStats = {
    totalRows: 0,
    processed: 0,
    inserted: 0,
    updated: 0,
    skipped: 0,
    errors: [],
  };

  try {
    console.log(`üìÇ Loading Excel file: ${filePath}`);

    // Read the Excel file
    const workbook = XLSX.readFile(filePath);

    if (!workbook.SheetNames.includes(sheetName)) {
      throw new Error(
        `Sheet "${sheetName}" not found. Available sheets: ${workbook.SheetNames.join(
          ', '
        )}`
      );
    }

    console.log(`üìä Reading sheet: ${sheetName}`);
    const worksheet = workbook.Sheets[sheetName];

    if (!worksheet) {
      throw new Error(`Failed to read worksheet "${sheetName}"`);
    }

    // Convert sheet to JSON
    const rawData: GS1Row[] = XLSX.utils.sheet_to_json(worksheet);
    stats.totalRows = rawData.length;

    console.log(`üìã Found ${stats.totalRows} rows to process`);
    console.log('üîÑ Starting import...\n');

    // Process each row
    for (let i = 0; i < rawData.length; i++) {
      const row = rawData[i];
      if (!row) continue;

      const rowIndex = i + 2; // Excel row number (accounting for header)

      try {
        // Map and validate the row
        const product = mapGS1ToProduct(row, rowIndex);

        if (!product) {
          stats.skipped++;
          continue;
        }

        // Upsert to database
        const result = await upsertProduct(product);

        if (result.success) {
          stats.processed++;
          if (result.isUpdate) {
            stats.updated++;
            console.log(`‚úÖ Updated: ${product.name} (GTIN: ${product.gtin})`);
          } else {
            stats.inserted++;
            console.log(`‚ûï Inserted: ${product.name} (GTIN: ${product.gtin})`);
          }
        } else {
          stats.errors.push(`Row ${rowIndex}: ${result.error}`);
          console.error(`‚ùå Error on row ${rowIndex}: ${result.error}`);
        }

        // Progress indicator for large files
        if ((i + 1) % 100 === 0) {
          console.log(
            `üìà Progress: ${i + 1}/${stats.totalRows} rows processed`
          );
        }
      } catch (error) {
        const errorMsg =
          error instanceof Error ? error.message : 'Unknown error';
        stats.errors.push(`Row ${rowIndex}: ${errorMsg}`);
        console.error(`‚ùå Error processing row ${rowIndex}: ${errorMsg}`);
      }
    }

    return stats;
  } catch (error) {
    const errorMsg = error instanceof Error ? error.message : 'Unknown error';
    stats.errors.push(`File processing error: ${errorMsg}`);
    throw error;
  }
}

/**
 * Print import statistics
 */
function printStats(stats: ImportStats) {
  console.log('\nüìä Import Statistics:');
  console.log('‚ïê'.repeat(40));
  console.log(`Total rows in file:    ${stats.totalRows}`);
  console.log(`Successfully processed: ${stats.processed}`);
  console.log(`New products inserted:  ${stats.inserted}`);
  console.log(`Existing products updated: ${stats.updated}`);
  console.log(`Rows skipped:          ${stats.skipped}`);
  console.log(`Errors encountered:    ${stats.errors.length}`);

  if (stats.errors.length > 0) {
    console.log('\n‚ùå Errors:');
    stats.errors.forEach((error, index) => {
      console.log(`${index + 1}. ${error}`);
    });
  }

  console.log('‚ïê'.repeat(40));

  if (stats.processed > 0) {
    console.log('‚úÖ Import completed successfully!');
  } else {
    console.log('‚ö†Ô∏è  No products were imported.');
  }
}

/**
 * Main execution
 */
async function main() {
  try {
    // Get file path from command line arguments or use default
    const filePath = process.argv[2] || resolve(process.cwd(), 'gs1-data.xlsx');
    const sheetName = process.argv[3] || 'NL';

    console.log('üöÄ GS1 Excel Import Tool');
    console.log('‚ïê'.repeat(40));
    console.log(`File: ${filePath}`);
    console.log(`Sheet: ${sheetName}`);
    console.log(`Supabase URL: ${supabaseUrl}`);
    console.log('‚ïê'.repeat(40));

    // Test Supabase connection
    console.log('üîó Testing Supabase connection...');
    const { error: connectionError } = await supabase
      .from('products')
      .select('count', { count: 'exact', head: true });

    if (connectionError) {
      throw new Error(
        `Failed to connect to Supabase: ${connectionError.message}`
      );
    }
    console.log('‚úÖ Supabase connection successful\n');

    // Run the import
    const stats = await importGS1Excel(filePath, sheetName);

    // Print results
    printStats(stats);
  } catch (error) {
    console.error('\nüí• Import failed:');
    console.error(error instanceof Error ? error.message : error);
    process.exit(1);
  }
}

// Execute if called directly
if (require.main === module) {
  main();
}

export {
  importGS1Excel,
  mapGS1ToProduct,
  validateGTIN,
  parseBoolean,
  parseDate,
  parseNetContent,
};

#!/usr/bin/env node

/**
 * Translation Validation Script voor Remcura
 * Controleert consistentie tussen alle taalbestanden
 */

const fs = require('fs');
const path = require('path');

// Taal configuratie (voor nu alleen Nederlands)
const LANGUAGES = ['nl'];
const I18N_DIR = path.join(__dirname, '../src/i18n');

/**
 * Haalt alle keys uit een object recursief
 */
function extractKeys(obj, prefix = '') {
  const keys = [];

  for (const key in obj) {
    if (Object.prototype.hasOwnProperty.call(obj, key)) {
      const fullKey = prefix ? `${prefix}.${key}` : key;

      if (
        typeof obj[key] === 'object' &&
        obj[key] !== null &&
        !Array.isArray(obj[key])
      ) {
        keys.push(...extractKeys(obj[key], fullKey));
      } else {
        keys.push(fullKey);
      }
    }
  }

  return keys;
}

/**
 * Laadt vertaalbestand
 */
function loadTranslationFile(lang) {
  const filePath = path.join(I18N_DIR, lang, 'index.ts');

  if (!fs.existsSync(filePath)) {
    console.error(`‚ùå Bestand niet gevonden: ${filePath}`);
    return null;
  }

  try {
    // Load filters first if they exist
    let filters = {};
    const filtersPath = path.join(I18N_DIR, lang, 'filters.ts');
    if (fs.existsSync(filtersPath)) {
      try {
        const filtersContent = fs.readFileSync(filtersPath, 'utf8');
        const filtersMatch = filtersContent.match(
          /export default\s*({[\s\S]*});?\s*$/
        );
        if (filtersMatch) {
          const _filters = eval(`(${filtersMatch[1]})`);
        }
      } catch (filtersError) {
        console.warn(
          `‚ö†Ô∏è Kon filters niet laden voor ${lang}:`,
          filtersError.message
        );
      }
    }

    // Dynamically import en eval de TS export
    const content = fs.readFileSync(filePath, 'utf8');
    const exportMatch = content.match(/export default\s*({[\s\S]*});?\s*$/);

    if (!exportMatch) {
      console.error(`‚ùå Kan export niet vinden in ${filePath}`);
      return null;
    }

    // Eval is not ideal but for this script it works
    // Make filters available in eval context
    const translations = eval(`(${exportMatch[1]})`);
    return translations;
  } catch (error) {
    console.error(`‚ùå Fout bij laden van ${filePath}:`, error.message);
    return null;
  }
}

/**
 * Valideert vertalingen
 */
function validateTranslations() {
  console.log('üîç Remcura Vertaling Validator\n');

  const languageData = {};
  const allKeys = new Set();

  // Laad alle taalbestanden
  for (const lang of LANGUAGES) {
    console.log(`üìÇ Laden ${lang.toUpperCase()}...`);
    const translations = loadTranslationFile(lang);

    if (translations) {
      languageData[lang] = translations;
      const keys = extractKeys(translations);
      keys.forEach(key => allKeys.add(key));
      console.log(`   ‚úÖ ${keys.length} keys gevonden`);
    } else {
      console.log(`   ‚ùå Laden mislukt`);
    }
  }

  console.log(`\nüìä Totaal unieke keys: ${allKeys.size}\n`);

  // Controleer missende keys per taal
  const missingKeys = {};

  for (const lang of LANGUAGES) {
    if (!languageData[lang]) continue;

    const langKeys = new Set(extractKeys(languageData[lang]));
    const missing = Array.from(allKeys).filter(key => !langKeys.has(key));

    missingKeys[lang] = missing;

    console.log(`üîç ${lang.toUpperCase()} Analyse:`);
    console.log(`   üìã Keys aanwezig: ${langKeys.size}`);
    console.log(`   ‚ùå Keys ontbrekend: ${missing.length}`);

    if (missing.length > 0) {
      console.log(`   üö® Eerste 10 ontbrekende keys:`);
      missing.slice(0, 10).forEach(key => {
        console.log(`      - ${key}`);
      });
      if (missing.length > 10) {
        console.log(`      ... en ${missing.length - 10} meer`);
      }
    }
    console.log('');
  }

  // Zoek naar duplicaten binnen bestanden
  console.log('üîÑ Controleren op duplicaten...\n');

  for (const lang of LANGUAGES) {
    if (!languageData[lang]) continue;

    const filePath = path.join(I18N_DIR, lang, 'index.ts');
    const content = fs.readFileSync(filePath, 'utf8');

    // Vind propertynames die meerdere keren voorkomen
    const propertyMatches = content.match(/^\s*(\w+):\s/gm);
    if (propertyMatches) {
      const properties = propertyMatches.map(match => match.match(/(\w+):/)[1]);
      const counts = {};

      properties.forEach(prop => {
        counts[prop] = (counts[prop] || 0) + 1;
      });

      const duplicates = Object.entries(counts).filter(
        ([_, count]) => count > 1
      );

      if (duplicates.length > 0) {
        console.log(`‚ö†Ô∏è ${lang.toUpperCase()} heeft duplicaten:`);
        duplicates.forEach(([prop, count]) => {
          console.log(`   - "${prop}" komt ${count} keer voor`);
        });
      } else {
        console.log(`‚úÖ ${lang.toUpperCase()} heeft geen duplicaten`);
      }
    }
  }

  // Genereer rapport
  console.log('\nüìã SAMENVATTING:');
  console.log('================');

  const totalMissing = Object.values(missingKeys).reduce(
    (sum, arr) => sum + arr.length,
    0
  );

  if (totalMissing === 0) {
    console.log('üéâ Alle vertalingen zijn compleet!');
  } else {
    console.log(`‚ùå ${totalMissing} missende vertalingen gevonden`);
    console.log('\nüõ†Ô∏è AANBEVELINGEN:');

    for (const [lang, missing] of Object.entries(missingKeys)) {
      if (missing.length > 0) {
        console.log(`\n${lang.toUpperCase()}:`);
        console.log(`- Voeg ${missing.length} ontbrekende keys toe`);
        console.log(`- Meest kritieke: ${missing.slice(0, 3).join(', ')}`);
      }
    }
  }

  return {
    allKeys: Array.from(allKeys),
    missingKeys,
    languageData,
    isValid: totalMissing === 0,
  };
}

// Run validatie
if (require.main === module) {
  validateTranslations();
}

module.exports = { validateTranslations };

import { ref } from 'vue';
import { supabase } from '@/boot/supabase';

interface StockLevelRecord {
  product_id: string;
  current_quantity: number;
  minimum_quantity: number;
  location_id: string;
  practice_locations: {
    name: string;
  };
}

interface SupplierProductRecord {
  product_id: string;
  supplier_id: string;
  supplier_sku: string;
  unit_price: number;
  minimum_order_quantity: number | null;
  lead_time_days: number | null;
  is_preferred: boolean;
  suppliers: {
    name: string;
    contact_email: string | null;
  };
}

interface ProductBatchRecord {
  product_id: string;
  batch_number: string;
  expiry_date: string;
  quantity_remaining: number;
  location_id: string;
  practice_locations: {
    name: string;
  };
}

interface RelatedDataFilters {
  [key: string]: string | number | boolean | null;
}

export function useBulkData() {
  const loading = ref(false);
  const error = ref<Error | null>(null);

  const loadStockLevelsForProducts = async (
    productIds: string[],
    practiceId: string
  ): Promise<Record<string, StockLevelRecord[]>> => {
    if (!productIds.length) return {};

    loading.value = true;
    error.value = null;

    try {
      const { data, error: queryError } = await supabase
        .from('stock_levels')
        .select(
          `
          product_id,
          current_quantity,
          minimum_quantity,
          location_id,
          practice_locations!inner(name)
        `
        )
        .eq('practice_id', practiceId)
        .in('product_id', productIds);

      if (queryError) throw queryError;

      const stockByProduct: Record<string, StockLevelRecord[]> = {};
      (data as StockLevelRecord[] | null)?.forEach(stock => {
        if (!stockByProduct[stock.product_id]) {
          stockByProduct[stock.product_id] = [];
        }
        stockByProduct[stock.product_id]?.push(stock);
      });

      return stockByProduct;
    } catch (err) {
      error.value = err as Error;
      console.error('Bulk stock loading failed:', err);
      return {};
    } finally {
      loading.value = false;
    }
  };

  const loadSupplierProductsForProducts = async (
    productIds: string[],
    practiceId: string
  ): Promise<Record<string, SupplierProductRecord[]>> => {
    if (!productIds.length) return {};

    loading.value = true;
    error.value = null;

    try {
      const { data, error: queryError } = await supabase
        .from('supplier_products')
        .select(
          `
          id,
          product_id,
          supplier_id,
          supplier_sku,
          cost_price,
          list_price,
          minimum_order_quantity,
          lead_time_days,
          is_preferred,
          suppliers!inner(name, contact_email)
        `
        )
        .in('product_id', productIds)
        .eq('is_available', true);

      if (queryError) throw queryError;

      const suppliersByProduct: Record<string, SupplierProductRecord[]> = {};
      (data as unknown as SupplierProductRecord[] | null)?.forEach(sp => {
        if (!suppliersByProduct[sp.product_id]) {
          suppliersByProduct[sp.product_id] = [];
        }
        suppliersByProduct[sp.product_id]?.push(sp);
      });

      return suppliersByProduct;
    } catch (err) {
      error.value = err as Error;
      console.error('Bulk supplier products loading failed:', err);
      return {};
    } finally {
      loading.value = false;
    }
  };

  const loadBatchesForProducts = async (
    productIds: string[],
    practiceId: string
  ): Promise<Record<string, ProductBatchRecord[]>> => {
    if (!productIds.length) return {};

    loading.value = true;
    error.value = null;

    try {
      const { data, error: queryError } = await supabase
        .from('product_batches')
        .select(
          `
          id,
          product_id,
          batch_number,
          expiry_date,
          current_quantity,
          available_quantity,
          location_id,
          practice_locations!inner(name)
        `
        )
        .eq('practice_id', practiceId)
        .in('product_id', productIds)
        .gt('current_quantity', 0)
        .order('expiry_date', { ascending: true });

      if (queryError) throw queryError;

      const batchesByProduct: Record<string, ProductBatchRecord[]> = {};
      (data as unknown as ProductBatchRecord[] | null)?.forEach(batch => {
        if (!batchesByProduct[batch.product_id]) {
          batchesByProduct[batch.product_id] = [];
        }
        batchesByProduct[batch.product_id]?.push(batch);
      });

      return batchesByProduct;
    } catch (err) {
      error.value = err as Error;
      console.error('Bulk batch loading failed:', err);
      return {};
    } finally {
      loading.value = false;
    }
  };

  const loadRelatedData = async <T extends Record<string, unknown>>(
    table: string,
    foreignKey: string,
    entityIds: string[],
    selectFields = '*',
    additionalFilters: RelatedDataFilters = {}
  ): Promise<Record<string, T[]>> => {
    if (!entityIds.length) return {};

    loading.value = true;
    error.value = null;

    try {
      let query = (supabase as any)
        .from(table)
        .select(selectFields as any)
        .in(foreignKey as any, entityIds);

      Object.entries(additionalFilters).forEach(([key, value]) => {
        query = query.eq(key as any, value as any);
      });

      const { data, error: queryError } = await query;

      if (queryError) throw queryError;

      const groupedData: Record<string, T[]> = {};
      (data as unknown as T[] | null)?.forEach(item => {
        const key = String(item[foreignKey]);
        if (!groupedData[key]) {
          groupedData[key] = [];
        }
        groupedData[key].push(item);
      });

      return groupedData;
    } catch (err) {
      error.value = err as Error;
      console.error(`Bulk loading failed for ${table}:`, err);
      return {};
    } finally {
      loading.value = false;
    }
  };

  return {
    loading,
    error,
    loadStockLevelsForProducts,
    loadSupplierProductsForProducts,
    loadBatchesForProducts,
    loadRelatedData,
  };
}

import { ref, type Ref, watch } from 'vue';

/**
 * Debounce composable for better performance
 * Delays execution of expensive operations like filtering and API calls
 */
type GenericFunction = (...args: unknown[]) => unknown;

export function useDebounce<T extends GenericFunction>(
  fn: T,
  delay = 300
): {
  debouncedFn: (...args: Parameters<T>) => void;
  cancel: () => void;
  flush: () => void;
  pending: Ref<boolean>;
} {
  let timeoutId: NodeJS.Timeout | null = null;
  const pending = ref(false);
  let lastArgs: Parameters<T> | null = null;

  const cancel = () => {
    if (timeoutId) {
      clearTimeout(timeoutId);
      timeoutId = null;
      pending.value = false;
    }
  };

  const flush = () => {
    if (timeoutId && lastArgs) {
      cancel();
      fn(...lastArgs);
      lastArgs = null;
    }
  };

  const debouncedFn = (...args: Parameters<T>) => {
    lastArgs = args;
    cancel();
    pending.value = true;

    timeoutId = setTimeout(() => {
      fn(...args);
      pending.value = false;
      timeoutId = null;
      lastArgs = null;
    }, delay);
  };

  return {
    debouncedFn,
    cancel,
    flush,
    pending,
  };
}

/**
 * Debounced ref for reactive values
 * Useful for filter inputs that should update with delay
 */
export function useDebouncedRef<T>(
  initialValue: T,
  delay = 300
): {
  value: Ref<T>;
  debouncedValue: Ref<T>;
  pending: Ref<boolean>;
} {
  const value = ref(initialValue) as Ref<T>;
  const debouncedValue = ref(initialValue) as Ref<T>;
  const pending = ref(false);

  const { debouncedFn } = useDebounce(
    ((newValue: T) => {
      debouncedValue.value = newValue;
      pending.value = false;
    }) as any,
    delay
  );

  watch(
    value,
    newValue => {
      pending.value = true;
      debouncedFn(newValue);
    },
    { immediate: true }
  );

  return {
    value,
    debouncedValue,
    pending,
  };
}

import { createI18n } from 'vue-i18n';
import type { SupportedLocale } from '@/types/i18n';
export type { SupportedLocale } from '@/types/i18n';

// Always use lazy loading to avoid require() issues in Vite
const ENABLE_LAZY_LOADING = true;

// Messages for upfront loading (empty for lazy loading)
const messages: Record<SupportedLocale, any> = {} as any;

// Get saved locale from localStorage or default to 'nl'
const getSavedLocale = (): SupportedLocale => {
  const _saved = localStorage.getItem('remcura_locale');
  // We only support Dutch now; ignore any previously saved en/es
  return 'nl';
};

// Check if we're in development mode
export const isDevelopment =
  process.env.NODE_ENV === 'development' || process.env.DEV === 'true';

// Custom missing handler for development feedback
const missingHandler = (
  locale: string,
  key: string,
  _instance: any,
  _type: string
) => {
  if (isDevelopment) {
    console.warn(`[i18n:missing] ${locale}:${key}`);
    return key; // show key in dev
  }
  return '';
};

// Create i18n instance
// Use composition mode for Vue 3 compatibility
export const i18n = createI18n({
  locale: getSavedLocale(),
  fallbackLocale: 'nl',
  messages: ENABLE_LAZY_LOADING ? {} : messages, // Empty in production for lazy loading
  legacy: false, // Use composition mode for Vue 3 compatibility
  globalInjection: true,
  missingWarn: isDevelopment,
  fallbackWarn: isDevelopment,
  missing: missingHandler as any,
});

// Async loader for lazy loading
const loadedLanguages: Set<SupportedLocale> = new Set();

export const loadLanguageAsync = async (
  locale: SupportedLocale
): Promise<void> => {
  if (!ENABLE_LAZY_LOADING) {
    // In development, everything is already loaded
    return Promise.resolve();
  }

  if (loadedLanguages.has(locale)) {
    // Already loaded
    return Promise.resolve();
  }

  try {
    const messages = await import(`./${locale}/index.ts`);
    i18n.global.setLocaleMessage(locale, messages.default);
    loadedLanguages.add(locale);

    if (isDevelopment) {
      // Lazy loaded translations
    }
  } catch (error) {
    console.error(`‚ùå [i18n] Failed to load ${locale} translations:`, error);
    throw error;
  }
};

// Initialize with Dutch language in production (lazy load)
if (ENABLE_LAZY_LOADING) {
  const initialLocale = getSavedLocale();
  loadLanguageAsync(initialLocale)
    .then(() => {
      if (isDevelopment) {
        // Initial translations loaded
      }
    })
    .catch(error => {
      console.error('‚ùå [i18n] Failed to load initial translations:', error);
    });
}

// Locale setter with persistence and lazy loading
export const setI18nLanguage = async (
  locale: SupportedLocale
): Promise<SupportedLocale> => {
  // Load the language if lazy loading is enabled
  if (ENABLE_LAZY_LOADING) {
    await loadLanguageAsync(locale);
  }

  // In composition mode, locale is a ref with .value
  (i18n.global.locale as any).value = locale;
  document.querySelector('html')?.setAttribute('lang', locale);
  localStorage.setItem('remcura_locale', locale);
  return locale;
};

// Get current locale
export const getCurrentLocale = (): SupportedLocale => {
  // In composition mode, locale is a ref with .value
  return (i18n.global.locale as any).value as SupportedLocale;
};

// Development helper: Check if a translation exists
export const hasTranslation = (
  key: string,
  locale?: SupportedLocale
): boolean => {
  const targetLocale = locale || getCurrentLocale();
  return i18n.global.te(key, targetLocale);
};

// Development helper: Get missing translations for current locale
export const getMissingTranslations = (
  keys: string[],
  locale?: SupportedLocale
): string[] => {
  const targetLocale = locale || getCurrentLocale();
  return keys.filter(key => !hasTranslation(key, targetLocale));
};

// Development helper: Log translation coverage stats
export const logTranslationStats = () => {
  if (!isDevelopment) {
    return;
  }

  const locales: SupportedLocale[] = ['nl'];
  console.group('üåê Translation Coverage Statistics');

  locales.forEach(locale => {
    if (ENABLE_LAZY_LOADING) {
      const _isLoaded = loadedLanguages.has(locale);
      // Language loaded check
    } else {
      // Count keys for status
    }
  });

  console.groupEnd();
};

// Preload all languages (useful for offline apps)
export const preloadAllLanguages = async (): Promise<void> => {
  if (!ENABLE_LAZY_LOADING) {
    // Already loaded in development
    return Promise.resolve();
  }

  const locales: SupportedLocale[] = ['nl'];
  const loadPromises = locales.map(locale => loadLanguageAsync(locale));

  try {
    await Promise.all(loadPromises);
    if (isDevelopment) {
      // All languages preloaded successfully
    }
  } catch (error) {
    console.error('‚ùå [i18n] Failed to preload all languages:', error);
    throw error;
  }
};

// Check if lazy loading is enabled
export const isLazyLoadingEnabled = (): boolean => ENABLE_LAZY_LOADING;

