export interface AuditLogEntry {
  id: string;
  practice_id: string;
  user_id: string | null;
  action: string;
  resource_type: string;
  resource_id: string | null;
  old_values: Record<string, unknown>;
  new_values: Record<string, unknown>;
  metadata: Record<string, unknown>;
  timestamp: Date;
  ip_address?: string;
