  }
}

// Export an instance for compatibility with existing code
export const analyticsService = new AnalyticsService();

import { supabase } from '../supabase';
import { dashboardLogger } from '@/utils/logger';
import { t } from '@/utils/i18n-service';
import { ServiceErrorHandler } from '@/utils/service-error-handler';

export interface PlatformWidget {
  id: string;
  title: string;
  type: 'metric' | 'chart' | 'list' | 'table' | 'system';
  data: Record<string, any>;
  size: 'small' | 'medium' | 'large';
  position: number;
  visible: boolean;
  loading?: boolean;
  error?: string;
}

export interface PlatformDashboardData {
  widgets: PlatformWidget[];
  systemInfo: {
    version: string;
    buildNumber: string;
    lastDeployment: string;
    uptime: string;
    environment: string;
  };
  platformMetrics: {
    totalPractices: number;
    totalUsers: number;
    activeToday: number;
    totalEvents: number;
    systemHealth: 'healthy' | 'warning' | 'critical';
  };
  quickActions: Array<{
    id: string;
    label: string;
    icon: string;
    route: string;
    color: string;
    permission?: string;
  }>;
}

class PlatformDashboardService {
  private getWidgetConfig(): string[] {
    return [
      'system-health',
      'version-info',
      'platform-audit-logs',
      'customer-management',
      'api-integration-status',
      'performance-metrics',
      'database-status',
      'error-monitoring',
    ];
  }

  async getDashboardData(): Promise<PlatformDashboardData> {
    try {
      dashboardLogger.info('üåê Loading platform dashboard');

      const widgetIds = this.getWidgetConfig();

      // Load system info
      const systemInfo = await this.loadSystemInfo();

      // Load platform metrics
      const platformMetrics = await this.loadPlatformMetrics();

      // Load platform widgets
      const widgets = await this.loadWidgets(widgetIds);

      // Load quick actions
      const quickActions = this.getQuickActions();

      return {
        widgets,
        systemInfo,
        platformMetrics,
        quickActions,
      };
    } catch (error) {
      dashboardLogger.error(
        'Error loading platform dashboard:',
        error as Record<string, unknown>
      );
      throw error;
    }
  }

  private async loadSystemInfo() {
    // This would typically come from environment variables or system APIs
    const packageInfo = await import('../../../package.json');

    return {
      version: packageInfo.version || '2.1.0',
      buildNumber: process.env.VITE_BUILD_NUMBER || '1234',
      lastDeployment:
        process.env.VITE_DEPLOYMENT_TIME || new Date().toISOString(),
      uptime: this.getSystemUptime(),
      environment: process.env.NODE_ENV || 'development',
    };
  }

  private getSystemUptime(): string {
    // In a real application, this would be calculated from deployment time
    const uptimeMs = Date.now() - new Date('2024-01-01').getTime();
    const days = Math.floor(uptimeMs / (1000 * 60 * 60 * 24));
    const hours = Math.floor(
      (uptimeMs % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
    );
    return `${days}d ${hours}h`;
  }

  private async loadPlatformMetrics() {
    try {
      // Get real data from Supabase
      const { count: totalPractices } = await supabase
        .from('practices')
        .select('id', { count: 'exact' });

      const { count: totalUsers } = await supabase
        .from('practice_members')
        .select('user_id', { count: 'exact' });

      const today = new Date().toISOString().split('T')[0];
      const { count: activeToday } = await supabase
        .from('usage_analytics')
        .select('user_id', { count: 'exact' })
        .gte('created_at', today);

      const { count: totalEvents } = await supabase
        .from('usage_analytics')
        .select('id', { count: 'exact' })
        .gte('created_at', today);

      const { count: recentErrors } = await supabase
        .from('activity_log')
        .select('id', { count: 'exact' })
        .ilike('activity_type', '%error%')
        .gte('created_at', new Date(Date.now() - 60 * 60 * 1000).toISOString());

      let systemHealth: 'healthy' | 'warning' | 'critical' = 'healthy';
      if (recentErrors && recentErrors > 10) systemHealth = 'warning';
      if (recentErrors && recentErrors > 50) systemHealth = 'critical';

      return {
        totalPractices: totalPractices || 0,
        totalUsers: totalUsers || 0,
        activeToday: activeToday || 0,
        totalEvents: totalEvents || 0,
        systemHealth,
      };
    } catch (error) {
      dashboardLogger.error(
        'Error loading platform metrics:',
        error as Record<string, unknown>
      );
      return {
        totalPractices: 0,
        totalUsers: 0,
        activeToday: 0,
        totalEvents: 0,
        systemHealth: 'critical' as const,
      };
    }
  }

  private async loadWidgets(widgetIds: string[]): Promise<PlatformWidget[]> {
    const widgets: PlatformWidget[] = [];

    for (let i = 0; i < widgetIds.length; i++) {
      const widgetId = widgetIds[i];
      if (!widgetId) continue; // Skip undefined widget IDs

      try {
        const widget = await this.loadWidget(widgetId, i);
        if (widget) {
          widgets.push(widget);
        }
      } catch (error) {
        ServiceErrorHandler.handle(
          error as Error,
          {
            service: 'PlatformDashboardService',
            operation: 'loadWidget',
            metadata: { widgetId, position: i },
          },
          { rethrow: false, logLevel: 'error' }
        );

        // Add error widget
        widgets.push({
          id: widgetId,
          title: this.getWidgetTitle(widgetId),
          type: 'system',
          data: { error: 'Failed to load widget data' },
          size: 'medium',
          position: i,
          visible: true,
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    }

    return widgets;
  }

  private async loadWidget(
    widgetId: string,
    position: number
  ): Promise<PlatformWidget | null> {
    const baseWidget = {
      id: widgetId,
      title: this.getWidgetTitle(widgetId),
      position,
      visible: true,
      loading: false,
    };

    switch (widgetId) {
      case 'system-health':
        return {
          ...baseWidget,
          type: 'metric' as const,
          size: 'large' as const,
          data: await this.loadSystemHealth(),
        };

      case 'version-info':
        return {
          ...baseWidget,
          type: 'system' as const,
          size: 'medium' as const,
          data: await this.loadVersionInfo(),
        };

      case 'platform-audit-logs':
        return {
          ...baseWidget,
          type: 'list' as const,
          size: 'large' as const,
          data: await this.loadPlatformAuditLogs(),
        };

      case 'customer-management':
        return {
          ...baseWidget,
          type: 'table' as const,
          size: 'large' as const,
          data: await this.loadCustomerManagement(),
        };

      case 'api-integration-status':
        return {
          ...baseWidget,
          type: 'table' as const,
          size: 'medium' as const,
          data: await this.loadApiIntegrationStatus(),
        };

      case 'performance-metrics':
        return {
          ...baseWidget,
          type: 'chart' as const,
          size: 'medium' as const,
          data: await this.loadPerformanceMetrics(),
        };

      case 'database-status':
        return {
          ...baseWidget,
          type: 'metric' as const,
          size: 'small' as const,
          data: await this.loadDatabaseStatus(),
        };

      case 'error-monitoring':
        return {
          ...baseWidget,
          type: 'list' as const,
          size: 'medium' as const,
          data: await this.loadErrorMonitoring(),
        };

      default:
        return null;
    }
  }

  // Platform widget data loaders
  private async loadSystemHealth() {
    const { data: recentErrors } = await supabase
      .from('activity_log')
      .select('activity_type, created_at')
      .ilike('activity_type', '%error%')
      .gte(
        'created_at',
        new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString()
