          return [
            practice.name,
            practice.email || 'N/A',
            String(userCount),
            String(locationCount),
            lastActivity,
            practice.created_at ? new Date(practice.created_at).toLocaleDateString() : 'N/A',
          ];
        }) || [],
    };
  }

  private async loadApiIntegrationStatus() {
    const { data } = await supabase
      .from('suppliers')
      .select('integration_type, order_method, sync_enabled, last_sync_at')
      .neq('integration_type', 'manual');

    // Group by integration type and method
    const integrationStats: Record<
      string,
      {
        total: number;
        active: number;
        recent_syncs: number;
      }
    > = {};

    data?.forEach(supplier => {
      const key = `${supplier.integration_type}-${supplier.order_method}`;
      if (!integrationStats[key]) {
        integrationStats[key] = { total: 0, active: 0, recent_syncs: 0 };
      }

      integrationStats[key].total++;
      if (supplier.sync_enabled) integrationStats[key].active++;
      if (
        supplier.last_sync_at &&
        new Date(supplier.last_sync_at) >
          new Date(Date.now() - 24 * 60 * 60 * 1000)
      ) {
        integrationStats[key].recent_syncs++;
      }
    });

    return {
      headers: ['Integration', 'Total', 'Active', 'Recent Syncs', 'Health'],
      rows: Object.entries(integrationStats).map(([key, stats]) => {
        const health =
          stats.active > 0
            ? stats.recent_syncs / stats.active > 0.8
              ? 'Good'
              : 'Warning'
            : 'Inactive';

        return [
          key.replace('-', ' + '),
          stats.total,
          stats.active,
          stats.recent_syncs,
          health,
        ];
      }),
    };
  }

  private async loadPerformanceMetrics() {
    // Get usage analytics for the last 7 days
    const { data } = await supabase
      .from('usage_analytics')
      .select('created_at, event_type')
      .gte(
        'created_at',
        new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString()
      )
      .order('created_at', { ascending: true });

    // Group by day
    const dailyMetrics: Record<string, number> = {};
    const last7Days: string[] = [];

    for (let i = 6; i >= 0; i--) {
      const date = new Date(Date.now() - i * 24 * 60 * 60 * 1000)
        .toISOString()
        .split('T')[0];
      if (date) {
        last7Days.push(date);
        dailyMetrics[date] = 0;
      }
    }

    data?.forEach(event => {
      if (event.created_at) {
        const day = event.created_at.split('T')[0];
        if (day && Object.prototype.hasOwnProperty.call(dailyMetrics, day)) {
          const currentValue = dailyMetrics[day];
          if (currentValue !== undefined) {
            dailyMetrics[day] = currentValue + 1;
          }
        }
      }
    });

    return {
      chart_type: 'line',
      labels: last7Days.map(date => new Date(date).toLocaleDateString()),
      datasets: [
        {
          label: 'Daily Events',
          data: last7Days.map(date => dailyMetrics[date]),
          color: '#2196F3',
        },
      ],
      total_events: Object.values(dailyMetrics).reduce(
        (sum, val) => sum + val,
        0
      ),
    };
  }

  private async loadDatabaseStatus() {
    try {
      // Simple connectivity test
      const start = Date.now();
      const { data: pingData, error: pingError } = await supabase
        .from('practices')
        .select('id')
        .limit(1);
      const responseTime = Date.now() - start;

      // Optional RPC get_table_stats
      let totalTables = 0;
      let largestTable = 'unknown';
      try {
        const { data: tableStats } = await supabase.rpc('get_table_stats');
        totalTables = tableStats?.length || 0;
        largestTable = tableStats?.[0]?.table_name || 'unknown';
      } catch (rpcError) {
        ServiceErrorHandler.handle(
          rpcError as Error,
          {
            service: 'PlatformDashboardService',
            operation: 'get_table_stats',
          },
          { rethrow: false, logLevel: 'warn' }
        );
      }

      return {
        status: pingError ? 'error' : pingData ? 'connected' : 'disconnected',
        response_time_ms: responseTime,
        total_tables: totalTables,
        largest_table: largestTable,
        last_check: new Date().toISOString(),
      };
    } catch (error) {
      return {
        status: 'error',
        response_time_ms: null,
        total_tables: 0,
        largest_table: 'unknown',
        last_check: new Date().toISOString(),
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  private async loadErrorMonitoring() {
    const { data } = await supabase
      .from('activity_log')
      .select(
        `
        activity_type,
        description,
        created_at,
        practices!inner(name)
      `
      )
      .ilike('activity_type', '%error%')
      .gte(
        'created_at',
        new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString()
      )
      .order('created_at', { ascending: false })
      .limit(20);

    // Group errors by type
    const errorGroups: Record<
      string,
      { count: number; last_seen: string; practices: Set<string> }
    > = {};

    data?.forEach(error => {
      const type = error.activity_type ?? 'unknown';
      const createdAt = error.created_at ?? new Date().toISOString();

      if (!errorGroups[type]) {
        errorGroups[type] = {
          count: 0,
          last_seen: createdAt,
          practices: new Set(),
        };
      }

      errorGroups[type].count++;
      errorGroups[type].practices.add(
        (error.practices as any)?.name || 'Unknown'
      );

      if (new Date(createdAt) > new Date(errorGroups[type].last_seen)) {
        errorGroups[type].last_seen = createdAt;
      }
    });

    return {
      items: Object.entries(errorGroups)
        .map(([type, info]) => ({
          error_type: type,
          count: info.count,
          affected_practices: info.practices.size,
          last_seen: info.last_seen,
          severity:
            info.count > 10 ? 'high' : info.count > 5 ? 'medium' : 'low',
        }))
        .sort((a, b) => b.count - a.count),
    };
  }

