class MagentoApiService {
  private config: MagentoConfig | null = null;
  private readonly DEFAULT_TIMEOUT = 30000;

  /**
   * Configure the Magento API service
   */
  configure(config: MagentoConfig): void {
    validateRequired(
      {
        baseUrl: config.baseUrl,
        token: config.token,
      },
      {
        service: 'MagentoApiService',
        operation: 'configure',
      }
    );

    this.config = {
      ...config,
      timeout: config.timeout || this.DEFAULT_TIMEOUT,
      storeCode: config.storeCode || 'default',
    };
  }

  private async makeRequest(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<any> {
    if (!this.config) {
      throw new Error('Magento API configuration is not initialized');
    }

    const url = `${this.config.baseUrl}/rest/V1${endpoint}`;

    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(
        () => controller.abort(),
        this.config?.timeout ?? this.DEFAULT_TIMEOUT
      );

      const response = await fetch(url, {
        ...options,
        headers: {
          Authorization: `Bearer ${this.config.token}`,
          'Content-Type': 'application/json',
          Store: this.config.storeCode || 'default',
          ...options.headers,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorBody = await response.text();
        handleApiError(
          new Error(`HTTP ${response.status}: ${response.statusText}`),
          {
            service: 'MagentoApiService',
            operation: 'makeRequest',
            metadata: {
              endpoint,
              method: options.method || 'GET',
              status: response.status,
            },
          }
        );
      }

      return response.json();
    } catch (error) {
      const err = error as Error;
      if (err.name === 'AbortError') {
        handleApiError(new Error('Request timeout'), {
          service: 'MagentoApiService',
          operation: 'makeRequest',
          metadata: { endpoint, timeout: this.config.timeout },
        });
      }

      handleApiError(err, {
        service: 'MagentoApiService',
        operation: 'makeRequest',
        metadata: { endpoint, method: options.method || 'GET' },
      });
    }
  }

  /**
   * Test the connection to Magento API
   */
  async testConnection(): Promise<{ success: boolean; message: string }> {
    if (!this.isConfigured()) {
      return {
        success: false,
        message: 'API not configured',
      };
    }

    try {
      // Test with a simple API call
      await this.makeRequest('/store/storeConfigs');
      return {
        success: true,
        message: 'Connection successful',
      };
    } catch (error) {
      return {
        success: false,
        message: error instanceof Error ? error.message : 'Connection failed',
      };
    }
  }

  /**
   * Get connection status with detailed information
   */
  async getConnectionStatus(): Promise<{
    configured: boolean;
    connected: boolean;
    message: string;
    lastTested?: Date;
  }> {
    const configured = this.isConfigured();

    if (!configured) {
      return {
        configured: false,
        connected: false,
        message: 'Not configured',
      };
    }

    const connectionTest = await this.testConnection();

    return {
      configured: true,
      connected: connectionTest.success,
      message: connectionTest.message,
      lastTested: new Date(),
    };
  }

  /**
   * Create search criteria helper
   */
  createSearchCriteria(options: {
    filters?: Array<{
      field: string;
      value: string | number;
      condition?: string;
    }>;
    sortBy?: string;
    sortDirection?: 'ASC' | 'DESC';
    pageSize?: number;
    currentPage?: number;
  }): MagentoSearchCriteria {
    const criteria: MagentoSearchCriteria = {};

    if (options.filters && options.filters.length > 0) {
      criteria.filterGroups = [
        {
          filters: options.filters.map(filter => ({
            field: filter.field,
            value: filter.value,
            condition_type: filter.condition || 'eq',
          })),
        },
      ];
    }

    if (options.sortBy) {
      criteria.sortOrders = [
        {
          field: options.sortBy,
          direction: options.sortDirection || 'ASC',
        },
      ];
    }

    if (options.pageSize) {
      criteria.pageSize = options.pageSize;
    }

    if (options.currentPage) {
      criteria.currentPage = options.currentPage;
    }

    return criteria;
  }

  // Order management
  async getOrders(
    searchCriteria?: MagentoSearchCriteria
  ): Promise<MagentoOrder[]> {
    const endpoint = '/orders';
    const params: string[] = [];
    if (searchCriteria) {
      if (
        searchCriteria.filterGroups &&
        searchCriteria.filterGroups.length > 0
      ) {
        params.push(
          `searchCriteria[filter_groups]=${JSON.stringify(
            searchCriteria.filterGroups
          )}`
        );
      }
      if (searchCriteria.sortOrders && searchCriteria.sortOrders.length > 0) {
        params.push(
          `searchCriteria[sort_orders]=${JSON.stringify(
            searchCriteria.sortOrders
          )}`
        );
      }
      if (searchCriteria.pageSize) {
        params.push(`searchCriteria[page_size]=${searchCriteria.pageSize}`);
      }
      if (searchCriteria.currentPage) {
        params.push(
          `searchCriteria[current_page]=${searchCriteria.currentPage}`
        );
      }
    }
    const queryString = params.length > 0 ? `?${params.join('&')}` : '';
    return this.makeRequest(`${endpoint}${queryString}`);
  }

  async getOrder(orderId: string): Promise<MagentoOrder | null> {
    return this.makeRequest(`/orders/${orderId}`);
  }

  async createOrder(orderData: MagentoOrder): Promise<MagentoOrder> {
    return this.makeRequest('/orders', {
      method: 'POST',
      body: JSON.stringify({ entity: orderData }),
    });
  }

