  static async createGuestSession(
    request: CreateGuestSessionRequest
  ): Promise<GuestSession> {
    try {
      // Generate unique session token
      const sessionToken = uuidv4();

      // Get invite details
      const { data: invite } = await supabase
        .from('magic_invites')
        .select('*')
        .eq('id', request.magic_invite_id)
        .single();
      if (!invite) throw new Error('Invalid invite');

      const sessionData = {
        id: uuidv4(),
        magic_invite_id: request.magic_invite_id,
        practice_id: invite.practice_id,
        guest_name: request.guest_name,
        display_emoji: request.display_emoji || 'üë§',
        session_color: this.generateSessionColor(),
        session_token: sessionToken,
        expires_at: new Date(
          Date.now() + (invite.guest_session_hours ?? 24) * 60 * 60 * 1000
        ).toISOString(),
        is_active: true,
        can_extend: true,
        granted_permissions: this.getGuestPermissions(invite.target_role ?? 'guest'),
        accessible_locations: invite.location_access,
        restricted_features: this.getRestrictedFeatures(invite.target_role ?? 'guest'),
        device_fingerprint: request.device_fingerprint,
        ip_address: request.ip_address || '',
        user_agent: request.user_agent || '',
        timezone: request.timezone || 'UTC',
        actions_performed: {},
        achievements_unlocked: [invite.welcome_achievement],
        upgrade_prompts_shown: 0,
        showed_upgrade_interest: false,
        last_activity_at: new Date().toISOString(),
      };

      const { data, error } = await supabase
        .from('guest_sessions')
        .insert([sessionData as any])
        .select()
        .single();

      if (error) throw error;

      // Update invite usage
      await this.incrementInviteUsage(request.magic_invite_id);

      // Track conversion
      await this.trackInviteAnalytics(
        request.magic_invite_id,
        invite.practice_id,
        'guest_join',
        {
          guest_name: request.guest_name,
          session_id: data.id,
        }
      );

      return mapGuestSessionRow(data);
    } catch (error) {
      console.error('Error creating guest session:', error);
      throw new Error('Failed to create guest session');
    }
  }

