      console.error('Error tracking analytics:', error);
    }
  }

  // üé® HELPER METHODS
  static generateSessionColor(): string {
    const colors = [
      '#2196F3',
      '#4CAF50',
      '#FF9800',
      '#9C27B0',
      '#F44336',
      '#009688',
    ];
    return colors[Math.floor(Math.random() * colors.length)] ?? '#2196F3';
  }

  static getGuestPermissions(role: string): Record<string, any> {
    const basePermissions = {
      view_inventory: true,
      view_products: true,
      view_locations: true,
    };

    switch (role) {
      case 'admin':
        return {
          ...basePermissions,
          edit_inventory: true,
          manage_orders: true,
          view_analytics: true,
        };
      case 'member':
        return {
          ...basePermissions,
          edit_inventory: true,
          create_orders: true,
        };
      default:
        return basePermissions;
    }
  }

  static getRestrictedFeatures(role: string): string[] {
    const commonRestrictions = [
      'user_management',
      'system_settings',
      'billing',
    ];

    switch (role) {
      case 'guest':
        return [...commonRestrictions, 'delete_data', 'export_data'];
      case 'member':
        return [...commonRestrictions, 'delete_data'];
      default:
        return commonRestrictions;
    }
  }

  static getDeviceType(): string {
    const userAgent = navigator.userAgent.toLowerCase();
    if (userAgent.includes('mobile')) {
      return 'mobile';
    }
    if (userAgent.includes('tablet')) {
      return 'tablet';
    }
    return 'desktop';
  }

  // üì± GET ACTIVE SESSIONS
  static async getActiveSessions(practiceId: string): Promise<GuestSession[]> {
    try {
      const { data, error } = await supabase
        .from('guest_sessions')
        .select('*')
        .eq('practice_id', practiceId)
        .eq('is_active', true)
        .gt('expires_at', new Date().toISOString())
        .order('created_at', { ascending: false });

      if (error) throw error;
      return (data || []).map(mapGuestSessionRow);
    } catch (error) {
      console.error('Error getting active sessions:', error);
      return [];
    }
  }

