    sessionId: string,
    additionalHours: number = 4
  ): Promise<void> {
    try {
      const newExpiryTime = new Date(
        Date.now() + additionalHours * 60 * 60 * 1000
      ).toISOString();

      await supabase
        .from('guest_sessions')
        .update({
          expires_at: newExpiryTime,
          updated_at: new Date().toISOString(),
        })
        .eq('id', sessionId);
    } catch (error) {
      console.error('Error extending guest session:', error);
      throw new Error('Failed to extend session');
    }
  }

  // üéØ GET PRACTICE INVITES
  static async getPracticeInvites(practiceId: string): Promise<MagicInvite[]> {
    try {
      const { data, error } = await supabase
        .from('magic_invites')
        .select('*')
        .eq('practice_id', practiceId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      return (data || []).map(mapMagicInviteRow);
    } catch (error) {
      console.error('Error getting practice invites:', error);
      return [];
    }
  }
}

