import { supabase } from '@/services/supabase';
import type {
  NotificationSettings,
  NotificationSettingsInsert,
  NotificationChannel,
  NotificationType,
} from '@/types/supabase';
import { useAuthStore } from '@/stores/auth';
import { notificationLogger } from 'src/utils/logger';
import { ref, reactive } from 'vue';
import type { NotificationMessage } from '@/types/notifications';

export class NotificationService {
  private registration: ServiceWorkerRegistration | null = null;
  private inAppNotifications = ref<NotificationMessage[]>([]);
  private settings = reactive<Record<string, NotificationSettings>>({});

  constructor() {
    this.initializeServiceWorker();
    this.loadNotificationSettings();
  }

  /**
   * Initialize service worker for push notifications
   */
  private async initializeServiceWorker() {
    if ('serviceWorker' in navigator && 'PushManager' in window) {
      try {
        this.registration = await navigator.serviceWorker.register('/sw.js');
        notificationLogger.info(
          'Service Worker registered',
          { registration: this.registration?.scope ?? 'unknown' }
        );
      } catch (error) {
        notificationLogger.error('Service Worker registration failed', error as Record<string, unknown>);
      }
    }
  }

  /**
   * Request push notification permission
   */
  async requestNotificationPermission(): Promise<NotificationPermission> {
    if (!('Notification' in window)) {
      throw new Error('This browser does not support notifications');
    }

    const permission = await Notification.requestPermission();

    if (permission === 'granted') {
      await this.subscribeToPushNotifications();
    }

    return permission;
  }

  /**
   * Subscribe to push notifications
   */
  async subscribeToPushNotifications(): Promise<PushSubscription | null> {
    if (!this.registration) {
      throw new Error('Service worker not registered');
    }

    try {
      // You would need to replace this with your actual VAPID public key
      const vapidPublicKey = 'your-vapid-public-key';

      const subscription = await this.registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: this.urlBase64ToUint8Array(vapidPublicKey),
      });

      // Store subscription in database
      await this.storePushSubscription(subscription);

      return subscription;
    } catch (error) {
      notificationLogger.error(
        'Failed to subscribe to push notifications',
        error as Record<string, unknown>
      );
      return null;
    }
  }

  /**
   * Store push subscription in database
   */
  private async storePushSubscription(
    subscription: PushSubscription
  ): Promise<void> {
    const authStore = useAuthStore();
    const user = authStore.user;

    if (!user) {
      return;
    }

    const endpoint = subscription.endpoint;
    const keys = subscription.getKey
      ? {
          p256dh: this.arrayBufferToBase64(subscription.getKey('p256dh')),
          auth: this.arrayBufferToBase64(subscription.getKey('auth')),
        }
      : null;

    const tokenData = {
      user_id: user.id,
      token: JSON.stringify({ endpoint, keys }),
      platform: 'web',
      is_active: true,
    };

    // TODO: Implement push_tokens functionality when push notifications are activated
    notificationLogger.info(
      'Push subscription stored (placeholder):',
      tokenData
    );
  }

  /**
   * Send in-app notification
   */
  showInAppNotification(notification: NotificationMessage): void {
    const id = crypto.randomUUID();
    const notificationWithId = { ...notification, id };

    this.inAppNotifications.value.unshift(notificationWithId);

    // Auto-remove after 5 seconds unless requireInteraction is true
    if (!notification.requireInteraction) {
      setTimeout(() => {
        this.removeInAppNotification(id);
      }, 5000);
    }
  }

  /**
   * Remove in-app notification
   */
  removeInAppNotification(id: string): void {
    const index = this.inAppNotifications.value.findIndex(n => n.id === id);
    if (index !== -1) {
      this.inAppNotifications.value.splice(index, 1);
    }
  }

  /**
   * Clear all in-app notifications
   */
  clearAllInAppNotifications(): void {
    this.inAppNotifications.value = [];
  }

  /**
   * Get in-app notifications
   */
  getInAppNotifications() {
    return this.inAppNotifications;
  }

  /**
   * Send browser notification
   */
  async showBrowserNotification(
    notification: NotificationMessage
  ): Promise<void> {
    if (Notification.permission !== 'granted') {
      return;
    }

    const options: NotificationOptions = {
      body: notification.body,
      icon: notification.icon || '/icons/icon-192x192.png',
      badge: notification.badge || '/icons/icon-192x192.png',
      tag: notification.tag,
      requireInteraction: notification.requireInteraction,
      data: notification.data,
      actions: notification.actions,
    } as any; // actions is not standard NotificationOptions

    new Notification(notification.title, options);
  }

  /**
   * Send push notification (server-side would call this)
   */
  async sendPushNotification(
    userId: string,
    notification: NotificationMessage
  ): Promise<void> {
    // In a real implementation, this would be called from your backend
    // Here we simulate it by showing a browser notification
    const authStore = useAuthStore();
    if (authStore.user?.id === userId) {
      await this.showBrowserNotification(notification);
    }
  }

  /**
   * Load notification settings for current user
   */
  async loadNotificationSettings(): Promise<void> {
    const authStore = useAuthStore();
    const user = authStore.user;
    const practiceId = authStore.selectedPractice?.id;

    if (!user || !practiceId) {
      return;
    }

    const { data, error } = await supabase
      .from('notification_settings')
      .select('*')
      .eq('user_id', user.id)
      .eq('practice_id', practiceId);

    if (error) {
      notificationLogger.error('Failed to load notification settings:', error);
      return;
    }

    data?.forEach(setting => {
      const key = `${setting.notification_type}_${setting.channel}`;
      this.settings[key] = setting;
    });
  }

  /**
   * Update notification settings
   */
  async updateNotificationSetting(
    notificationType: NotificationType,
    channel: NotificationChannel,
    isEnabled: boolean,
    settings?: any
  ): Promise<void> {
    const authStore = useAuthStore();
    const user = authStore.user;
    const practiceId = authStore.selectedPractice?.id;

    if (!user || !practiceId) {
      return;
    }

    const settingData: NotificationSettingsInsert = {
      user_id: user.id,
      practice_id: practiceId,
      notification_type: notificationType,
      channel,
      is_enabled: isEnabled,
      settings: settings || {},
    };

    const { error } = await supabase
      .from('notification_settings')
      .upsert(settingData, {
        onConflict: 'user_id,practice_id,notification_type,channel',
        ignoreDuplicates: false,
      });

    if (error) {
      notificationLogger.error('Failed to update notification setting:', error);
      return;
    }

    // Update local cache
    const key = `${notificationType}_${channel}`;
    this.settings[key] = {
      ...settingData,
      id: crypto.randomUUID(),
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    } as any;
  }

  /**
   * Check if notification is enabled
   */
  isNotificationEnabled(
    notificationType: NotificationType,
    channel: NotificationChannel
  ): boolean {
    const key = `${notificationType}_${channel}`;
    return this.settings[key]?.is_enabled ?? true; // Default to enabled
  }

  /**
   * Send stock alert notification
   */
  async sendStockAlert(
    productName: string,
    currentStock: number,
    minimumStock: number
  ): Promise<void> {
    const notification: NotificationMessage = {
      title: 'Voorraad Waarschuwing',
      body: `${productName} heeft lage voorraad (${currentStock}/${minimumStock})`,
      type: 'stock_alert',
      icon: '/icons/warning.png',
      tag: 'stock-alert',
      requireInteraction: true,
      data: { productName, currentStock, minimumStock },
    };

    if (this.isNotificationEnabled('stock_alert', 'in_app')) {
      this.showInAppNotification(notification);
    }

    if (this.isNotificationEnabled('stock_alert', 'push')) {
      await this.showBrowserNotification(notification);
    }
  }

  /**
   * Send order update notification
   */
  async sendOrderUpdate(
    orderNumber: string,
    status: string,
    message?: string
  ): Promise<void> {
    const notification: NotificationMessage = {
      title: 'Bestelling Update',
      body: message || `Bestelling ${orderNumber} is ${status}`,
      type: 'order_update',
      icon: '/icons/order.png',
      tag: `order-${orderNumber}`,
      data: { orderNumber, status },
    };

    if (this.isNotificationEnabled('order_update', 'in_app')) {
      this.showInAppNotification(notification);
    }

    if (this.isNotificationEnabled('order_update', 'push')) {
      await this.showBrowserNotification(notification);
    }
  }

  /**
   * Send system notification
   */
  async sendSystemNotification(
    title: string,
    message: string,
    data?: any
  ): Promise<void> {
    const notification: NotificationMessage = {
      title,
      body: message,
      type: 'system_notification',
      icon: '/icons/system.png',
      data,
    };

    if (this.isNotificationEnabled('system_notification', 'in_app')) {
      this.showInAppNotification(notification);
    }

    if (this.isNotificationEnabled('system_notification', 'push')) {
      await this.showBrowserNotification(notification);
    }
  }

  /**
   * Send reminder notification
   */
  async sendReminder(
    title: string,
    message: string,
    data?: any
  ): Promise<void> {
    const notification: NotificationMessage = {
      title,
      body: message,
      type: 'reminder',
      icon: '/icons/reminder.png',
      requireInteraction: true,
      data,
    };

    if (this.isNotificationEnabled('reminder', 'in_app')) {
      this.showInAppNotification(notification);
    }

    if (this.isNotificationEnabled('reminder', 'push')) {
      await this.showBrowserNotification(notification);
    }
  }

  /**
   * Schedule periodic stock checks
   */
  startStockMonitoring(): void {
    // Check stock levels every hour
    setInterval(
      async () => {
        await this.checkStockLevels();
      },
      60 * 60 * 1000
    );

    // Initial check
    this.checkStockLevels();
  }

  /**
   * Check stock levels and send alerts
   */
  private async checkStockLevels(): Promise<void> {
    const authStore = useAuthStore();
    const practiceId = authStore.selectedPractice?.id;

    if (!practiceId) {
      return;
    }

    try {
      const { data: lowStockItems, error } = await supabase
        .from('product_list_items')
        .select(
          `
          *,
          products (name),
          product_lists!inner (practice_id)
        `
        )
        .eq('product_lists.practice_id', practiceId);
        // TODO: Add filter for current_stock < minimum_stock when RLS supports raw expressions

      if (error) {
        notificationLogger.error('Failed to check stock levels:', error);
        return;
      }

      for (const item of lowStockItems || []) {
        await this.sendStockAlert(
          item.products?.name || 'Unknown Product',
          item.current_stock,
          item.minimum_stock
        );
      }
    } catch (error) {
      notificationLogger.error('Stock monitoring error', error as Record<string, unknown>);
    }
  }

  // Utility methods
  private urlBase64ToUint8Array(base64String: string): Uint8Array {
    const padding = '='.repeat((4 - (base64String.length % 4)) % 4);
    const base64 = (base64String + padding)
      .replace(/-/g, '+')
      .replace(/_/g, '/');

    const rawData = window.atob(base64);
    const outputArray = new Uint8Array(rawData.length);

    for (let i = 0; i < rawData.length; ++i) {
      outputArray[i] = rawData.charCodeAt(i);
    }
    return outputArray;
  }

  private arrayBufferToBase64(buffer: ArrayBuffer | null): string {
    if (!buffer) {
      return '';
    }
    const bytes = new Uint8Array(buffer);
    let binary = '';
    for (let i = 0; i < bytes.byteLength; i++) {
      binary += String.fromCharCode(bytes[i] ?? 0);
    }
    return window.btoa(binary);
  }
}

export const notificationService = new NotificationService();

import { ref, reactive } from 'vue';
import { supabase } from '@/services/supabase';
import type {
  Product,
  ShoppingCartItem,
} from '@/types/supabase';
import { useAuthStore } from '@/stores/auth';
import { offlineLogger } from 'src/utils/logger';
import { analyticsService } from './analytics';
import type { OfflineAction, OfflineData } from '@/types/offline';

export class OfflineService {
  private isOnline = ref(navigator.onLine);
  private offlineActions = ref<OfflineAction[]>([]);
  private offlineData = reactive<OfflineData>({
    bestellijsten: [],
    bestellijst_items: [],
    products: [],
    shopping_carts: [],
    shopping_cart_items: [],
    last_sync: null,
  });
  private syncInProgress = ref(false);
  private maxRetries = 3;
  private syncInterval: NodeJS.Timeout | null = null;

  constructor() {
    this.setupNetworkListeners();
    this.loadOfflineData();
    this.startPeriodicSync();
  }

  /**
   * Setup network status listeners
   */
  private setupNetworkListeners(): void {
    window.addEventListener('online', () => {
      this.isOnline.value = true;
      this.syncWhenOnline();
    });

    window.addEventListener('offline', () => {
      this.isOnline.value = false;
    });
  }

  /**
   * Check if device is online
   */
  getOnlineStatus(): boolean {
    return this.isOnline.value;
  }

  /**
   * Sync data when coming back online
   */
  private async syncWhenOnline(): Promise<void> {
    if (this.isOnline.value && !this.syncInProgress.value) {
      await this.syncToServer();
      await this.downloadLatestData();
    }
  }

  /**
   * Download latest data for offline use
   */
  async downloadLatestData(): Promise<void> {
    if (!this.isOnline.value) {
      return;
    }

    const authStore = useAuthStore();
    const practiceId = authStore.selectedPractice?.id;

    if (!practiceId) {
      return;
    }

    try {
      // Download bestellijsten
      const { data: bestellijsten, error: bestellijstenError } = await supabase
        .from('product_lists')
        .select('*')
        .eq('practice_id', practiceId);

      if (bestellijstenError) throw bestellijstenError;

      // Download bestellijst items
      const { data: items, error: itemsError } = await supabase
        .from('product_list_items')
        .select(
          `
          *,
          product_lists!inner (practice_id)
        `
        )
        .eq('product_lists.practice_id', practiceId);

      if (itemsError) throw itemsError;

      // Download products (only those in use)
      const productIds = [
        ...new Set(items?.map(item => item.product_id) || []),
      ];
      let products: Product[] = [];

      if (productIds.length > 0) {
        const { data: productsData, error: productsError } = await supabase
          .from('products')
          .select('*')
          .in('id', productIds);

        if (productsError) throw productsError;
        products = productsData || [];
      }

      // Download shopping carts
      const { data: carts, error: cartsError } = await supabase
        .from('shopping_carts')
        .select('*')
        .eq('practice_id', practiceId);

      if (cartsError) throw cartsError;

      // Download shopping cart items
      const cartIds = carts?.map(cart => cart.id) || [];
      let cartItems: ShoppingCartItem[] = [];

      if (cartIds.length > 0) {
        const { data: cartItemsData, error: cartItemsError } = await supabase
          .from('shopping_cart_items')
          .select('*')
          .in('cart_id', cartIds);

        if (cartItemsError) throw cartItemsError;
        cartItems = cartItemsData || [];
      }

      // Update offline data
      this.offlineData.bestellijsten = bestellijsten || [];
      this.offlineData.bestellijst_items =
        items?.map(item => ({
          ...item,
          last_counted: item.last_counted,
        })) || [];
      this.offlineData.products = products;
      this.offlineData.shopping_carts = carts || [];
      this.offlineData.shopping_cart_items = cartItems;
      this.offlineData.last_sync = new Date();

      // Save to localStorage
      this.saveOfflineData();

      // Track analytics
      analyticsService.trackEvent('offline_data_downloaded', {
        bestellijsten_count: this.offlineData.bestellijsten.length,
        items_count: this.offlineData.bestellijst_items.length,
        products_count: this.offlineData.products.length,
      });
    } catch (error) {
      offlineLogger.error(
        'Failed to download offline data:',
        error as Record<string, unknown>
      );
      throw error;
    }
  }

  /**
   * Get offline data
   */
  getOfflineData(): OfflineData {
    return this.offlineData;
  }

  /**
   * Add action to offline queue
   */
  addOfflineAction(
    type: OfflineAction['type'],
    table: string,
    data: any
  ): void {
    const authStore = useAuthStore();
    const practiceId = authStore.selectedPractice?.id;
    const userId = authStore.user?.id;

    if (!practiceId || !userId) {
      offlineLogger.error('Cannot add offline action - no practice or user');
      return;
    }

    const action: OfflineAction = {
      id: crypto.randomUUID(),
      type,
      table,
      data,
      timestamp: new Date(),
      retry_count: 0,
      practice_id: practiceId,
      user_id: userId,
    };

    this.offlineActions.value.push(action);
    this.saveOfflineActions();

    // Try to sync immediately if online
    if (this.isOnline.value) {
      this.syncToServer();
    }
  }

  /**
   * Sync offline actions to server
   */
  async syncToServer(): Promise<void> {
    if (
      !this.isOnline.value ||
      this.syncInProgress.value ||
      this.offlineActions.value.length === 0
    ) {
      return;
    }

    this.syncInProgress.value = true;

    try {
      offlineLogger.info(
        `Syncing ${this.offlineActions.value.length} offline actions...`
      );

      const actionsToSync = [...this.offlineActions.value];
      const syncedActions: string[] = [];
      const failedActions: OfflineAction[] = [];

      for (const action of actionsToSync) {
        try {
          await this.syncSingleAction(action);
          syncedActions.push(action.id);
        } catch (error) {
          offlineLogger.error('Failed to sync action:', {
            action,
            error: error as Record<string, unknown>,
          });

          // Increment retry count
          action.retry_count++;

          if (action.retry_count >= this.maxRetries) {
            offlineLogger.error('Max retries reached for action:', action);
            // Remove from queue after max retries
            syncedActions.push(action.id);
          } else {
            failedActions.push(action);
          }
        }
      }

      // Remove synced actions
      this.offlineActions.value = this.offlineActions.value.filter(
        action => !syncedActions.includes(action.id)
      );

      // Update failed actions with new retry counts
      failedActions.forEach(failedAction => {
        const index = this.offlineActions.value.findIndex(
          a => a.id === failedAction.id
        );
        if (index !== -1) {
          this.offlineActions.value[index] = failedAction;
        }
      });

      this.saveOfflineActions();

      offlineLogger.info(
        `Sync completed. Synced: ${syncedActions.length}, Failed: ${failedActions.length}`
      );

      // Track analytics
      analyticsService.trackEvent('offline_sync_completed', {
        synced_count: syncedActions.length,
        failed_count: failedActions.length,
        remaining_count: this.offlineActions.value.length,
      });
    } catch (error) {
      offlineLogger.error(
        'Sync process failed:',
        error as Record<string, unknown>
      );
    } finally {
      this.syncInProgress.value = false;
    }
  }

  /**
   * Sync a single action to server
   */
  private async syncSingleAction(action: OfflineAction): Promise<void> {
    switch (action.type) {
      case 'create':
        await this.syncCreateAction(action);
        break;
      case 'update':
        await this.syncUpdateAction(action);
        break;
      case 'delete':
        await this.syncDeleteAction(action);
        break;
      default:
        throw new Error(`Unknown action type: ${action.type}`);
    }
  }

  /**
   * Sync create action
   */
  private async syncCreateAction(action: OfflineAction): Promise<void> {
    const table = (supabase as any).from(action.table);
    const result: any = await table.insert(action.data);
    const { error } = result;

    if (error) {
      throw new Error(`Failed to sync create: ${error.message}`);
    }
  }

  /**
   * Sync update action
   */
  private async syncUpdateAction(action: OfflineAction): Promise<void> {
    const { id, ...updateData } = action.data;

    const table = (supabase as any).from(action.table);
    const result: any = await table.update(updateData).eq('id', id);
    const { error } = result;

    if (error) {
      throw new Error(`Failed to sync update: ${error.message}`);
    }
  }

  /**
   * Sync delete action
   */
  private async syncDeleteAction(action: OfflineAction): Promise<void> {
    const table = (supabase as any).from(action.table);
    const result: any = await table.delete().eq('id', action.data.id);
    const { error } = result;

    if (error) {
      throw new Error(`Failed to sync delete: ${error.message}`);
    }
  }

  /**
   * Update bestellijst item offline
   */
  updateBestellijstItemOffline(
    itemId: string,
    updates: Record<string, unknown>
  ): void {
    // Update local data
    const index = this.offlineData.bestellijst_items.findIndex(
      item => item.id === itemId
    );
    if (index !== -1) {
      this.offlineData.bestellijst_items[index] = {
        ...this.offlineData.bestellijst_items[index],
        ...updates,
        updated_at: new Date().toISOString(),
      };
      this.saveOfflineData();
    }

    // Add to sync queue
    this.addOfflineAction('update', 'product_list_items', {
      id: itemId,
      ...updates,
      updated_at: new Date().toISOString(),
    });
  }

  /**
   * Add product to cart offline
   */
  addToCartOffline(cartId: string, productId: string, quantity: number): void {
    // Update local data
    const existingItem = this.offlineData.shopping_cart_items.find(
      item => item.cart_id === cartId && item.product_id === productId
    );

    if (existingItem) {
      existingItem.quantity = (existingItem.quantity as number) + quantity;
      existingItem.updated_at = new Date().toISOString();

      this.addOfflineAction('update', 'shopping_cart_items', {
        id: existingItem.id,
        quantity: existingItem.quantity,
        updated_at: existingItem.updated_at,
      });
    } else {
      const newItem: ShoppingCartItem = {
        id: crypto.randomUUID(),
        cart_id: cartId,
        product_id: productId,
        quantity,
        notes: null,
        suggested_by: 'manual',
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString(),
      };

      this.offlineData.shopping_cart_items.push(newItem);
      this.addOfflineAction('create', 'shopping_cart_items', newItem);
    }

    this.saveOfflineData();
  }

  /**
   * Save offline data to localStorage
   */
  private saveOfflineData(): void {
    try {
      localStorage.setItem(
        'remcura_offline_data',
        JSON.stringify({
          ...this.offlineData,
          last_sync: this.offlineData.last_sync?.toISOString(),
        })
      );
    } catch (error) {
      offlineLogger.error(
        'Failed to save offline data:',
        error as Record<string, unknown>
      );
    }
  }

  /**
   * Load offline data from localStorage
   */
  private loadOfflineData(): void {
    try {
      const saved = localStorage.getItem('remcura_offline_data');
      if (saved) {
        const data = JSON.parse(saved);
        Object.assign(this.offlineData, {
          ...data,
          last_sync: data.last_sync ? new Date(data.last_sync) : null,
        });
      }
    } catch (error) {
      offlineLogger.error(
        'Failed to load offline data:',
        error as Record<string, unknown>
      );
    }
  }

  /**
   * Save offline actions to localStorage
   */
  private saveOfflineActions(): void {
    try {
      localStorage.setItem(
        'remcura_offline_actions',
        JSON.stringify(
          this.offlineActions.value.map(action => ({
            ...action,
            timestamp: action.timestamp.toISOString(),
          }))
        )
      );
    } catch (error) {
      offlineLogger.error(
        'Failed to save offline actions:',
        error as Record<string, unknown>
      );
    }
  }

  /**
   * Load offline actions from localStorage
   */
  private loadOfflineActions(): void {
    try {
      const saved = localStorage.getItem('remcura_offline_actions');
      if (saved) {
        const actions = JSON.parse(saved);
        this.offlineActions.value = actions.map((action: any) => ({
          ...action,
          timestamp: new Date(action.timestamp),
        }));
      }
    } catch (error) {
      offlineLogger.error(
        'Failed to load offline actions:',
        error as Record<string, unknown>
      );
    }
  }

  /**
   * Start periodic sync
   */
  private startPeriodicSync(): void {
    // Sync every 5 minutes when online
    this.syncInterval = setInterval(
      () => {
        if (this.isOnline.value && this.offlineActions.value.length > 0) {
          this.syncToServer();
        }
      },
      5 * 60 * 1000
    );
  }

  /**
   * Clear all offline data
   */
  clearOfflineData(): void {
    this.offlineData.bestellijsten = [];
    this.offlineData.bestellijst_items = [];
    this.offlineData.products = [];
    this.offlineData.shopping_carts = [];
    this.offlineData.shopping_cart_items = [];
    this.offlineData.last_sync = null;

    this.offlineActions.value = [];

    localStorage.removeItem('remcura_offline_data');
    localStorage.removeItem('remcura_offline_actions');
  }

  /**
   * Get pending sync count
   */
  getPendingSyncCount(): number {
    return this.offlineActions.value.length;
  }

  /**
   * Get sync status
   */
  getSyncStatus() {
    return {
      isOnline: this.isOnline.value,
      syncInProgress: this.syncInProgress.value,
      pendingActions: this.offlineActions.value.length,
      lastSync: this.offlineData.last_sync,
    };
  }

  /**
   * Force sync now
   */
  async forceSyncNow(): Promise<void> {
    if (this.isOnline.value) {
      await this.syncToServer();
      await this.downloadLatestData();
    } else {
      throw new Error('Cannot sync while offline');
    }
  }

  /**
   * Clean up resources
   */
  destroy(): void {
    if (this.syncInterval) {
      clearInterval(this.syncInterval);
      this.syncInterval = null;
    }
  }
}

export const offlineService = new OfflineService();

import { ref } from 'vue';
import { ServiceErrorHandler } from 'src/utils/service-error-handler';

export interface OfflineAction {
  id: string;
  type: 'create' | 'update' | 'delete';
  table: string;
  data: any;
  timestamp: Date;
  retry_count: number;
  practice_id: string;
  user_id: string;
  priority?: number; // Lower numbers = higher priority
}

export interface ActionSyncResult {
  success: boolean;
  action: OfflineAction;
  error?: Error;
}

export interface ActionExecutor {
  (action: OfflineAction): Promise<void>;
}

export class ActionQueue {
  private actions = ref<OfflineAction[]>([]);
  private readonly maxRetries: number;
  private readonly storageKey = 'offline_actions';
  private executors: Map<string, ActionExecutor> = new Map();

  constructor(maxRetries = 3) {
    this.maxRetries = maxRetries;
    this.loadFromStorage();
  }

  /**
   * Register an executor for a specific table
   */
  registerExecutor(table: string, executor: ActionExecutor): void {
    this.executors.set(table, executor);
  }

  /**
   * Add action to the queue
   */
  addAction(
    type: OfflineAction['type'],
    table: string,
    data: any,
    practiceId: string,
    userId: string,
    priority = 5
  ): string {
    const action: OfflineAction = {
      id: crypto.randomUUID(),
      type,
      table,
      data,
      timestamp: new Date(),
      retry_count: 0,
      practice_id: practiceId,
      user_id: userId,
      priority,
    };

    this.actions.value.push(action);
    this.sortActionsByPriority();
    this.saveToStorage();

    // Offline action added - debug logging removed
    return action.id;
  }

  /**
   * Get all actions
   */
  get allActions() {
    return this.actions.value;
  }

  /**
   * Get actions count
   */
  get count(): number {
    return this.actions.value.length;
  }

  /**
   * Get failed actions (those that have reached max retries)
   */
  get failedActions(): OfflineAction[] {
    return this.actions.value.filter(
      action => action.retry_count >= this.maxRetries
    );
  }

  /**
   * Get pending actions (those that haven't reached max retries)
   */
  get pendingActions(): OfflineAction[] {
    return this.actions.value.filter(
      action => action.retry_count < this.maxRetries
    );
  }

  /**
   * Execute all pending actions
   */
  async executeAll(): Promise<ActionSyncResult[]> {
    const results: ActionSyncResult[] = [];
    const actionsToProcess = [...this.pendingActions];

    for (const action of actionsToProcess) {
      const result = await this.executeAction(action);
      results.push(result);

      if (result.success) {
        this.removeAction(action.id);
      } else {
        this.incrementRetryCount(action.id);
      }
    }

    this.saveToStorage();
    return results;
  }

  /**
   * Execute a single action
   */
  private async executeAction(
    action: OfflineAction
  ): Promise<ActionSyncResult> {
    const executor = this.executors.get(action.table);

    if (!executor) {
      const error = new Error(
        `No executor registered for action type: ${action.type}`
      );
      ServiceErrorHandler.handle(
        error,
        {
          service: 'ActionQueue',
          operation: 'executeAction',
          practiceId: action.practice_id,
          userId: action.user_id,
          metadata: { actionId: action.id, table: action.table },
        },
        { rethrow: false }
      );

      return {
        success: false,
        action,
        error,
      };
    }

    try {
      await executor(action);
      return {
        success: true,
        action,
      };
    } catch (error) {
      ServiceErrorHandler.handle(
        error as Error,
        {
          service: 'ActionQueue',
          operation: 'executeAction',
          practiceId: action.practice_id,
          userId: action.user_id,
          metadata: {
            actionId: action.id,
            table: action.table,
            retryCount: action.retry_count,
          },
        },
        { rethrow: false }
      );

      return {
        success: false,
        action,
        error: error instanceof Error ? error : new Error(String(error)),
      };
    }
  }

  /**
   * Remove action from queue
   */
  removeAction(actionId: string): boolean {
    const index = this.actions.value.findIndex(
      action => action.id === actionId
    );
    if (index !== -1) {
      this.actions.value.splice(index, 1);
      this.saveToStorage();
      return true;
    }
    return false;
  }

  /**
   * Increment retry count for an action
   */
  private incrementRetryCount(actionId: string): void {
    const action = this.actions.value.find(a => a.id === actionId);
    if (action) {
      action.retry_count++;

      // If max retries reached, log it
      if (action.retry_count >= this.maxRetries) {
        console.error('Max retries reached for action:', action);
        ServiceErrorHandler.handle(
          new Error(`Action failed after ${this.maxRetries} retries`),
          {
            service: 'ActionQueue',
            operation: 'incrementRetryCount',
            practiceId: action.practice_id,
            userId: action.user_id,
            metadata: { actionId: action.id, table: action.table },
          },
          { rethrow: false, logLevel: 'warn' }
        );
      }
    }
  }

  /**
   * Clear all actions
   */
  clear(): void {
    this.actions.value = [];
    this.saveToStorage();
  }

  /**
   * Clear failed actions only
   */
  clearFailed(): void {
    this.actions.value = this.actions.value.filter(
      action => action.retry_count < this.maxRetries
    );
    this.saveToStorage();
  }

  /**
   * Retry a specific action
   */
  retryAction(actionId: string): void {
    const action = this.actions.value.find(a => a.id === actionId);
    if (action) {
      action.retry_count = 0;
      this.saveToStorage();
    }
  }

  /**
   * Sort actions by priority (lower number = higher priority)
   */
  private sortActionsByPriority(): void {
    this.actions.value.sort((a, b) => {
      const priorityA = a.priority || 5;
      const priorityB = b.priority || 5;

      if (priorityA !== priorityB) {
        return priorityA - priorityB;
      }

      // If same priority, sort by timestamp (older first)
      return a.timestamp.getTime() - b.timestamp.getTime();
    });
  }

  /**
   * Save actions to localStorage
   */
  private saveToStorage(): void {
    try {
      const serializedActions = this.actions.value.map(action => ({
        ...action,
        timestamp: action.timestamp.toISOString(),
      }));
      localStorage.setItem(this.storageKey, JSON.stringify(serializedActions));
    } catch (error) {
      console.error('Failed to save actions to storage:', error);
    }
  }

  /**
   * Load actions from localStorage
   */
  private loadFromStorage(): void {
    try {
      const stored = localStorage.getItem(this.storageKey);
      if (stored) {
        const parsedActions = JSON.parse(stored);
        this.actions.value = parsedActions.map((action: any) => ({
          ...action,
          timestamp: new Date(action.timestamp),
        }));
        this.sortActionsByPriority();
      }
    } catch (error) {
      console.error('Failed to load actions from storage:', error);
      this.actions.value = [];
    }
  }
}

// Export singleton instance
export const actionQueue = new ActionQueue();

import { reactive } from 'vue';
import { supabase } from 'src/boot/supabase';
import { handleSupabaseError } from 'src/utils/service-error-handler';
import { analyticsService } from '../analytics';
import type {
  Bestellijst,
  BestellijstItem,
  Product,
  ShoppingCart,
  ShoppingCartItem,
} from 'src/types/supabase';

export interface OfflineData {
  bestellijsten: Bestellijst[];
  bestellijst_items: BestellijstItem[];
  products: Product[];
  shopping_carts: ShoppingCart[];
  shopping_cart_items: ShoppingCartItem[];
  last_sync: Date | null;
}

export interface SyncProgress {
  phase:
    | 'bestellijsten'
    | 'items'
    | 'products'
    | 'carts'
    | 'cart_items'
    | 'complete';
  current: number;
  total: number;
  message: string;
}

export type SyncProgressCallback = (progress: SyncProgress) => void;

export class DataSyncManager {
  private data = reactive<OfflineData>({
    bestellijsten: [],
    bestellijst_items: [],
    products: [],
    shopping_carts: [],
    shopping_cart_items: [],
    last_sync: null,
  });

  private readonly storageKey = 'offline_data';
  private syncInProgress = false;

  constructor() {
    this.loadFromStorage();
  }

  /**
   * Get current offline data
   */
  get offlineData(): any {
    return this.data;
  }

  /**
   * Check if sync is in progress
   */
  get isSyncing(): boolean {
    return this.syncInProgress;
  }

  /**
   * Get last sync time
   */
  get lastSync(): Date | null {
    return this.data.last_sync;
  }

  /**
   * Download latest data for offline use
   */
  async downloadData(
    practiceId: string,
    onProgress?: SyncProgressCallback
  ): Promise<void> {
    if (this.syncInProgress) {
      throw new Error('Sync already in progress');
    }

    this.syncInProgress = true;

    try {
      // Downloading latest data for offline use

      // Phase 1: Download bestellijsten
      onProgress?.({
        phase: 'bestellijsten',
        current: 0,
        total: 5,
        message: 'Downloading order lists...',
      });

      const bestellijsten = await this.downloadBestellijsten(practiceId);

      // Phase 2: Download bestellijst items
      onProgress?.({
        phase: 'items',
        current: 1,
        total: 5,
        message: 'Downloading order list items...',
      });

      const items = await this.downloadBestellijstItems(practiceId);

      // Phase 3: Download products
      onProgress?.({
        phase: 'products',
        current: 2,
        total: 5,
        message: 'Downloading products...',
      });

      const products = await this.downloadProducts(items);

      // Phase 4: Download shopping carts
      onProgress?.({
        phase: 'carts',
        current: 3,
        total: 5,
        message: 'Downloading shopping carts...',
      });

      const carts = await this.downloadShoppingCarts(practiceId);

      // Phase 5: Download shopping cart items
      onProgress?.({
        phase: 'cart_items',
        current: 4,
        total: 5,
        message: 'Downloading cart items...',
      });

      const cartItems = await this.downloadCartItems(carts);

      // Update data
      this.data.bestellijsten = bestellijsten as any;
      this.data.bestellijst_items = items as any;
      this.data.products = products as any;
      this.data.shopping_carts = carts as any;
      this.data.shopping_cart_items = cartItems as any;
      this.data.last_sync = new Date();

      // Save to storage
      this.saveToStorage();

      onProgress?.({
        phase: 'complete',
        current: 5,
        total: 5,
        message: 'Sync complete',
      });

      // Offline data downloaded successfully

      // Track analytics
      analyticsService.trackEvent('offline_data_downloaded', {
        bestellijsten_count: bestellijsten.length,
        items_count: items.length,
        products_count: products.length,
        carts_count: carts.length,
        cart_items_count: cartItems.length,
      });
    } catch (error) {
      console.error('Failed to download offline data:', error);
      throw error;
    } finally {
      this.syncInProgress = false;
    }
  }

  /**
   * Download bestellijsten
   */
  private async downloadBestellijsten(
    practiceId: string
  ): Promise<Bestellijst[]> {
    const { data, error } = await supabase
      .from('product_lists')
      .select('*')
      .eq('practice_id', practiceId);

    if (error) {
      handleSupabaseError(error, {
        service: 'DataSyncManager',
        operation: 'downloadBestellijsten',
        practiceId,
      });
    }

    return (data as any) || [];
  }

  /**
   * Download bestellijst items
   */
  private async downloadBestellijstItems(
    practiceId: string
  ): Promise<BestellijstItem[]> {
    const { data, error } = await supabase
      .from('product_list_items')
      .select(
        `
        *,
        product_lists!inner (practice_id)
      `
      )
      .eq('product_lists.practice_id', practiceId);

    if (error) {
      handleSupabaseError(error, {
        service: 'DataSyncManager',
        operation: 'downloadBestellijstItems',
        practiceId,
      });
    }

    return (
      (data as any)?.map((item: any) => ({
        ...item,
        last_counted: item.last_counted,
      })) || []
    );
  }

  /**
   * Download products (only those in use)
   */
  private async downloadProducts(items: BestellijstItem[]): Promise<Product[]> {
    const productIds = [...new Set(items.map(item => item.product_id))];

    if (productIds.length === 0) {
      return [];
    }

    const { data, error } = await supabase
      .from('products')
      .select('*')
      .in('id', productIds);

    if (error) {
      handleSupabaseError(error, {
        service: 'DataSyncManager',
        operation: 'downloadProducts',
        metadata: { productCount: productIds.length },
      });
    }

    return data || [];
  }

  /**
   * Download shopping carts
   */
  private async downloadShoppingCarts(
    practiceId: string
  ): Promise<ShoppingCart[]> {
    const { data, error } = await supabase
      .from('shopping_carts')
      .select('*')
      .eq('practice_id', practiceId);

    if (error) {
      handleSupabaseError(error, {
        service: 'DataSyncManager',
        operation: 'downloadShoppingCarts',
        practiceId,
      });
    }

    return data || [];
  }

  /**
   * Download shopping cart items
   */
  private async downloadCartItems(
    carts: ShoppingCart[]
  ): Promise<ShoppingCartItem[]> {
    const cartIds = carts.map(cart => cart.id);

    if (cartIds.length === 0) {
      return [];
    }

    const { data, error } = await supabase
      .from('shopping_cart_items')
      .select('*')
      .in('cart_id', cartIds);

    if (error) {
      handleSupabaseError(error, {
        service: 'DataSyncManager',
        operation: 'downloadCartItems',
        metadata: { cartCount: cartIds.length },
      });
    }

    return data || [];
  }

  /**
   * Clear all offline data
   */
  clearData(): void {
    this.data.bestellijsten = [];
    this.data.bestellijst_items = [];
    this.data.products = [];
    this.data.shopping_carts = [];
    this.data.shopping_cart_items = [];
    this.data.last_sync = null;

    this.saveToStorage();
  }

  /**
   * Get data statistics
   */
  getStats() {
    return {
      bestellijsten: this.data.bestellijsten.length,
      items: this.data.bestellijst_items.length,
      products: this.data.products.length,
      carts: this.data.shopping_carts.length,
      cartItems: this.data.shopping_cart_items.length,
      lastSync: this.data.last_sync,
      totalSize: this.calculateStorageSize(),
    };
  }

  /**
   * Calculate approximate storage size in bytes
   */
  private calculateStorageSize(): number {
    try {
      const serialized = JSON.stringify(this.data);
      return new Blob([serialized]).size;
    } catch {
      return 0;
    }
  }

  /**
   * Save data to localStorage
   */
  private saveToStorage(): void {
    try {
      const serializedData = {
        ...this.data,
        last_sync: this.data.last_sync?.toISOString() || null,
      };
      localStorage.setItem(this.storageKey, JSON.stringify(serializedData));
    } catch (error) {
      console.error('Failed to save offline data to storage:', error);
    }
  }

  /**
   * Load data from localStorage
   */
  private loadFromStorage(): void {
    try {
      const stored = localStorage.getItem(this.storageKey);
      if (stored) {
        const parsedData = JSON.parse(stored);

        this.data.bestellijsten = parsedData.bestellijsten || [];
        this.data.bestellijst_items = parsedData.bestellijst_items || [];
        this.data.products = parsedData.products || [];
        this.data.shopping_carts = parsedData.shopping_carts || [];
        this.data.shopping_cart_items = parsedData.shopping_cart_items || [];
        this.data.last_sync = parsedData.last_sync
          ? new Date(parsedData.last_sync)
          : null;
      }
    } catch (error) {
      console.error('Failed to load offline data from storage:', error);
      this.clearData();
    }
  }
}

