import { supabase } from '@/services/supabase';
import type {
  Order,
  OrderInsert,
  OrderItemInsert,
  OrderWithItems,
} from '@/types/supabase';
import type { MagentoOrder } from '@/types/magento';
import { useAuthStore } from '@/stores/auth';

export class OrderProcessingService {
  /**
   * Create a new order from shopping cart
   */
  async createOrderFromCart(cartId: string, notes?: string): Promise<Order> {
    const authStore = useAuthStore();
    const user = authStore.user;
    const practiceId = authStore.clinicId;

    if (!user || !practiceId) {
      throw new Error('User not authenticated or no practice selected');
    }

    // Get cart details
    const { data: cart, error: cartError } = await supabase
      .from('shopping_carts')
      .select(
        `
        *,
        shopping_cart_items (
          *,
          products (*)
        )
      `
      )
      .eq('id', cartId)
      .single();

    if (cartError || !cart) {
      throw new Error('Shopping cart not found');
    }

    // Generate order number
    const { data: orderNumber, error: numberError } = await supabase.rpc(
      'generate_order_number',
      { practice_uuid: practiceId } as any
    );

    if (numberError || !orderNumber) {
      throw new Error('Failed to generate order number');
    }

    // Create order
    const orderData: OrderInsert = {
      practice_id: practiceId,
      cart_id: cartId,
      order_number: orderNumber,
      status: 'draft',
      notes: notes,
      created_by: user.id,
    };

    const { data: order, error: orderError } = await supabase
      .from('orders')
      .insert(orderData)
      .select()
      .single();

    if (orderError || !order) {
      throw new Error('Failed to create order');
    }

    // Create order items from cart items
    if (cart.shopping_cart_items && cart.shopping_cart_items.length > 0) {
      const orderItems: OrderItemInsert[] = cart.shopping_cart_items.map(
        item => ({
          order_id: order.id,
          product_id: item.product_id,
          quantity: item.quantity,
          unit_price: item.products?.price || 0,
          total_price: (item.products?.price || 0) * item.quantity,
          notes: item.notes,
        })
      );

      const { error: itemsError } = await supabase
        .from('order_items')
        .insert(orderItems);

      if (itemsError) {
        throw new Error('Failed to create order items');
      }
    }

    // Update cart status
    await supabase
      .from('shopping_carts')
      .update({ status: 'ordered' })
      .eq('id', cartId);

    return order;
  }

  /**
   * Get orders for current practice
   */
  async getOrders(filters?: {
    status?: string;
    supplier_id?: string;
    date_from?: string;
    date_to?: string;
  }): Promise<OrderWithItems[]> {
    const authStore = useAuthStore();
    const practiceId = authStore.clinicId;

    if (!practiceId) {
      throw new Error('No practice selected');
    }

    let query = supabase
      .from('orders')
      .select(
        `
        *,
        order_items (
          *,
          products (*)
        ),
        suppliers (*)
      `
      )
      .eq('practice_id', practiceId)
      .order('order_date', { ascending: false });

    if (filters?.status) {
      query = query.eq('status', filters.status);
    }
    if (filters?.supplier_id) {
      query = query.eq('supplier_id', filters.supplier_id);
    }
    if (filters?.date_from) {
      query = query.gte('order_date', filters.date_from);
    }
    if (filters?.date_to) {
      query = query.lte('order_date', filters.date_to);
    }

    const { data, error } = await query;

    if (error) {
      throw new Error('Failed to get orders');
    }

    return data || [];
  }

  /**
   * Update order status
   */
  async updateOrderStatus(
    orderId: string,
    status: string,
    notes?: string
  ): Promise<void> {
    const updateData: Partial<Order> = {
      status,
      updated_at: new Date().toISOString(),
    };
    if (notes) {
      updateData.notes = notes;
    }

    const { error } = await supabase
      .from('orders')
      .update(updateData)
      .eq('id', orderId);

    if (error) {
      throw new Error('Failed to update order status');
    }
  }

  /**
   * Export order to CSV
   */
  async exportOrderToCSV(orderId: string): Promise<Blob> {
    const orders = await this.getOrders();
    const order = orders.find(o => o.id === orderId);

    if (!order) {
      throw new Error('Order not found');
    }

    const csvHeaders = [
      'Order Number',
      'Product SKU',
      'Product Name',
      'Quantity',
      'Unit Price',
      'Total Price',
      'Notes',
    ];

    const csvRows =
      order.order_items?.map(item => [
        order.order_number,
        item.products?.sku || '',
        item.products?.name || '',
        item.quantity.toString(),
        (item.unit_price || 0).toFixed(2),
        (item.total_price || 0).toFixed(2),
        item.notes || '',
      ]) || [];

    const csvContent = [
      csvHeaders.join(','),
      ...csvRows.map(row => row.map(field => `"${field}"`).join(',')),
    ].join('\n');

    return new Blob([csvContent], { type: 'text/csv' });
  }

  /**
   * Export multiple orders to CSV
   */
  async exportOrdersToCSV(orderIds: string[]): Promise<Blob> {
    const orders = await this.getOrders();
    const filteredOrders = orders.filter(o => orderIds.includes(o.id));

    const csvHeaders = [
      'Order Number',
      'Order Date',
      'Status',
      'Supplier',
      'Product SKU',
      'Product Name',
      'Quantity',
      'Unit Price',
      'Total Price',
      'Order Notes',
      'Item Notes',
    ];

    const csvRows: string[][] = [];

    filteredOrders.forEach(order => {
      if (order.order_items && order.order_items.length > 0) {
        order.order_items.forEach(item => {
          csvRows.push([
            order.order_number,
            order.order_date
              ? new Date(order.order_date).toLocaleDateString()
              : '',
            order.status ?? '',
            order.suppliers?.name || '',
            item.products?.sku || '',
            item.products?.name || '',
            item.quantity.toString(),
            (item.unit_price || 0).toFixed(2),
            (item.total_price || 0).toFixed(2),
            order.notes || '',
            item.notes || '',
          ]);
        });
      } else {
        csvRows.push([
          order.order_number,
          order.order_date
            ? new Date(order.order_date).toLocaleDateString()
            : '',
          order.status ?? '',
          order.suppliers?.name || '',
          '',
          '',
          '0',
          '0.00',
          '0.00',
          order.notes || '',
          '',
        ]);
      }
    });

    const csvContent = [
      csvHeaders.join(','),
      ...csvRows.map(row => row.map(field => `"${field}"`).join(',')),
    ].join('\n');

    return new Blob([csvContent], { type: 'text/csv' });
  }

  /**
   * Generate PDF for order
   */
  async generateOrderPDF(orderId: string): Promise<Blob> {
    // For now, return HTML content that can be converted to PDF
    // In a full implementation, you'd use a library like jsPDF or html2canvas
