export class MultiSupplierOrderListService {
  /**
   * Create a new supplier-agnostic order list
   */
  async createOrderList(data: {
    practice_id: string;
    name: string;
    description?: string;
    location_id: string;
    auto_reorder_enabled?: boolean;
  }): Promise<SupplierAgnosticOrderList> {
    try {
      orderLogger.info('Creating new supplier-agnostic order list:', data.name);

      const { data: orderList, error } = await supabase
        .from('order_lists')
        .insert<OrderListInsert>({
          practice_id: data.practice_id,
          name: data.name,
          description: data.description ?? null,
          location_id: data.location_id,
          auto_reorder_enabled: data.auto_reorder_enabled ?? false,
        })
        .select()
        .single();

      if (error) throw error;

      orderLogger.info('‚úÖ Order list created successfully:', orderList.id);

      return {
        ...mapOrderListRowToDTO(orderList as OrderListRow),
        items: [],
      } satisfies SupplierAgnosticOrderList;
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to create order list:', error);
      throw new Error(`Failed to create order list: ${error.message}`);
    }
  }

  /**
   * Add a product to an order list with supplier selection
   */
  async addProductToOrderList(
    orderListId: string,
    data: {
      product_id: string;
      supplier_id: string;
      minimum_stock: number;
      maximum_stock: number;
      is_preferred_supplier?: boolean;
    }
  ): Promise<OrderListItemDTO> {
    try {
      orderLogger.info('Adding product to order list:', {
        orderListId,
        productId: data.product_id,
      });

      // Get product and supplier details
      const [productResult, supplierProductResult, stockResult] =
        await Promise.all([
          supabase
            .from('products')
            .select('id, name, sku')
            .eq('id', data.product_id)
            .single<ProductRow>(),
          supabase
            .from('supplier_products')
            .select(
              `
            supplier_sku,
            cost_price,
            currency,
            lead_time_days,
            supplier_id,
            suppliers (id, name)
          `
            )
            .eq('product_id', data.product_id)
            .eq('supplier_id', data.supplier_id)
            .single<SupplierProductWithSupplier>(),
          supabase
            .from('stock_levels')
            .select('current_quantity')
            .eq('product_id', data.product_id)
            .order('created_at', { ascending: false })
            .limit(1)
            .maybeSingle<StockLevelRow>(),
        ]);

      if (productResult.error) throw productResult.error;
      if (supplierProductResult.error) throw supplierProductResult.error;

      const product = productResult.data;
      const supplierProduct = supplierProductResult.data;
      const currentStock = stockResult.data
        ? mapStockLevelRowToView(stockResult.data).currentQuantity
        : null;

      if (!product || !supplierProduct) {
        throw new Error('Product or supplier product could not be loaded');
      }

      // Insert order list item
      const { data: orderListItem, error } = await supabase
        .from('order_list_items')
        .insert<OrderListItemInsert>({
          order_list_id: orderListId,
          product_id: data.product_id,
          supplier_product_id: supplierProduct.id,
          preferred_supplier_id: data.supplier_id,
          minimum_stock: data.minimum_stock,
          maximum_stock: data.maximum_stock,
          current_stock: currentStock,
          unit_price: supplierProduct.cost_price,
          ordered_quantity: data.minimum_stock,
          suggested_quantity: data.minimum_stock,
          total_price:
            (supplierProduct.cost_price ?? 0) * Math.max(data.minimum_stock, 0),
        })
        .select(
          `
          *,
          product:products(id, name, sku),
          supplier_product:supplier_products(id, supplier_id, supplier_sku, cost_price, currency, lead_time_days),
          supplier:suppliers!order_list_items_preferred_supplier_id_fkey(id, name)
        `
        )
        .single<OrderListItemRow>();

      if (error) throw error;

      orderLogger.info('‚úÖ Product added to order list successfully');

      const dto = mapOrderListItemRowToDTO(orderListItem);
      return {
        ...dto,
        product: {
          ...(dto.product ?? {}),
          id: product.id,
          name: product.name,
          sku: product.sku,
        } as OrderListItemDTO['product'],
        supplier_product: supplierProduct
          ? ({
              ...(dto.supplier_product ?? {}),
              id: supplierProduct.id,
              supplier_id: supplierProduct.supplier_id,
              supplier_sku: supplierProduct.supplier_sku ?? null,
              cost_price: supplierProduct.cost_price ?? null,
              currency: supplierProduct.currency ?? null,
              lead_time_days: supplierProduct.lead_time_days ?? null,
            } as OrderListItemDTO['supplier_product'])
          : dto.supplier_product,
      } satisfies OrderListItemDTO;
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to add product to order list:', error);
      throw new Error(`Failed to add product to order list: ${error.message}`);
    }
  }

  /**
   * Update supplier for an existing order list item
   */
  async updateItemSupplier(
    itemId: string,
    newSupplierId: string
  ): Promise<OrderListItemDTO> {
    try {
      orderLogger.info('Updating supplier for order list item:', {
        itemId,
        newSupplierId,
      });

      // Get current item
      const { data: currentItem, error: currentError } = await supabase
        .from('order_list_items')
        .select('product_id, order_list_id')
        .eq('id', itemId)
        .single();

      if (currentError) throw currentError;

      // Get new supplier product details
      const { data: supplierProduct, error: supplierError } = await supabase
        .from('supplier_products')
        .select(
          `
          id,
          supplier_sku,
          cost_price,
          currency,
          lead_time_days,
          supplier_id,
          suppliers (id, name)
        `
        )
        .eq('product_id', currentItem.product_id)
        .eq('supplier_id', newSupplierId)
        .single();

      if (supplierError) throw supplierError;

      // Update the item
      const { data: updatedItem, error: updateError } = await supabase
        .from('order_list_items')
        .update({
          supplier_product_id: supplierProduct.id,
          preferred_supplier_id: newSupplierId,
          unit_price: supplierProduct.cost_price,
          updated_at: new Date().toISOString(),
        })
        .eq('id', itemId)
        .select(
          `
          *,
          product:products(id, name, sku),
          supplier_product:supplier_products(id, supplier_id, supplier_sku, cost_price, currency, lead_time_days),
          supplier:suppliers!order_list_items_preferred_supplier_id_fkey(id, name)
        `
        )
        .single<OrderListItemRow>();

      if (updateError) throw updateError;

      orderLogger.info('‚úÖ Supplier updated successfully');

      const dto = mapOrderListItemRowToDTO(updatedItem);
      return {
        ...dto,
        supplier_product: {
          ...(dto.supplier_product ?? {}),
          id: supplierProduct.id,
          supplier_id: supplierProduct.supplier_id,
          supplier_sku: supplierProduct.supplier_sku ?? null,
          cost_price: supplierProduct.cost_price ?? null,
          currency: supplierProduct.currency ?? null,
          lead_time_days: supplierProduct.lead_time_days ?? null,
        } as OrderListItemDTO['supplier_product'],
      } satisfies OrderListItemDTO;
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to update item supplier:', error);
      throw new Error(`Failed to update item supplier: ${error.message}`);
    }
  }

  /**
   * Get alternative suppliers for a product
   */
  async getAlternativeSuppliers(productId: string): Promise<
    Array<{
      supplier_id: string;
      supplier_name: string;
      supplier_sku?: string;
      cost_price: number;
      currency: string;
      lead_time_days: number;
      is_available: boolean;
      price_difference_percent: number;
    }>
  > {
    try {
      const { data: supplierProducts, error } = await supabase
        .from('supplier_products')
        .select(
          `
          supplier_id,
          supplier_sku,
          cost_price,
          currency,
          lead_time_days,
          is_available,
          suppliers (id, name)
        `
        )
        .eq('product_id', productId)
        .eq('is_available', true)
        .order('cost_price', { ascending: true });

      if (error) throw error;

      const lowestPrice = supplierProducts[0]?.cost_price ?? 0;

      return supplierProducts.map(sp => ({
        supplier_id: sp.supplier_id,
        supplier_name: sp.suppliers?.name ?? '',
        supplier_sku: sp.supplier_sku ?? undefined,
        cost_price: sp.cost_price ?? 0,
        currency: sp.currency || 'EUR',
        lead_time_days: sp.lead_time_days ?? 1,
        is_available: sp.is_available ?? false,
        price_difference_percent:
          lowestPrice > 0 && sp.cost_price !== null
            ? ((sp.cost_price - lowestPrice) / lowestPrice) * 100
            : 0,
      }));
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to get alternative suppliers:', error);
      throw new Error(`Failed to get alternative suppliers: ${error.message}`);
    }
  }

  /**
   * Get analytics for an order list
   */
  async getOrderListAnalytics(
    orderListId: string
  ): Promise<OrderListAnalytics> {
    try {
      const { data: items, error } = await supabase
        .from('order_list_items')
        .select(
          `
          minimum_stock,
          maximum_stock,
          current_stock,
          unit_price,
          lead_time_days,
          suppliers (id, name)
        `
        )
        .eq('order_list_id', orderListId);

      if (error) throw error;

      const totalItems = items.length;
      const suppliersCount = new Set(
        items
          .map(item => item.suppliers?.id)
          .filter((id): id is string => Boolean(id))
      ).size;
      const lowStockItems = items.filter(item => {
        const current = item.current_stock ?? 0;
        const minimum = item.minimum_stock ?? 0;
        return current <= minimum;
      }).length;
      const outOfStockItems = items.filter(
        item => (item.current_stock ?? 0) === 0
      ).length;
      const totalValue = items.reduce(
        (sum, item) => sum + (item.unit_price ?? 0),
        0
      );
      const avgLeadTime =
        items.reduce(
          (sum, item) => sum + ((item as any).supplier_product?.lead_time_days ?? 0),
          0
        ) / (totalItems || 1);

      // Supplier breakdown
      const supplierMap = new Map<
        string,
        {
          supplier_id: string;
          supplier_name: string;
          item_count: number;
          total_value: number;
          total_lead_time: number;
        }
      >();
      items.forEach(item => {
        const supplierId = item.suppliers?.id;
        if (!supplierId) {
          return;
        }
        if (!supplierMap.has(supplierId)) {
          supplierMap.set(supplierId, {
            supplier_id: supplierId,
            supplier_name: item.suppliers?.name ?? '',
            item_count: 0,
            total_value: 0,
            total_lead_time: 0,
          });
        }
        const supplier = supplierMap.get(supplierId);
        if (!supplier) return;
        supplier.item_count++;
        supplier.total_value += item.unit_price ?? 0;
        supplier.total_lead_time += (item as any).supplier_product?.lead_time_days ?? 0;
      });

      const supplierBreakdown = Array.from(supplierMap.values()).map(
        supplier => ({
          ...supplier,
          avg_lead_time: supplier.total_lead_time / supplier.item_count,
        })
      );

      return {
        total_items: totalItems,
        suppliers_count: suppliersCount,
        low_stock_items: lowStockItems,
        out_of_stock_items: outOfStockItems,
        total_value: totalValue,
        avg_lead_time: avgLeadTime || 0,
        supplier_breakdown: supplierBreakdown,
      };
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to get order list analytics:', error);
      throw new Error(`Failed to get order list analytics: ${error.message}`);
    }
  }

  /**
   * Optimize supplier selection for better pricing and lead times
   */
  async optimizeSupplierSelection(
    orderListId: string,
    criteria: {
      prioritize: 'price' | 'lead_time' | 'balanced';
      max_price_increase_percent?: number;
      max_lead_time_days?: number;
    }
  ): Promise<{
    recommendations: Array<{
      item_id: string;
      current_supplier: string;
      recommended_supplier: string;
      price_savings: number;
      lead_time_improvement: number;
      reason: string;
    }>;
    total_savings: number;
    avg_lead_time_improvement: number;
  }> {
    try {
      orderLogger.info(
        'Optimizing supplier selection for order list:',
        orderListId
      );

      // Get current order list items
      const { data: items, error: itemsError } = await supabase
        .from('order_list_items')
        .select(
          `
          id,
          product_id,
          supplier_product_id,
          preferred_supplier_id,
          unit_price,
          supplier_product:supplier_products(id, supplier_id, supplier_sku, cost_price, currency, lead_time_days),
          suppliers (id, name, lead_time_days)
        `
        )
        .eq('order_list_id', orderListId);

      if (itemsError) throw itemsError;

      const recommendations: Array<{
        item_id: string;
        current_supplier: string;
        recommended_supplier: string;
        price_savings: number;
        lead_time_improvement: number;
        reason: string;
      }> = [];
      let totalSavings = 0;
      let totalLeadTimeImprovement = 0;

      for (const item of items) {
        const alternatives = await this.getAlternativeSuppliers(
          item.product_id
        );

        if (alternatives.length <= 1) continue; // No alternatives

        const currentPrice = item.unit_price ?? 0;
        const currentLeadTime = item.supplier_product?.lead_time_days ?? 7;
        const currentSupplierId =
          item.preferred_supplier_id ??
          item.supplier_product?.supplier_id ??
          null;

        let bestAlternative;

        switch (criteria.prioritize) {
          case 'price':
            bestAlternative = alternatives
              .filter(alt => alt.supplier_id !== currentSupplierId)
              .filter(alt => alt.cost_price < currentPrice)
              .sort((a, b) => a.cost_price - b.cost_price)[0];
            break;

          case 'lead_time':
            bestAlternative = alternatives
              .filter(alt => alt.supplier_id !== currentSupplierId)
              .filter(alt => alt.lead_time_days < currentLeadTime)
              .sort((a, b) => a.lead_time_days - b.lead_time_days)[0];
            break;

          case 'balanced':
            bestAlternative = alternatives
              .filter(alt => alt.supplier_id !== currentSupplierId)
              .map(alt => ({
                ...alt,
                score:
                  (currentPrice > 0
                    ? ((currentPrice - alt.cost_price) / currentPrice) * 0.6
                    : 0) +
                  (currentLeadTime > 0
                    ? ((currentLeadTime - alt.lead_time_days) /
                        currentLeadTime) *
                      0.4
                    : 0),
              }))
              .filter(alt => alt.score > 0)
              .sort((a, b) => b.score - a.score)[0];
            break;
        }

        if (bestAlternative) {
          const priceSavings = currentPrice - bestAlternative.cost_price;
          const leadTimeImprovement =
            currentLeadTime - bestAlternative.lead_time_days;

          // Apply criteria filters
          if (
            criteria.max_price_increase_percent &&
            priceSavings < 0 &&
            Math.abs((priceSavings / currentPrice) * 100) >
              criteria.max_price_increase_percent
          ) {
            continue;
          }

          if (
            criteria.max_lead_time_days &&
            bestAlternative.lead_time_days > criteria.max_lead_time_days
          ) {
            continue;
          }

          recommendations.push({
            item_id: item.id,
            current_supplier: (item.suppliers as any)?.name ?? 'Unknown',
            recommended_supplier: bestAlternative.supplier_name,
            price_savings: priceSavings,
            lead_time_improvement: leadTimeImprovement,
            reason:
              priceSavings > 0
                ? `Save ‚Ç¨${priceSavings.toFixed(2)} per unit`
                : `Improve lead time by ${leadTimeImprovement} days`,
          });

          totalSavings += priceSavings;
          totalLeadTimeImprovement += leadTimeImprovement;
        }
      }

      orderLogger.info('‚úÖ Supplier optimization completed');

      return {
        recommendations,
        total_savings: totalSavings,
        avg_lead_time_improvement:
          recommendations.length > 0
            ? totalLeadTimeImprovement / recommendations.length
            : 0,
      };
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to optimize supplier selection:', error);
      throw new Error(
        `Failed to optimize supplier selection: ${error.message}`
      );
    }
  }

  /**
   * Duplicate an order list to another department/location
   */
  async duplicateOrderList(
    orderListId: string,
    newData: {
      name: string;
      description?: string;
      location_id: string;
    }
  ): Promise<SupplierAgnosticOrderList> {
    try {
      orderLogger.info('Duplicating order list:', orderListId);

      // Get original order list
      const { data: originalList, error: listError } = await supabase
        .from('order_lists')
        .select(
          `
          practice_id,
          auto_reorder_enabled,
          order_list_items (
            product_id,
            preferred_supplier_id,
            minimum_stock,
            maximum_stock,
            is_preferred_supplier
          )
        `
        )
        .eq('id', orderListId)
        .single<
          OrderListRow & {
            order_list_items: Array<
              OrderListItemRow & {
                preferred_supplier_id?: string | null;
                supplier_id?: string | null;
              }
            >;
          }
        >();

      if (listError) throw listError;

      // Create new order list
      if (!newData.location_id) {
        throw new Error('Location is required to duplicate order list');
      }

      const newOrderList = await this.createOrderList({
        practice_id: originalList.practice_id,
        name: newData.name,
        description: newData.description,
        location_id: newData.location_id,
        auto_reorder_enabled: originalList.auto_reorder_enabled ?? false,
      });

      // Copy all items
      const itemPromises = originalList.order_list_items.map(item => {
        const supplierId =
          item.preferred_supplier_id ??
          (item as { supplier_id?: string | null }).supplier_id ??
          null;

        if (!supplierId) {
          return Promise.resolve(null);
        }

        return this.addProductToOrderList(newOrderList.id, {
          product_id: item.product_id,
          supplier_id: supplierId,
          minimum_stock: item.minimum_stock ?? 0,
          maximum_stock: item.maximum_stock ?? 0,
        });
      });

      const items = (await Promise.all(itemPromises)).filter(
        (item): item is OrderListItemDTO => item !== null
      );

      orderLogger.info('‚úÖ Order list duplicated successfully');

      return {
        ...newOrderList,
        items,
      };
    } catch (error: any) {
      orderLogger.error('‚ùå Failed to duplicate order list:', error);
      throw new Error(`Failed to duplicate order list: ${error.message}`);
    }
  }
}

export const multiSupplierOrderListService =
  new MultiSupplierOrderListService();

import { supabase } from '@/boot/supabase';
import { orderLogger } from '@/utils/logger';
import { t } from '@/utils/i18n-service';
import type {
  SupplierOrder,
  OrderSendingResult,
} from '@/stores/orderLists/orderLists-supplier-splitting';
import type { Tables } from '@/types';

export interface APIConfig {
  api_endpoint: string;
  api_key?: string;
  api_username?: string;
  api_password?: string;
  api_token?: string;
  api_format?: 'json' | 'xml' | 'form-data';
  api_auth_type?: 'bearer' | 'basic' | 'api-key' | 'oauth2';
  oauth2_config?: {
    client_id?: string;
    client_secret?: string;
    token_endpoint?: string;
    scope?: string;
  };
  webhook_url?: string;
  test_mode?: boolean;
  timeout_seconds?: number;
  custom_headers?: Record<string, string>;
}

export interface APIOrderPayload {
  order_reference: string;
  order_date: string;
  requested_delivery_date?: string;
  customer: {
    id?: string;
    name: string;
    address: {
      street: string;
      city: string;
      postal_code: string;
      country: string;
    };
    contact?: {
      email?: string;
      phone?: string;
    };
  };
  items: Array<{
    sku: string;
    name?: string;
    quantity: number;
    unit_price: number;
    line_total: number;
  }>;
  totals: {
    subtotal: number;
    tax?: number;
    shipping?: number;
    total: number;
    currency: string;
  };
  notes?: string;
  metadata?: Record<string, any>;
}

export class APIService {
  private cache = new Map<string, { token: string; expires_at: number }>();

  /**
   * Send order via API
   */
  async sendOrderViaAPI(
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> {
    try {
      orderLogger.info(
        `Sending order ${orderReference} via API to supplier ${order.supplier_name}`
      );

      // Get supplier API configuration
      const { data: supplier, error: supplierError } = await supabase
        .from('suppliers')
        .select('integration_config, name, code')
        .eq('id', order.supplier_id)
        .single<Tables<'suppliers'>>();

      if (supplierError || !supplier) {
        throw new Error(t('services.supplierIntegration.supplierNotFound'));
      }

      const rawConfig = supplier.integration_config;
      if (
        !rawConfig ||
        typeof rawConfig !== 'object' ||
        Array.isArray(rawConfig)
      ) {
        throw new Error(
          t('services.supplierIntegration.apiEndpointNotConfigured')
        );
      }

      const apiConfig = rawConfig as Partial<APIConfig>;

      const apiEndpoint = apiConfig.api_endpoint;
      if (typeof apiEndpoint !== 'string' || apiEndpoint.length === 0) {
        throw new Error(
          t('services.supplierIntegration.apiEndpointNotConfigured')
        );
      }

      // Optional auth configuration
      if (
        apiConfig.api_auth_type === 'oauth2' &&
        (!apiConfig.oauth2_config?.client_id ||
          !apiConfig.oauth2_config.client_secret)
      ) {
        throw new Error(
          t('services.supplierIntegration.oauth2ConfigurationIncomplete')
        );
      }

      // Get practice details
      const { data: practice, error: practiceError } = await supabase
        .from('practices')
        .select('name, address, city, postal_code, country, email, phone')
        .eq('id', order.practice_id)
        .single<Tables<'practices'>>();

      if (practiceError || !practice) {
        throw new Error(
          t('services.supplierIntegration.practiceDetailsNotFound')
        );
      }

      // Build API payload
      const apiPayload = this.buildAPIPayload(order, orderReference, practice);

      // Get authentication token if needed
      const authHeaders = await this.getAuthHeaders(apiConfig as APIConfig);

      // Send order to API
      const response = await this.sendToAPI(
        apiConfig as APIConfig,
        apiPayload,
        authHeaders
      );

      // Record the order
      await this.recordSupplierOrder(order, orderReference, 'api', response);

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: response.success ? 'success' : 'failed',
        method_used: 'api',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        error_message: response.success ? '' : (response.error ?? ''),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    } catch (error: any) {
      orderLogger.error(
        `API order sending failed for ${orderReference}:`,
        error
      );

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: 'failed',
        method_used: 'api',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        error_message: error instanceof Error ? error.message : String(error),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    }
  }

  /**
   * Build API payload from order data
   */
  private buildAPIPayload(
    order: SupplierOrder,
    orderReference: string,
    practice: Tables<'practices'>
  ): APIOrderPayload {
    const contact: { email?: string; phone?: string } = {};
    if (practice.email) {
      contact.email = practice.email;
    }
    if (practice.phone) {
      contact.phone = practice.phone;
    }

    return {
      order_reference: orderReference,
      order_date: new Date().toISOString(),
      requested_delivery_date: order.estimated_delivery_date,
      customer: {
        id: order.practice_id,
        name: practice.name ?? '',
        address: {
          street: practice.address ?? '',
          city: practice.city ?? '',
          postal_code: practice.postal_code ?? '',
          country: practice.country ?? 'NL',
        },
        ...(Object.keys(contact).length > 0 ? { contact } : {}),
      },
      items: order.items.map(item => ({
        sku: item.supplier_sku || item.product_sku || '',
        name: item.product_name ?? '',
        quantity: item.quantity,
        unit_price: item.unit_price ?? 0,
        line_total: item.quantity * (item.unit_price ?? 0),
      })),
      totals: {
        subtotal: order.items.reduce((sum, item) => sum + item.total_price, 0),
        total:
          order.items.reduce((sum, item) => sum + item.total_price, 0) +
          (order.shipping_cost || 0),
        currency: 'EUR',
        shipping: order.shipping_cost ?? undefined,
      },
      notes: `Automated order from Remcura for ${practice.name ?? ''}`,
      metadata: {
        source: 'remcura',
        practice_id: order.practice_id,
        supplier_id: order.supplier_id,
        created_at: new Date().toISOString(),
      },
    };
  }

  /**
   * Get authentication headers based on config
   */
  private async getAuthHeaders(
    config: APIConfig
  ): Promise<Record<string, string>> {
    const headers: Record<string, string> = {};

    // Add custom headers
    if (config.custom_headers) {
      Object.assign(headers, config.custom_headers);
    }

    switch (config.api_auth_type) {
      case 'bearer':
        if (config.api_token) {
          headers['Authorization'] = `Bearer ${config.api_token}`;
        } else if (config.oauth2_config) {
          const token = await this.getOAuth2Token(config);
          headers['Authorization'] = `Bearer ${token}`;
        }
        break;

      case 'basic':
        if (config.api_username && config.api_password) {
          const auth = btoa(`${config.api_username}:${config.api_password}`);
          headers['Authorization'] = `Basic ${auth}`;
        }
        break;

      case 'api-key':
        if (config.api_key) {
          headers['X-API-Key'] = config.api_key;
          // Some APIs use different header names
          headers['Authorization'] = `ApiKey ${config.api_key}`;
        }
        break;

      case 'oauth2': {
        const token = await this.getOAuth2Token(config);
        headers['Authorization'] = `Bearer ${token}`;
        break;
      }
    }

    return headers;
  }

  /**
   * Get OAuth2 token (cached)
   */
  private async getOAuth2Token(config: APIConfig): Promise<string> {
    if (
      !config.oauth2_config?.client_id ||
      !config.oauth2_config?.client_secret
    ) {
      throw new Error(
        t('services.supplierIntegration.oauth2ConfigurationIncomplete')
      );
    }

    const cacheKey = `${config.oauth2_config.client_id}_${config.oauth2_config.token_endpoint}`;
    const cached = this.cache.get(cacheKey);

    // Return cached token if still valid
    if (cached && cached.expires_at > Date.now() + 60000) {
      // 1 minute buffer
      return cached.token;
    }

    try {
      const tokenEndpoint =
        config.oauth2_config.token_endpoint ||
        `${config.api_endpoint}/oauth/token`;

      const tokenResponse = await fetch(tokenEndpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({
          grant_type: 'client_credentials',
          client_id: config.oauth2_config.client_id,
          client_secret: config.oauth2_config.client_secret,
          scope: config.oauth2_config.scope || '',
        }),
      });

      if (!tokenResponse.ok) {
        throw new Error(
          t('services.supplierIntegration.oauth2TokenRequestFailed', {
            status: tokenResponse.status,
          })
        );
      }

      const tokenData = await tokenResponse.json();
      const expiresAt = Date.now() + tokenData.expires_in * 1000 - 60000; // 1 minute buffer

      // Cache the token
      this.cache.set(cacheKey, {
        token: tokenData.access_token,
        expires_at: expiresAt,
      });

      return tokenData.access_token;
    } catch (error: any) {
      orderLogger.error('OAuth2 token acquisition failed:', error);
      throw new Error(
        t('services.supplierIntegration.oauth2AuthenticationFailed', {
          error: error.message,
        })
      );
    }
  }

  /**
   * Send data to API endpoint
   */
  private async sendToAPI(
    config: APIConfig,
    payload: APIOrderPayload,
    authHeaders: Record<string, string>
  ): Promise<{ success: boolean; error?: string; response?: any }> {
    try {
      const headers: Record<string, string> = {
        ...authHeaders,
      };

      let body: string | FormData;

      switch (config.api_format || 'json') {
        case 'json':
          headers['Content-Type'] = 'application/json';
          body = JSON.stringify(payload);
          break;

        case 'xml':
          headers['Content-Type'] = 'application/xml';
          body = this.convertToXML(payload);
          break;

        case 'form-data': {
          const formData = new FormData();
          this.flattenObjectToFormData(payload, formData);
          body = formData;
          // Don't set Content-Type for FormData, browser will set it with boundary
          break;
        }

        default:
          throw new Error(
            t('services.supplierIntegration.unsupportedApiFormat', {
              format: config.api_format,
            })
          );
      }

      const controller = new AbortController();
      const timeoutId = setTimeout(
        () => controller.abort(),
        (config.timeout_seconds || 30) * 1000
      );

      const response = await fetch(config.api_endpoint, {
        method: 'POST',
        headers,
        body,
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      const responseText = await response.text();
      let responseData: any;

      try {
        responseData = JSON.parse(responseText);
      } catch {
        responseData = responseText;
      }

      if (!response.ok) {
        throw new Error(
          t('services.supplierIntegration.apiResponseError', {
            status: response.status,
            response: responseText,
          })
        );
      }

      orderLogger.info(
        `API order sent successfully, response: ${response.status}`
      );

      return {
        success: true,
        response: {
          status: response.status,
          headers: Object.fromEntries(response.headers.entries()),
          body: responseData,
        },
      };
    } catch (error: any) {
      if (error.name === 'AbortError') {
        orderLogger.error('API request timed out');
        return {
          success: false,
          error: 'Request timed out',
        };
      }

      orderLogger.error(`API sending failed: ${error.message}`);
      return {
        success: false,
        error: error.message,
      };
    }
  }

  /**
   * Convert JSON payload to XML
   */
  private convertToXML(payload: APIOrderPayload): string {
    return `<?xml version="1.0" encoding="UTF-8"?>
<Order>
  <OrderReference>${payload.order_reference}</OrderReference>
  <OrderDate>${payload.order_date}</OrderDate>
  ${
    payload.requested_delivery_date
      ? `<RequestedDeliveryDate>${payload.requested_delivery_date}</RequestedDeliveryDate>`
      : ''
  }
  <Customer>
    <ID>${payload.customer.id}</ID>
    <Name><![CDATA[${payload.customer.name}]]></Name>
    <Address>
      <Street><![CDATA[${payload.customer.address.street}]]></Street>
      <City><![CDATA[${payload.customer.address.city}]]></City>
      <PostalCode>${payload.customer.address.postal_code}</PostalCode>
      <Country>${payload.customer.address.country}</Country>
    </Address>
    ${
      payload.customer.contact?.email
        ? `<Email>${payload.customer.contact.email}</Email>`
        : ''
    }
    ${
      payload.customer.contact?.phone
        ? `<Phone>${payload.customer.contact.phone}</Phone>`
        : ''
    }
  </Customer>
  <Items>
    ${payload.items
      .map(
        item => `
    <Item>
      <SKU>${item.sku}</SKU>
      <Name><![CDATA[${item.name || ''}]]></Name>
      <Quantity>${item.quantity}</Quantity>
      <UnitPrice>${item.unit_price}</UnitPrice>
      <LineTotal>${item.line_total}</LineTotal>
    </Item>`
      )
      .join('')}
  </Items>
  <Totals>
    <Subtotal>${payload.totals.subtotal}</Subtotal>
    <Total>${payload.totals.total}</Total>
    <Currency>${payload.totals.currency}</Currency>
    ${
      payload.totals.shipping
        ? `<Shipping>${payload.totals.shipping}</Shipping>`
        : ''
    }
  </Totals>
  ${payload.notes ? `<Notes><![CDATA[${payload.notes}]]></Notes>` : ''}
</Order>`;
  }

  /**
   * Flatten object to FormData
   */
  private flattenObjectToFormData(
    obj: any,
    formData: FormData,
    prefix = ''
  ): void {
    for (const key in obj) {
      if (Object.prototype.hasOwnProperty.call(obj, key)) {
        const formKey = prefix ? `${prefix}[${key}]` : key;
        const value = obj[key];

        if (
          value !== null &&
          typeof value === 'object' &&
          !(value instanceof Date)
        ) {
          if (Array.isArray(value)) {
            value.forEach((item, index) => {
              this.flattenObjectToFormData(
                item,
                formData,
                `${formKey}[${index}]`
              );
            });
          } else {
            this.flattenObjectToFormData(value, formData, formKey);
          }
        } else {
          formData.append(formKey, value?.toString() || '');
        }
      }
    }
  }

  /**
   * Record order in supplier_orders table
   */
  private async recordSupplierOrder(
    order: SupplierOrder,
    orderReference: string,
    method: string,
    response: { success: boolean; error?: string; response?: unknown }
  ): Promise<void> {
    const subtotal = order.items.reduce(
      (sum, item) => sum + item.total_price,
      0
    );
    const total = subtotal + (order.shipping_cost || 0);

    orderLogger.info('Supplier order recorded (simulation)', {
      supplier_id: order.supplier_id,
      order_reference: orderReference,
      method,
      success: response.success,
      subtotal,
      total,
    });
  }

  /**
   * Test API connection
   */
  async testAPIConnection(
    config: APIConfig
  ): Promise<{ success: boolean; error?: string; response?: any }> {
    try {
      const headers = await this.getAuthHeaders(config);

      // Try a simple GET request to test the connection
      const testEndpoint =
        config.api_endpoint.replace(/\/orders?$/, '/health') ||
        `${config.api_endpoint}/test`;

      const response = await fetch(testEndpoint, {
        method: 'GET',
        headers,
      });

      const responseText = await response.text();

      return {
        success: response.ok,
        response: {
          status: response.status,
          body: responseText,
        },
      };
    } catch (error: any) {
      return {
        success: false,
        error: error.message,
      };
    }
  }
}

export const apiService = new APIService();

import { supabase } from '@/boot/supabase';
import { orderLogger } from '@/utils/logger';
import type {
  SupplierOrder,
  OrderSendingResult,
} from '@/stores/orderLists/orderLists-supplier-splitting';
import type { Tables } from '@/types';

export interface EDIConfig {
  edi_endpoint: string;
  edi_partner_id: string;
  edi_interchange_id?: string;
  edi_test_mode?: boolean;
  edi_username?: string;
  edi_password?: string;
  edi_format?: 'ORDERS' | 'X12_850' | 'EDIFACT_ORDERS';
}

export interface EDIOrderItem {
  line_number: number;
  sku: string;
  quantity: number;
  unit_price: number;
  description?: string;
  uom?: string; // Unit of Measure
}

export interface EDIOrder {
  order_number: string;
  order_date: string;
  delivery_date?: string;
  buyer_party: {
    gln?: string;
    name: string;
    address: string;
    city: string;
    postal_code: string;
    country: string;
  };
  supplier_party: {
    gln?: string;
    name: string;
    edi_partner_id: string;
  };
  items: EDIOrderItem[];
  currency: string;
  total_amount: number;
  notes?: string;
}

export class EDIService {
  /**
   * Send order via EDI/XML
   */
  async sendOrderViaEDI(
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> {
    try {
      orderLogger.info(
        `Sending order ${orderReference} via EDI to supplier ${order.supplier_name}`
      );

      // Get supplier EDI configuration
      const { data: supplier, error: supplierError } = await supabase
        .from('suppliers')
        .select('integration_config, name, code')
        .eq('id', order.supplier_id)
        .single<Tables<'suppliers'>>();

      if (supplierError || !supplier) {
        throw new Error('Supplier not found');
      }

      const rawConfig = supplier.integration_config;
      if (
        !rawConfig ||
        typeof rawConfig !== 'object' ||
        Array.isArray(rawConfig)
      ) {
        throw new Error('EDI configuration incomplete');
      }

      const ediConfig = rawConfig as Partial<EDIConfig>;
      if (!ediConfig.edi_endpoint || !ediConfig.edi_partner_id) {
        throw new Error('EDI configuration incomplete');
      }

      // Get practice details for buyer party
      const { data: practice, error: practiceError } = await supabase
        .from('practices')
        .select('name, address, city, postal_code, country')
        .eq('id', order.practice_id)
        .single<Tables<'practices'>>();

      if (practiceError || !practice) {
        throw new Error('Practice details not found');
      }

      // Build EDI order
      const ediOrder: EDIOrder = {
        order_number: orderReference,
        order_date: new Date().toISOString().split('T')[0] ?? '',
        delivery_date: order.estimated_delivery_date,
        buyer_party: {
          gln:
            (
              supplier.integration_config as
                | { buyer_gln?: string }
                | null
                | undefined
            )?.buyer_gln ?? '',
          name: practice.name ?? '',
          address: practice.address ?? '',
          city: practice.city ?? '',
          postal_code: practice.postal_code ?? '',
          country: practice.country ?? '',
        },
        supplier_party: {
          gln: ediConfig.edi_partner_id,
          name: supplier.name,
          edi_partner_id: ediConfig.edi_partner_id,
        },
        items: order.items.map((item, index) => ({
          line_number: index + 1,
          sku: item.supplier_sku || item.product_sku || '',
          quantity: item.quantity,
          unit_price: item.unit_price ?? 0,
          description: item.product_name ?? item.product_sku ?? '',
          uom: 'PCE', // Default to pieces, could be configurable
        })),
        currency: 'EUR',
        total_amount: order.items.reduce(
          (sum, item) => sum + item.total_price,
          0
        ),
        notes: `Automated order from Remcura for ${practice.name ?? ''}`,
      };

      // Generate XML based on EDI format
      let xmlContent: string;
      switch (ediConfig.edi_format || 'ORDERS') {
        case 'EDIFACT_ORDERS':
          xmlContent = this.generateEDIFACTXML(ediOrder);
          break;
        case 'X12_850':
          xmlContent = this.generateX12XML(ediOrder);
          break;
        default:
          xmlContent = this.generateGenericOrderXML(ediOrder);
      }

      // Send to EDI endpoint
      const response = await this.sendToEDIEndpoint(
        ediConfig as EDIConfig,
        xmlContent,
        orderReference
      );

      // Record the order in supplier_orders table
      await this.recordSupplierOrder(order, orderReference, 'edi', response);

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: response.success ? 'success' : 'failed',
        method_used: 'edi',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        error_message: response.success ? '' : (response.error ?? ''),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    } catch (error: any) {
      orderLogger.error(
        `EDI order sending failed for ${orderReference}:`,
        error
      );

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: 'failed',
        method_used: 'edi',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        error_message: error instanceof Error ? error.message : String(error),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    }
  }

  /**
   * Generate EDIFACT ORDERS D.96A XML
   */
  private generateEDIFACTXML(order: EDIOrder): string {
    const timestamp = new Date()
      .toISOString()
      .replace(/[-:]/g, '')
      .replace(/\.\d{3}Z/, '');

    return `<?xml version="1.0" encoding="UTF-8"?>
<EDIFACT_ORDERS>
  <UNH>
    <MessageReferenceNumber>${order.order_number}</MessageReferenceNumber>
    <MessageType>ORDERS</MessageType>
    <Version>D</Version>
    <Release>96A</Release>
  </UNH>
  <BGM>
    <DocumentMessageName>220</DocumentMessageName>
    <DocumentMessageNumber>${order.order_number}</DocumentMessageNumber>
    <MessageFunction>9</MessageFunction>
  </BGM>
  <DTM>
    <DateTimePeriod>
      <DateTimePeriodQualifier>137</DateTimePeriodQualifier>
      <DateTimePeriod>${timestamp}</DateTimePeriod>
      <DateTimePeriodFormatQualifier>102</DateTimePeriodFormatQualifier>
    </DateTimePeriod>
  </DTM>
  <NAD_BY>
    <PartyQualifier>BY</PartyQualifier>
    <PartyIdentificationDetails>
      <PartyIdIdentification>${order.buyer_party.gln}</PartyIdIdentification>
    </PartyIdentificationDetails>
    <NameAndAddress>
      <NameAndAddressLine>${order.buyer_party.name}</NameAndAddressLine>
      <NameAndAddressLine>${order.buyer_party.address}</NameAndAddressLine>
      <NameAndAddressLine>${order.buyer_party.postal_code} ${
        order.buyer_party.city
      }</NameAndAddressLine>
      <CountryCode>${order.buyer_party.country}</CountryCode>
    </NameAndAddress>
  </NAD_BY>
  <NAD_SU>
    <PartyQualifier>SU</PartyQualifier>
    <PartyIdentificationDetails>
      <PartyIdIdentification>${order.supplier_party.gln}</PartyIdIdentification>
    </PartyIdentificationDetails>
  </NAD_SU>
  ${order.items
    .map(
      item => `
  <LIN>
    <LineItemNumber>${item.line_number}</LineItemNumber>
    <ItemNumberIdentification>
      <ItemNumber>${item.sku}</ItemNumber>
      <ItemNumberType>SA</ItemNumberType>
    </ItemNumberIdentification>
  </LIN>
  <QTY>
    <QuantityDetails>
      <QuantityQualifier>21</QuantityQualifier>
      <Quantity>${item.quantity}</Quantity>
      <MeasureUnitQualifier>${item.uom}</MeasureUnitQualifier>
    </QuantityDetails>
  </QTY>
  <PRI>
    <PriceInformation>
      <PriceQualifier>AAB</PriceQualifier>
      <Price>${item.unit_price}</Price>
      <PriceType>TU</PriceType>
    </PriceInformation>
  </PRI>`
    )
    .join('')}
  <UNT>
    <NumberOfSegments>${2 + order.items.length * 3}</NumberOfSegments>
    <MessageReferenceNumber>${order.order_number}</MessageReferenceNumber>
  </UNT>
</EDIFACT_ORDERS>`;
  }

  /**
   * Generate X12 850 (Purchase Order) XML
   */
  private generateX12XML(order: EDIOrder): string {
    return `<?xml version="1.0" encoding="UTF-8"?>
<X12_850>
  <ST>
    <TransactionSetIdentifierCode>850</TransactionSetIdentifierCode>
    <TransactionSetControlNumber>${
      order.order_number
    }</TransactionSetControlNumber>
  </ST>
  <BEG>
    <TransactionSetPurposeCode>00</TransactionSetPurposeCode>
    <PurchaseOrderTypeCode>NE</PurchaseOrderTypeCode>
    <PurchaseOrderNumber>${order.order_number}</PurchaseOrderNumber>
    <Date>${order.order_date.replace(/-/g, '')}</Date>
  </BEG>
  <N1_BY>
    <EntityIdentifierCode>BY</EntityIdentifierCode>
    <Name>${order.buyer_party.name}</Name>
    <IdentificationCodeQualifier>91</IdentificationCodeQualifier>
    <IdentificationCode>${order.buyer_party.gln}</IdentificationCode>
  </N1_BY>
  <N3_BY>
    <AddressInformation>${order.buyer_party.address}</AddressInformation>
  </N3_BY>
  <N4_BY>
    <CityName>${order.buyer_party.city}</CityName>
    <StateOrProvinceCode></StateOrProvinceCode>
    <PostalCode>${order.buyer_party.postal_code}</PostalCode>
    <CountryCode>${order.buyer_party.country}</CountryCode>
  </N4_BY>
  <N1_ST>
    <EntityIdentifierCode>ST</EntityIdentifierCode>
    <Name>${order.supplier_party.name}</Name>
    <IdentificationCodeQualifier>91</IdentificationCodeQualifier>
    <IdentificationCode>${order.supplier_party.gln}</IdentificationCode>
  </N1_ST>
  ${order.items
    .map(
      item => `
  <PO1>
    <AssignedIdentification>${item.line_number}</AssignedIdentification>
    <QuantityOrdered>${item.quantity}</QuantityOrdered>
    <UnitOrBasisForMeasurementCode>${item.uom}</UnitOrBasisForMeasurementCode>
    <UnitPrice>${item.unit_price}</UnitPrice>
    <BasisOfUnitPriceCode>PE</BasisOfUnitPriceCode>
    <ProductServiceIdQualifier>SK</ProductServiceIdQualifier>
    <ProductServiceId>${item.sku}</ProductServiceId>
  </PO1>`
    )
    .join('')}
  <CTT>
    <NumberOfLineItems>${order.items.length}</NumberOfLineItems>
  </CTT>
  <SE>
    <NumberOfIncludedSegments>${
      4 + order.items.length
    }</NumberOfIncludedSegments>
    <TransactionSetControlNumber>${
      order.order_number
    }</TransactionSetControlNumber>
  </SE>
</X12_850>`;
  }

  /**
   * Generate generic order XML for suppliers without specific EDI format
   */
  private generateGenericOrderXML(order: EDIOrder): string {
    return `<?xml version="1.0" encoding="UTF-8"?>
<Order xmlns="http://www.remcura.nl/order/schema">
  <Header>
    <OrderNumber>${order.order_number}</OrderNumber>
    <OrderDate>${order.order_date}</OrderDate>
    <Currency>${order.currency}</Currency>
    <TotalAmount>${order.total_amount}</TotalAmount>
    ${
      order.delivery_date
        ? `<RequestedDeliveryDate>${order.delivery_date}</RequestedDeliveryDate>`
        : ''
    }
  </Header>
  <BuyerParty>
    <Name>${order.buyer_party.name}</Name>
    <GLN>${order.buyer_party.gln}</GLN>
    <Address>
      <Street>${order.buyer_party.address}</Street>
      <City>${order.buyer_party.city}</City>
      <PostalCode>${order.buyer_party.postal_code}</PostalCode>
      <Country>${order.buyer_party.country}</Country>
    </Address>
  </BuyerParty>
  <SupplierParty>
    <Name>${order.supplier_party.name}</Name>
    <PartnerID>${order.supplier_party.edi_partner_id}</PartnerID>
  </SupplierParty>
  <OrderLines>
    ${order.items
      .map(
        item => `
    <OrderLine>
      <LineNumber>${item.line_number}</LineNumber>
      <SKU>${item.sku}</SKU>
      <Description><![CDATA[${item.description || ''}]]></Description>
      <Quantity>${item.quantity}</Quantity>
      <UnitOfMeasure>${item.uom}</UnitOfMeasure>
      <UnitPrice>${item.unit_price}</UnitPrice>
      <LineTotal>${(item.quantity * item.unit_price).toFixed(2)}</LineTotal>
    </OrderLine>`
      )
      .join('')}
  </OrderLines>
  ${order.notes ? `<Notes><![CDATA[${order.notes}]]></Notes>` : ''}
</Order>`;
  }

  /**
   * Send XML to EDI endpoint
   */
  private async sendToEDIEndpoint(
    config: EDIConfig,
    xmlContent: string,
    orderReference: string
  ): Promise<{ success: boolean; error?: string; response?: any }> {
    try {
      const headers: Record<string, string> = {
        'Content-Type': 'application/xml',
        'X-Order-Reference': orderReference,
      };

      // Add authentication if configured
      if (config.edi_username && config.edi_password) {
        const auth = btoa(`${config.edi_username}:${config.edi_password}`);
        headers['Authorization'] = `Basic ${auth}`;
      }

      const response = await fetch(config.edi_endpoint, {
        method: 'POST',
        headers,
        body: xmlContent,
      });

      const responseText = await response.text();

      if (!response.ok) {
        throw new Error(
          `EDI endpoint returned ${response.status}: ${responseText}`
        );
      }

      orderLogger.info(`EDI order ${orderReference} sent successfully`);

      return {
        success: true,
        response: {
          status: response.status,
          headers: Object.fromEntries(response.headers.entries()),
          body: responseText,
        },
      };
    } catch (error: any) {
      orderLogger.error(`EDI sending failed: ${error.message}`);
      return {
        success: false,
        error: error.message,
      };
    }
  }

  /**
   * Record order in supplier_orders table
   */
  private async recordSupplierOrder(
    order: SupplierOrder,
    orderReference: string,
    method: string,
    response: any
  ): Promise<void> {
    const subtotal = order.items.reduce(
      (sum, item) => sum + item.total_price,
      0
    );
    const total = subtotal + (order.shipping_cost || 0);

    orderLogger.info('Supplier order recorded (simulation)', {
      supplier_id: order.supplier_id,
      order_reference: orderReference,
      method,
      success: response.success,
      subtotal,
      total,
    });
  }
}

export const ediService = new EDIService();

import { supabase } from '@/boot/supabase';
import { orderLogger } from '@/utils/logger';
import type {
  SupplierOrder,
  OrderSendingResult,
} from '@/stores/orderLists/orderLists-supplier-splitting';
import type { Tables } from '@/types';

export interface PDFConfig {
  email_template?: 'standard' | 'custom';
  pdf_template?: 'standard' | 'custom';
  email_subject_template?: string;
  include_logos?: boolean;
  include_terms?: boolean;
  auto_send_email?: boolean;
  cc_emails?: string[];
  bcc_emails?: string[];
  custom_css?: string;
  footer_text?: string;
}

export interface EmailTemplate {
  subject: string;
  body_html: string;
  body_text: string;
}

export class PDFService {
  /**
   * Send order via PDF (email with PDF attachment)
   */
  async sendOrderViaPDF(
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> {
    try {
      orderLogger.info(
        `Generating PDF order ${orderReference} for supplier ${order.supplier_name}`
      );

      // Get supplier and practice details
      const [supplierResult, practiceResult] = await Promise.all([
        supabase
          .from('suppliers')
          .select('integration_config, name, contact_email, contact_person')
          .eq('id', order.supplier_id)
          .single<Tables<'suppliers'>>(),
        supabase
          .from('practices')
          .select(
            'name, address, city, postal_code, country, contact_email, contact_phone, logo_url'
          )
          .eq('id', order.practice_id)
          .single<Tables<'practices'>>(),
      ]);

      if (supplierResult.error || !supplierResult.data) {
        throw new Error('Supplier not found');
      }

      if (practiceResult.error || !practiceResult.data) {
        throw new Error('Practice details not found');
      }

      const supplier = supplierResult.data;
      const practice = practiceResult.data;
      const practiceName = practice.name ?? 'Onbekende praktijk';
      const rawConfig = supplier.integration_config;
      const pdfConfig: PDFConfig =
        rawConfig && typeof rawConfig === 'object' && !Array.isArray(rawConfig)
          ? (rawConfig as PDFConfig)
          : {};

      if (!supplier.contact_email) {
        throw new Error('Supplier email not configured');
      }

      // Generate PDF content
      const pdfHTML = this.generatePDFHTML(
        order,
        orderReference,
        { ...practice, name: practiceName },
        supplier,
        pdfConfig
      );

      // Generate PDF using browser's print functionality (would need actual PDF generation in production)
      const pdfBlob = await this.generatePDFBlob(pdfHTML);

      // Prepare email
      const emailTemplate = this.generateEmailTemplate(
        order,
        orderReference,
        { ...practice, name: practiceName },
        supplier,
        pdfConfig
      );

      // Send email with PDF attachment
      await this.sendEmail(
        supplier.contact_email,
        emailTemplate,
        pdfBlob,
        orderReference,
        pdfConfig
      );

      // Record the order
      await this.recordSupplierOrder(order, orderReference, 'pdf', {
        success: true,
      });

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: 'success',
        method_used: 'pdf',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    } catch (error: any) {
      orderLogger.error(
        `PDF order sending failed for ${orderReference}:`,
        error
      );

      return {
        supplier_id: order.supplier_id,
        supplier_name: order.supplier_name,
        status: 'failed',
        method_used: 'pdf',
        order_reference: orderReference,
        sent_at: new Date().toISOString(),
        error_message: error instanceof Error ? error.message : String(error),
        delivery_expected: order.estimated_delivery_date ?? '',
      } satisfies OrderSendingResult;
    }
  }

  /**
   * Generate PDF HTML content
   */
  private generatePDFHTML(
    order: SupplierOrder,
    orderReference: string,
    practice: any,
    supplier: any,
    config: PDFConfig
  ): string {
    const currentDate = new Date().toLocaleDateString('nl-NL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });

    const practiceName = practice.name ?? 'Onbekende praktijk';

    return `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Bestelling ${orderReference}</title>
  <style>
    ${this.getDefaultPDFStyles()}
    ${config.custom_css || ''}
  </style>
</head>
<body>
  <div class="pdf-container">
    <!-- Header -->
    <header class="order-header">
      <div class="header-left">
        ${
          config.include_logos && practice.logo_url
            ? `<img src="${practice.logo_url}" alt="${practice.name}" class="logo">`
            : ''
        }
        <div class="practice-info">
          <h1>${practiceName}</h1>
          <div class="address">
            ${practice.address}<br>
            ${practice.postal_code} ${practice.city}<br>
            ${practice.country}
          </div>
          ${
            practice.contact_email
              ? `<div class="contact">Email: ${practice.contact_email}</div>`
              : ''
          }
          ${
            practice.contact_phone
              ? `<div class="contact">Tel: ${practice.contact_phone}</div>`
              : ''
          }
        </div>
      </div>
      <div class="header-right">
        <h2>BESTELLING</h2>
        <div class="order-info">
          <div><strong>Bestelnummer:</strong> ${orderReference}</div>
          <div><strong>Datum:</strong> ${currentDate}</div>
          ${
            order.estimated_delivery_date
              ? `<div><strong>Gewenste leverdatum:</strong> ${new Date(
                  order.estimated_delivery_date
                ).toLocaleDateString('nl-NL')}</div>`
              : ''
          }
        </div>
      </div>
    </header>

    <!-- Supplier info -->
    <section class="supplier-section">
      <h3>Leverancier</h3>
      <div class="supplier-info">
        <strong>${supplier.name}</strong>
        ${
          supplier.contact_person ? `<br>T.a.v. ${supplier.contact_person}` : ''
        }
      </div>
    </section>

    <!-- Order items -->
    <section class="items-section">
      <h3>Bestelde artikelen</h3>
      <table class="items-table">
        <thead>
          <tr>
            <th>Artikel</th>
            <th>SKU</th>
            <th>Aantal</th>
            <th>Prijs per stuk</th>
