              ? `
          <tr>
            <td colspan="4">Verzendkosten</td>
            <td>‚Ç¨${order.shipping_cost.toFixed(2)}</td>
          </tr>
          <tr class="grand-total">
            <td colspan="4"><strong>Totaal inclusief verzending</strong></td>
          <td><strong>‚Ç¨${(
            order.items.reduce((sum, item) => sum + item.total_price, 0) +
            order.shipping_cost
          ).toFixed(2)}</strong></td>
          </tr>
          `
              : ''
          }
        </tfoot>
