});

import { ref, computed } from 'vue';
import { supabase } from '@/boot/supabase';
import { useAuthStore } from '../auth';
import { useSuppliersStore } from '../suppliers';
import { createLogger } from '@/utils/logger';
import {
  OrderListRow,
  OrderListItemRow,
  OrderListStatus,
  OrderListDTO,
  OrderListItemDTO,
  OrderListInsert,
  OrderListItemInsert,
  mapOrderListRowToDTO,
  mapOrderListItemRowToDTO,
} from '@/types/inventory';
import { ServiceErrorHandler } from '@/utils/service-error-handler';
import type {
  OrderListWithItems,
  CreateOrderListRequest,
  UpdateOrderListRequest,
} from '@/types/stores';
import type { Tables } from '@/types';

const log = createLogger('OrderListsCore');

export function useOrderListsCore() {
  // State
  const orderLists = ref<OrderListWithItems[]>([]);
  const loading = ref(false);
  const saving = ref(false);

  // Dependencies
  const authStore = useAuthStore();
  const suppliersStore = useSuppliersStore();

  // Getters
  const getOrderListById = computed(() => {
    return (id: string) => orderLists.value.find((list: any) => list.id === id);
  });

  const getOrderListsBySupplier = computed(() => {
    return (supplierId: string) =>
      orderLists.value.filter((list: any) => list.supplier_id === supplierId);
  });

  const getOrderListsByStatus = computed(() => {
    return (status: OrderListStatus) =>
      orderLists.value.filter((list: any) => list.status === status);
  });

  const orderListStats = computed(() => {
    const stats: Record<OrderListStatus, number> = {
      draft: 0,
      active: 0,
      submitted: 0,
      completed: 0,
      cancelled: 0,
    };

    orderLists.value.forEach(list => {
      const status: OrderListStatus = list.status ?? 'draft';
      stats[status] = (stats[status] ?? 0) + 1;
    });

    return {
      total: orderLists.value.length,
      ...stats,
    };
  });

  // Actions
  const fetchOrderLists = async (practiceId: string) => {
    loading.value = true;
    try {
      const { data, error } = await supabase
        .from('order_lists')
        .select(
          `
          *,
          supplier:suppliers(*),
          items:order_list_items(
            *,
            product:products(*),
            supplier_product:supplier_products(*),
            reorder_suggestion:reorder_suggestions(*)
          )
        `
        )
        .eq('practice_id', practiceId)
        .order('created_at', { ascending: false });

      if (error) throw error;

      const orderListsWithRelations = (data ?? []) as Array<
        OrderListRow & {
          supplier: Tables<'suppliers'> | null;
          items: Array<
            OrderListItemRow & {
              product: Tables<'products'> | null;
              supplier_product: Tables<'supplier_products'> | null;
            }
          > | null;
        }
      >;

      orderLists.value = (orderListsWithRelations as any).map((orderList: any) => {
        const dto = mapOrderListRowToDTO(orderList);
        dto.supplier = orderList.supplier ?? null;
        const items: OrderListItemDTO[] = (orderList.items ?? []).map((item: any) => {
          const itemDto = mapOrderListItemRowToDTO(item);
          itemDto.product = item.product ?? null;
          itemDto.supplier_product = item.supplier_product ?? null;
          return itemDto;
        });

        return {
          ...dto,
          items,
        } satisfies OrderListWithItems;
      });
    } catch (err: unknown) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'fetchOrderLists',
        practiceId,
      });
      log.error('Error fetching order lists', {
        error: handledError.message,
        practiceId,
      });
      throw handledError;
    } finally {
      loading.value = false;
    }
  };

  const createOrderList = async (
    request: CreateOrderListRequest
  ): Promise<OrderListWithItems> => {
    saving.value = true;
    try {
      const orderListData: OrderListInsert = {
        practice_id: request.practice_id,
        location_id: request.location_id,
        supplier_id: request.supplier_id ?? null,
        name: request.name,
        description: request.description ?? null,
        status: request.status ?? 'draft',
        total_items: 0,
        total_value: 0,
        min_order_value: null,
        created_by: authStore.user?.id ?? null,
      };

      const { data, error } = await supabase
        .from('order_lists')
        .insert(orderListData)
        .select(
          `
          *,
          supplier:suppliers(*)
        `
        )
        .single();

      if (error) throw error;

      const createdDto = mapOrderListRowToDTO(data as OrderListRow);
      createdDto.supplier =
        (data as { supplier?: Tables<'suppliers'> | null }).supplier ?? null;

      const createdOrderList: OrderListWithItems = {
        ...createdDto,
        items: [],
      };

      (orderLists.value as any).unshift(createdOrderList);
      return createdOrderList;
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'createOrderList',
        practiceId: request.practice_id,
        metadata: { request },
      });
      log.error('Error creating order list', {
        error: handledError.message,
        practiceId: request.practice_id,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  const updateOrderList = async (
    request: UpdateOrderListRequest
  ): Promise<void> => {
    saving.value = true;
    try {
      const updatePayload: Partial<OrderListRow> = {
        updated_at: new Date().toISOString(),
      };
      if (request.name !== undefined) updatePayload.name = request.name;
      if (request.description !== undefined)
        updatePayload.description = request.description;
      if (request.supplier_id !== undefined)
        updatePayload.supplier_id = request.supplier_id;
      if (request.location_id !== undefined)
        updatePayload.location_id = request.location_id;

      const { error } = await supabase
        .from('order_lists')
        .update(updatePayload)
        .eq('id', request.id)
        .eq('practice_id', authStore.userProfile?.clinic_id || '');

      if (error) throw error;

      // Update local state
      const index = orderLists.value.findIndex(list => list.id === request.id);
      if (index !== -1) {
        const orderList = orderLists.value[index];
        if (!orderList) {
          return;
        }

        if (request.name !== undefined) orderList.name = request.name;
        if (request.description !== undefined)
          orderList.description = request.description;
        if (request.supplier_id !== undefined) {
          orderList.supplier_id = request.supplier_id;
          orderList.supplier =
            (suppliersStore.suppliers as any).find((s: any) => s.id === request.supplier_id) ??
            null;
        }
        if (request.location_id !== undefined) {
          orderList.location_id = request.location_id;
        }
        orderList.updated_at =
          updatePayload.updated_at ?? new Date().toISOString();
      }
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'updateOrderList',
        practiceId: authStore.userProfile?.clinic_id ?? '',
        metadata: { request },
      });
      log.error('Error updating order list', {
        error: handledError.message,
        orderListId: request.id,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  const deleteOrderList = async (id: string): Promise<void> => {
    saving.value = true;
    try {
      const { error } = await supabase
        .from('order_lists')
        .delete()
        .eq('id', id)
        .eq('practice_id', authStore.userProfile?.clinic_id || '');

      if (error) throw error;

      // Update local state
      const index = orderLists.value.findIndex(list => list.id === id);
      if (index !== -1) {
        orderLists.value.splice(index, 1);
      }
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'deleteOrderList',
        practiceId: authStore.userProfile?.clinic_id ?? '',
        metadata: { orderListId: id },
      });
      log.error('Error deleting order list', {
        error: handledError.message,
        orderListId: id,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  const changeOrderListStatus = async (
    orderListId: string,
    status: OrderListStatus
  ): Promise<void> => {
    try {
      saving.value = true;

      // Prepare update data
      const updateData: Partial<OrderListRow> = {
        status,
        updated_at: new Date().toISOString(),
      };

      // Add submission specific fields
      if (status === 'submitted') {
        // No submitted fields in schema; future enhancement could persist elsewhere
      }

      const { error } = await supabase
        .from('order_lists')
        .update(updateData)
        .eq('id', orderListId)
        .eq('practice_id', authStore.userProfile?.clinic_id || '');

      if (error) throw error;

      // Update local state
      const orderList = orderLists.value.find((list: any) => list.id === orderListId);
      if (orderList) {
        orderList.status = status;
        orderList.updated_at = updateData.updated_at ?? orderList.updated_at;
        // Submitted metadata not persisted ‚Äì leaving local fields unchanged
      }
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'changeOrderListStatus',
        practiceId: authStore.userProfile?.clinic_id ?? '',
        metadata: { orderListId, status },
      });
      log.error('Error changing order list status', {
        error: handledError.message,
        orderListId,
        status,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  return {
    // State
    orderLists,
    loading,
    saving,

    // Getters
    getOrderListById,
    getOrderListsBySupplier,
    getOrderListsByStatus,
    orderListStats,

    // Actions
    fetchOrderLists,
    createOrderList,
    updateOrderList,
    deleteOrderList,
    changeOrderListStatus,
  };
}

import { ref, type Ref } from 'vue';
import { supabase } from '@/boot/supabase';
import { useAuthStore } from '../auth';
import { useProductsStore } from '../products';
import { createLogger } from '@/utils/logger';
import type {
  OrderListStatus,
  OrderListDTO,
  OrderListItemDTO,
  OrderListInsert,
  OrderListItemInsert,
  OrderListRow,
} from '@/types/inventory';
import { mapOrderListRowToDTO } from '@/types/inventory';
import { ServiceErrorHandler } from '@/utils/service-error-handler';
import type { OrderListWithItems } from '@/types/stores';

const log = createLogger('OrderListsIntegration');

export function useOrderListsIntegration(
  orderLists: Ref<OrderListWithItems[]>
) {
  // State
  const saving = ref(false);

  // Dependencies
  const authStore = useAuthStore();
  const productsStore = useProductsStore();

  const duplicateOrderList = async (
    originalId: string,
    newName: string
  ): Promise<OrderListWithItems> => {
    saving.value = true;
    try {
      const original = orderLists.value.find(
        (list: OrderListWithItems) => list.id === originalId
      );
      if (!original) throw new Error('Original order list not found');

      const insertPayload: OrderListInsert = {
        practice_id: original.practice_id,
        location_id: original.location_id,
        supplier_id: original.supplier_id,
        name: newName,
        description: original.description ?? null,
        status: 'draft',
        total_items: 0,
        total_value: 0,
        min_order_value: null,
        created_by: authStore.user?.id ?? null,
      };

      const { data: newOrderList, error: orderListError } = await supabase
        .from('order_lists')
        .insert(insertPayload)
        .select(
          `
          *,
          supplier:suppliers(*)
        `
        )
        .single();

      if (orderListError) throw orderListError;

      // Duplicate items if any exist
      if (original.items.length > 0) {
        const itemsToCreate: OrderListItemInsert[] = original.items.map(
          item => ({
            order_list_id: (newOrderList as OrderListRow).id,
            product_id: item.product_id,
            supplier_product_id: item.supplier_product_id ?? null,
            suggested_quantity:
              item.suggested_quantity ?? item.ordered_quantity ?? 0,
            ordered_quantity:
              item.ordered_quantity ?? item.suggested_quantity ?? 0,
            unit_price: item.unit_price ?? null,
            total_price:
              (item.ordered_quantity ?? item.suggested_quantity ?? 0) *
              (item.unit_price ?? 0),
            notes: item.notes ?? null,
          })
        );

        const { error: itemsError } = await supabase
          .from('order_list_items')
          .insert(itemsToCreate);

        if (itemsError) throw itemsError;
      }

      const dto: OrderListDTO = {
        ...mapOrderListRowToDTO(newOrderList as OrderListRow),
        total_items: original.items.length,
        total_cost: (newOrderList as OrderListRow).total_value ?? 0,
        supplier:
          (newOrderList as { supplier?: OrderListDTO['supplier'] }).supplier ??
          null,
      };

      const newOrderListWithItems: OrderListWithItems = {
        ...dto,
        items: original.items.map(item => ({
          ...item,
          order_list_id: dto.id,
        })) as OrderListItemDTO[],
      };

      orderLists.value.unshift(newOrderListWithItems);
      log.info('Order list duplicated', {
        newOrderListId: newOrderList.id,
      });
      return newOrderListWithItems;
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'duplicateOrderList',
        metadata: { originalId, newName },
      });
      log.error('Error duplicating order list', {
        error: handledError.message,
        originalId,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  const addToCart = async (orderListId: string): Promise<void> => {
    try {
      const orderList = orderLists.value.find(
        (list: OrderListWithItems) => list.id === orderListId
      );
      if (!orderList) throw new Error('Order list not found');

      // Clear existing cart
      productsStore.clearCart();

      // Add all items to cart
      for (const item of orderList.items) {
        const product = productsStore.getProductById(item.product_id);
        if (product) {
          const supplierProduct = (product as any).supplierProducts?.find(
            (sp: any) => sp.id === item.supplier_product_id
          );
          productsStore.addToCart(
            product,
            item.suggested_quantity ?? item.ordered_quantity ?? 0,
            supplierProduct?.supplier_id
          );
        }
      }
      log.info('Adding order list to cart', {
        orderListId,
      });
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'addToCart',
        metadata: { orderListId },
      });
      log.error('Error adding order list to cart', {
        error: handledError.message,
        orderListId,
      });
      throw handledError;
    }
  };

  const autoFillFromStockLevels = async (): Promise<void> => {
    saving.value = true;
    try {
      log.info('Auto-fill from stock levels not yet implemented');
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'autoFillFromStockLevels',
        metadata: {},
      });
      log.error('Error auto-filling order list', {
        error: handledError.message,
      });
      throw handledError;
    } finally {
      saving.value = false;
    }
  };

  return {
    // State
    saving,

    // Actions
    duplicateOrderList,
    addToCart,
    autoFillFromStockLevels,
  };
}

import type { Ref } from 'vue';
import { supabase } from '@/boot/supabase';
import { useProductsStore } from '../products';
import { createLogger } from '@/utils/logger';
import type {
  OrderListItemDTO,
  OrderListItemInsert,
  OrderListItemRow,
} from '@/types/inventory';
import { mapOrderListItemRowToDTO } from '@/types/inventory';
import type { Tables } from '@/types';
import { ServiceErrorHandler } from '@/utils/service-error-handler';
import type {
  OrderListWithItems,
  AddOrderListItemRequest,
} from '@/types/stores';

const log = createLogger('OrderListsItems');

export function useOrderListsItems(orderLists: Ref<OrderListWithItems[]>) {
  // Dependencies
  const productsStore = useProductsStore();

  const addOrderListItem = async (
    request: AddOrderListItemRequest
  ): Promise<void> => {
    try {
      const product = productsStore.getProductById(request.product_id);
      if (!product) {
        throw new Error('Product not found');
      }

      const supplierProduct = (product as any).supplierProducts?.find(
        (sp: any) => sp.id === request.supplier_product_id
      );

      if (!supplierProduct) {
        throw new Error('Supplier product not found');
      }

      const itemData: OrderListItemInsert = {
        order_list_id: request.order_list_id,
        product_id: request.product_id,
        supplier_product_id: request.supplier_product_id ?? null,
        suggested_quantity: request.requested_quantity,
        ordered_quantity: request.requested_quantity,
        unit_price:
          supplierProduct.list_price ?? supplierProduct.cost_price ?? 0,
        total_price:
          request.requested_quantity *
          (supplierProduct.list_price ?? supplierProduct.cost_price ?? 0),
        notes: request.notes ?? null,
      };

      const { data, error } = await supabase
        .from('order_list_items')
        .insert(itemData)
        .select(
          `
          *,
          product:products(*),
          supplier_product:supplier_products(*)
        `
        )
        .single();

      if (error) throw error;

      // Update local state
      const orderListIndex = orderLists.value.findIndex(
        (list: OrderListWithItems) => list.id === request.order_list_id
      );
      if (orderListIndex === -1) {
        return;
      }

      const targetList = orderLists.value[orderListIndex];
      if (!targetList) return;
      const newItemRow = data as OrderListItemRow & {
        product: Tables<'products'> | null;
        supplier_product: Tables<'supplier_products'> | null;
      };
      const newItem = mapOrderListItemRowToDTO(newItemRow);
      newItem.product = newItemRow.product ?? null;
      newItem.supplier_product = newItemRow.supplier_product ?? null;

      targetList.items.push(newItem);
      updateOrderListTotals(targetList.id);
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'addOrderListItem',
        metadata: { request },
      });
      log.error('Error adding order list item', {
        error: handledError.message,
        orderListId: request.order_list_id,
      });
      throw handledError;
    }
  };

  const removeOrderListItem = async (itemId: string): Promise<void> => {
    try {
      const { error } = await supabase
        .from('order_list_items')
        .delete()
        .eq('id', itemId);

      if (error) throw error;

      // Update local state
      for (const orderList of orderLists.value) {
        const itemIndex = orderList.items.findIndex(
          (item: OrderListItemDTO) => item.id === itemId
        );
        if (itemIndex !== -1) {
          orderList.items.splice(itemIndex, 1);
          updateOrderListTotals(orderList.id);
          break;
        }
      }
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'removeOrderListItem',
        metadata: { itemId },
      });
      log.error('Error removing order list item', {
        error: handledError.message,
        itemId,
      });
      throw handledError;
    }
  };

  const updateOrderListItem = async (
    itemId: string,
    updates: { requested_quantity?: number; notes?: string }
  ): Promise<void> => {
    try {
      const updateData: Partial<OrderListItemRow> = {};
      if (updates.requested_quantity !== undefined) {
        updateData.suggested_quantity = updates.requested_quantity;
        updateData.ordered_quantity = updates.requested_quantity;
      }
      if (updates.notes !== undefined) {
        updateData.notes = updates.notes;
      }

      const { error } = await supabase
        .from('order_list_items')
        .update(updateData)
        .eq('id', itemId);

      if (error) throw error;

      // Update local state
      for (const orderList of orderLists.value) {
        const item = orderList.items.find(
          (item: OrderListItemDTO) => item.id === itemId
        );
        if (item) {
          if (updates.requested_quantity !== undefined) {
            item.suggested_quantity = updates.requested_quantity;
            item.ordered_quantity = updates.requested_quantity;
            const unit = item.unit_price ?? 0;
            item.total_price = unit * updates.requested_quantity;
          }
          if (updates.notes !== undefined) item.notes = updates.notes;
          updateOrderListTotals(orderList.id);
          break;
        }
      }
    } catch (err) {
      const handledError = ServiceErrorHandler.handle(err as Error, {
        service: 'OrderListsStore',
        operation: 'updateOrderListItem',
        metadata: { itemId, updates },
      });
      log.error('Error updating order list item', {
        error: handledError.message,
        itemId,
      });
      throw handledError;
    }
  };

  const updateOrderListTotals = async (orderListId: string): Promise<void> => {
    const orderList = orderLists.value.find(
      (list: OrderListWithItems) => list.id === orderListId
    );
    if (!orderList) {
      return;
    }

    const totalItems = orderList.items.reduce(
      (sum: number, item: OrderListItemDTO) => sum + item.suggested_quantity,
      0
    );
    const totalValue = orderList.items.reduce(
      (sum: number, item: OrderListItemDTO) => sum + (item.total_price ?? 0),
      0
    );

    try {
      const { error } = await supabase
        .from('order_lists')
        .update({
          total_items: totalItems,
          total_cost: totalValue,
          updated_at: new Date().toISOString(),
        })
        .eq('id', orderListId);

      if (error) throw error;

      orderList.total_items = totalItems;
      orderList.total_cost = totalValue;
      orderList.updated_at = new Date().toISOString();
    } catch (err) {
      log.error('Error updating order list totals', {
        error: err instanceof Error ? err.message : String(err),
        orderListId,
      });
    }
  };

  return {
    // Actions
    addOrderListItem,
    removeOrderListItem,
    updateOrderListItem,
    updateOrderListTotals,
  };
}

import { ref, computed, onUnmounted } from 'vue';
import { supabase } from '@/boot/supabase';
import { orderLogger, createLogger } from '@/utils/logger';
import type { UrgencyLevel, OrderListInsert, OrderListItemInsert } from '@/types/inventory';

// Min/Max specific types
export interface MinMaxItem {
  id: string;
  order_list_id: string;
  product_id: string;
  minimum_stock: number;
  maximum_stock: number;
  current_stock: number;
  reorder_point: number;
  preferred_supplier_id?: string;
  alternative_suppliers: string[];
  last_order_date?: string;
  last_order_quantity: number;
  average_consumption: number;
  lead_time_days: number;
  urgency_level: 'low' | 'normal' | 'high' | 'critical';
  auto_suggest_enabled: boolean;
  manual_override: boolean;
  override_reason?: string;
}

export interface ReorderSuggestion {
  id: string;
  order_list_id: string;
  product_id: string;
  current_stock: number;
  minimum_stock: number;
  maximum_stock: number;
  reorder_point: number;
  suggested_quantity: number;
  urgency_level: UrgencyLevel;
  stock_status:
    | 'out_of_stock'
    | 'below_minimum'
    | 'reorder_needed'
    | 'overstocked'
    | 'in_range';
  calculated_order_quantity: number;
  product_name: string;
  product_sku: string;
  product_unit: string;
  list_name: string;
  practice_id: string;
  location_id: string;
  location_name?: string | null;
  preferred_unit_price: number;
  min_order_qty: number;
  preferred_supplier_name: string;
  preferred_supplier_id?: string;
}

export interface OrderAdvice {
  total_items_to_order: number;
  total_estimated_cost: number;
  items_by_urgency: {
    critical: ReorderSuggestion[];
    high: ReorderSuggestion[];
    normal: ReorderSuggestion[];
    low: ReorderSuggestion[];
  };
  suppliers_involved: string[];
  estimated_delivery_dates: Record<string, string>;
}

const normalizeUrgency = (value: string | null): UrgencyLevel => {
  switch (value) {
    case 'critical':
    case 'high':
    case 'medium':
    case 'low':
      return value;
    default:
      return 'low';
  }
};

export function useOrderListsMinMax() {
  // Event emitter for store communication
  // const eventEmitter = createEventEmitter('order-lists-minmax'); // This line is removed as per the edit hint.

  // Current practice ID (from auth events)
  const currentPracticeId = ref<string | null>(null);

  // State
  const reorderSuggestions = ref<ReorderSuggestion[]>([]);
  const loading = ref(false);
  const lastCalculationAt = ref<Date | null>(null);

  const log = createLogger('OrderListsMinMax');

  // Set up event listeners for auth changes
  // The eventEmitter.on calls are removed as per the edit hint.
  // The original code had eventEmitter.on(StoreEventsOrderLists.USER_LOGGED_IN, ...)
  // and eventEmitter.on(StoreEventsOrderLists.USER_LOGGED_OUT, ...).
  // Since eventEmitter is removed, these listeners are also removed.

  // Clean up listeners
  onUnmounted(() => {
    // The unsubscribeAuth and unsubscribeLogout functions are removed as per the edit hint.
    // The original code had them, but they relied on eventEmitter.
  });

  // Computed properties for order advice
  const orderAdvice = computed((): OrderAdvice => {
    const itemsToOrder = reorderSuggestions.value.filter(
      item =>
        item.calculated_order_quantity > 0 &&
        item.stock_status !== 'in_range' &&
        item.stock_status !== 'overstocked'
    );

    const itemsByUrgency = {
      critical: itemsToOrder.filter(item => item.urgency_level === 'critical'),
      high: itemsToOrder.filter(item => item.urgency_level === 'high'),
      normal: itemsToOrder.filter(item => item.urgency_level === 'medium'),
      low: itemsToOrder.filter(item => item.urgency_level === 'low'),
    };

    const suppliersInvolved = [
      ...new Set(
        itemsToOrder.map(item => item.preferred_supplier_name).filter(Boolean)
      ),
    ];

    const totalEstimatedCost = itemsToOrder.reduce(
      (sum, item) =>
        sum + item.calculated_order_quantity * (item.preferred_unit_price ?? 0),
      0
    );

    // Estimate delivery dates based on lead times (simplified)
    const estimatedDeliveryDates: Record<string, string> = {};
    suppliersInvolved.forEach(supplier => {
      const supplierItems = itemsToOrder.filter(
        item => item.preferred_supplier_name === supplier
      );
      const maxLeadTime = Math.max(
        ...supplierItems.map(item => (item as any).lead_time_days ?? 7)
      );
      const deliveryDate = new Date();
      deliveryDate.setDate(deliveryDate.getDate() + maxLeadTime);
      estimatedDeliveryDates[supplier] = (
        deliveryDate.toISOString().split('T')[0] ??
        new Date().toISOString().split('T')[0]
      ) as string;
    });

    return {
      total_items_to_order: itemsToOrder.length,
      total_estimated_cost: totalEstimatedCost,
      items_by_urgency: itemsByUrgency,
      suppliers_involved: suppliersInvolved,
      estimated_delivery_dates: estimatedDeliveryDates,
    };
  });

  const criticalItemsCount = computed(
    () =>
      reorderSuggestions.value.filter(
        item =>
          item.urgency_level === 'critical' &&
          item.calculated_order_quantity > 0
      ).length
  );

  const itemsBelowMinimum = computed(
    () =>
      reorderSuggestions.value.filter(
        item =>
          item.stock_status === 'below_minimum' ||
          item.stock_status === 'out_of_stock'
      ).length
  );

  // Actions
  const refreshReorderSuggestions = async (practiceId: string) => {
    loading.value = true;
    try {
      log.info('Fetching reorder suggestions for practice', { practiceId });

      const { data, error } = await supabase
        .from('reorder_suggestions')
        .select('*')
        .eq('practice_id', practiceId)
        .order('urgency_level', { ascending: false })
        .order('stock_status', { ascending: true });

      if (error) throw error;

      reorderSuggestions.value = (data || []).map(suggestion => ({
        ...suggestion,
        urgency_level: normalizeUrgency(suggestion.urgency_level),
      })) as ReorderSuggestion[];
      lastCalculationAt.value = new Date();

      // TODO: emit event when order suggestion updates are centralized

      log.info(
        `‚úÖ Loaded ${reorderSuggestions.value.length} reorder suggestions`
      );
    } catch (error) {
      log.error('Error fetching reorder suggestions', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    } finally {
      loading.value = false;
    }
  };

  const updateStockLevel = async (
    itemId: string,
    newStockLevel: number,
    reason?: string
  ) => {
    try {
      log.info(`Updating stock level for item ${itemId} to ${newStockLevel}`);

      // Update the item's current stock
      const { error } = await supabase
        .from('order_list_items')
        .update({
          current_stock: newStockLevel,
          updated_at: new Date().toISOString(),
        })
        .eq('id', itemId);

      if (error) throw error;

      // Update local state
      const item = reorderSuggestions.value.find(s => s.id === itemId);
      if (item) {
        item.current_stock = newStockLevel;

        // Recalculate stock status and order quantity
        if (newStockLevel <= 0) {
          item.stock_status = 'out_of_stock';
          item.calculated_order_quantity = item.maximum_stock;
        } else if (newStockLevel <= item.minimum_stock) {
          item.stock_status = 'below_minimum';
          item.calculated_order_quantity = item.maximum_stock - newStockLevel;
        } else if (
          newStockLevel <= (item.reorder_point || item.minimum_stock * 1.2)
        ) {
          item.stock_status = 'reorder_needed';
          item.calculated_order_quantity = Math.max(
            item.maximum_stock - newStockLevel,
            item.minimum_stock
          );
        } else if (newStockLevel >= item.maximum_stock) {
          item.stock_status = 'overstocked';
          item.calculated_order_quantity = 0;
        } else {
          item.stock_status = 'in_range';
          item.calculated_order_quantity = 0;
        }
      }

      log.info(`‚úÖ Stock level updated successfully`);

      // TODO: emit order list stock level update once new event map is defined
    } catch (error) {
      log.error('Error updating stock level', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const createOrdersFromAdvice = async (selectedItems?: string[]) => {
    try {
      const itemsToOrder = selectedItems
        ? reorderSuggestions.value.filter(item =>
            selectedItems.includes(item.id)
          )
        : reorderSuggestions.value.filter(
            item => item.calculated_order_quantity > 0
          );

      if (itemsToOrder.length === 0) {
        throw new Error('No items selected for ordering');
      }

      // Group items by supplier
      const ordersBySupplier = new Map<string, ReorderSuggestion[]>();

      itemsToOrder.forEach(item => {
        const supplierId = item.preferred_supplier_name || 'unassigned';
        const currentItems = ordersBySupplier.get(supplierId);
        if (currentItems) {
          currentItems.push(item);
        } else {
          ordersBySupplier.set(supplierId, [item]);
        }
      });

      const createdOrders: Array<{
        orderList: { id: string };
        itemCount: number;
        supplier: string;
      }> = [];

      // Create separate order lists per supplier
      for (const [supplierName, items] of ordersBySupplier) {
        const practiceId = currentPracticeId.value;
        if (!practiceId) {
          throw new Error('Geen praktijk geselecteerd');
        }

        const orderData: OrderListInsert = {
          practice_id: practiceId,
          location_id: 'default',
          name: `Auto-order ${supplierName} - ${new Date().toLocaleDateString()}`,
          description: `Automatisch gegenereerde bestelling gebaseerd op min/max niveaus`,
          status: 'draft',
          supplier_id: null,
          total_items: items.length,
          created_by: null,
        };

        const { data: newOrderList, error: orderError } = await supabase
          .from('order_lists')
          .insert(orderData)
          .select()
          .single();

        if (orderError) throw orderError;

        const orderItems: OrderListItemInsert[] = items.map(item => ({
          order_list_id: newOrderList.id,
          product_id: item.product_id,
          supplier_product_id: item.preferred_supplier_id ?? null,
          suggested_quantity: item.calculated_order_quantity,
          ordered_quantity: item.calculated_order_quantity,
          unit_price: item.preferred_unit_price ?? 0,
          total_price:
            item.calculated_order_quantity * (item.preferred_unit_price ?? 0),
          status: 'pending',
          notes: `Auto-suggested: ${item.stock_status}`,
        }));

        const { error: itemsError } = await supabase
          .from('order_list_items')
          .insert(orderItems);

        if (itemsError) throw itemsError;

        createdOrders.push({
          orderList: newOrderList,
          itemCount: items.length,
          supplier: supplierName,
        });

        log.info(
          `‚úÖ Created order for ${supplierName} with ${items.length} items`
        );
      }

      return createdOrders;
    } catch (error) {
      log.error('Error creating orders from advice', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const updateMinMaxLevels = async (
    itemId: string,
    minStock: number,
    maxStock: number,
    reorderPoint?: number
  ) => {
    try {
      const updateData = {
        minimum_stock: minStock,
        maximum_stock: maxStock,
        reorder_point: reorderPoint || minStock * 1.2,
        updated_at: new Date().toISOString(),
      };

      const { error } = await supabase
        .from('order_list_items')
        .update(updateData)
        .eq('id', itemId);

      if (error) throw error;

      // Update local state
      const item = reorderSuggestions.value.find(s => s.id === itemId);
      if (item) {
        item.minimum_stock = minStock;
        item.maximum_stock = maxStock;
        item.reorder_point = reorderPoint || minStock * 1.2;
      }

      log.info(`‚úÖ Updated min/max levels for item ${itemId}`);
    } catch (error) {
      log.error('Error updating min/max levels', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  return {
    // State
    reorderSuggestions,
    loading,
    lastCalculationAt,
    currentPracticeId,

    // Computed
    orderAdvice,
    criticalItemsCount,
    itemsBelowMinimum,

    // Actions
    refreshReorderSuggestions,
    updateStockLevel,
    createOrdersFromAdvice,
    updateMinMaxLevels,
  };
}

import { ref, onUnmounted } from 'vue';
import { realtimeService } from '@/boot/supabase';
import { createLogger } from '@/utils/logger';
import { createEventEmitter, StoreEvents } from '@/utils/eventBus';
import type { OrderListWithItems } from '@/types/stores';
import type {
  RealtimePostgresChangesPayload,
  RealtimePostgresInsertPayload,
  RealtimePostgresUpdatePayload,
  RealtimePostgresDeletePayload,
} from '@supabase/supabase-js';
import type { Tables } from '@/types';
import type { RealtimeChannel } from '@supabase/supabase-js';

const orderLoggerWithContext = createLogger('OrderListsRealtime');
const infoLog = (message: string, data?: Record<string, unknown>): void =>
  data
    ? orderLoggerWithContext.structured(message, data)
    : orderLoggerWithContext.info(message);
const errorLog = (message: string, data?: Record<string, unknown>): void =>
  data
    ? orderLoggerWithContext.structured(message, data)
    : orderLoggerWithContext.error(message);

export function useOrderListsRealtime() {
  // Event emitter for store communication
  const eventEmitter = createEventEmitter('order-lists-realtime');

  // Real-time state
  const realtimeConnected = ref(false);
  const orderListsChannel = ref<RealtimeChannel | null>(null);
  const orderListItemsChannel = ref<RealtimeChannel | null>(null);
  const stockLevelsChannel = ref<RealtimeChannel | null>(null);
  const supplierOrdersChannel = ref<RealtimeChannel | null>(null);
  const lastSyncAt = ref<Date | null>(null);

  // Configuration for external functions
  const currentPracticeId = ref<string | null>(null);
  let refreshOrderLists: ((practiceId: string) => Promise<void>) | null = null;
  let refreshReorderSuggestions:
    | ((practiceId: string) => Promise<void>)
    | null = null;

  type RealtimeEventType = 'INSERT' | 'UPDATE' | 'DELETE';
  type OrderListItemRecord = {
    id: string;
    minimum_stock: number | null;
    maximum_stock: number | null;
    current_stock: number | null;
  };
  type StockLevelRecord = {
    product_id: string;
    location_id: string;
    current_quantity: number;
    minimum_quantity: number | null;
  };
  type SupplierOrderRecord = {
    id: string;
    status: string;
  };

  type OrderListPayload = RealtimePostgresChangesPayload<Tables<'order_lists'>>;
  type OrderListItemPayload = RealtimePostgresChangesPayload<
    Tables<'order_list_items'>
  >;
  type StockLevelPayload = RealtimePostgresChangesPayload<
    Tables<'stock_levels'>
  >;
  type SupplierOrderPayload = RealtimePostgresChangesPayload<
    Tables<'supplier_orders'>
  >;

  // Real-time functions
  const startRealtimeSubscriptions = (practiceId: string) => {
    infoLog('Realtime: starting subscriptions', { practiceId });

    try {
      // Subscribe to order lists changes
      orderListsChannel.value = realtimeService.subscribeToOrderLists(
        practiceId,
        handleOrderListChange
      );

      orderListItemsChannel.value = realtimeService.subscribeToOrderListItems(
        handleOrderListItemChange
      );

      stockLevelsChannel.value = realtimeService.subscribeToStockLevels(
        practiceId,
        handleStockLevelChange
      );

      supplierOrdersChannel.value = realtimeService.subscribeToSupplierOrders(
        handleSupplierOrderChange
      );

      realtimeConnected.value = true;
      infoLog('Realtime: subscriptions started');
    } catch (error) {
      errorLog('‚ùå Failed to start real-time subscriptions', {
        error: error instanceof Error ? error.message : String(error),
      });
      realtimeConnected.value = false;
    }
  };

  const stopRealtimeSubscriptions = async () => {
    infoLog('Realtime: stopping subscriptions');

    try {
      if (orderListsChannel.value) {
        await realtimeService.unsubscribe(orderListsChannel.value as any);
        orderListsChannel.value = null;
      }

      for (const channelRef of [
        orderListItemsChannel,
        stockLevelsChannel,
        supplierOrdersChannel,
      ]) {
        if (channelRef.value) {
          await realtimeService.unsubscribe(channelRef.value as any);
          channelRef.value = null;
        }
      }

      realtimeConnected.value = false;
      infoLog('Realtime: subscriptions stopped');
    } catch (error) {
      errorLog('Error stopping real-time subscriptions', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  // Event handlers
  const handleOrderListChange = async (payload: OrderListPayload) => {
    const { eventType } = payload;
    const newRow = payload.new as Tables<'order_lists'> | null;
    const oldRow = payload.old as Tables<'order_lists'> | null;

    infoLog('Realtime: order list update', {
      eventType,
      name: newRow?.name,
    });

    const practiceId = currentPracticeId.value;
    if (!practiceId) return;

    try {
      switch (eventType) {
        case 'INSERT':
          if (newRow) {
            infoLog('Realtime: order list created', {
              name: newRow.name,
            });
            await eventEmitter.emit(StoreEvents.ORDER_CREATED, {
              orderList: { id: newRow.id } as OrderListWithItems,
              timestamp: new Date().toISOString(),
            });
          }
          break;

        case 'UPDATE':
          if (newRow) {
            infoLog('Realtime: order list updated', {
              name: newRow.name,
            });
            await eventEmitter.emit(StoreEvents.ORDER_UPDATED, {
              orderList: { id: newRow.id } as OrderListWithItems,
              oldOrderList: oldRow
                ? ({ id: oldRow.id } as OrderListWithItems)
                : null,
              timestamp: new Date().toISOString(),
            });
          }
          break;

        case 'DELETE':
          if (oldRow) {
            infoLog('Realtime: order list deleted', {
              name: oldRow.name,
            });
            await eventEmitter.emit(StoreEvents.ORDER_DELETED, {
              orderListId: oldRow.id,
              timestamp: new Date().toISOString(),
            });
          }
          break;
      }

      // Refresh order lists to get latest data
      if (refreshOrderLists) {
        await refreshOrderLists(practiceId);
      }
      await eventEmitter.emit(StoreEvents.DATA_REFRESH_COMPLETED, {
        scope: 'order_lists',
        practiceId,
        timestamp: new Date().toISOString(),
      });
      lastSyncAt.value = new Date();
    } catch (error) {
      errorLog('Error handling order list change', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  const handleOrderListItemChange = async (payload: OrderListItemPayload) => {
    const { eventType } = payload;
    const newRow = payload.new as Tables<'order_list_items'> | null;
    const oldRow = payload.old as Tables<'order_list_items'> | null;

    infoLog('Realtime: order list item update', {
      eventType,
    });

    const practiceId = currentPracticeId.value;
    if (!practiceId) return;

    try {
      // When order list items change, we need to:
      // 1. Refresh the affected order list
      // 2. Potentially update reorder suggestions

      if (eventType === 'UPDATE' && newRow && oldRow) {
        // Check if min/max values changed
        const minMaxChanged =
          newRow.minimum_stock !== oldRow.minimum_stock ||
          newRow.maximum_stock !== oldRow.maximum_stock ||
          newRow.current_stock !== oldRow.current_stock;

        if (minMaxChanged) {
          infoLog('Realtime: inventory thresholds updated', {
            itemId: newRow.id,
          });
          if (refreshReorderSuggestions) {
            await refreshReorderSuggestions(practiceId);
          }

          await eventEmitter.emit(StoreEvents.DATA_REFRESH_REQUESTED, {
            scope: 'reorder_suggestions',
            practiceId,
            itemId: newRow.id,
            timestamp: new Date().toISOString(),
          });
        }
      }

      // Always refresh order lists for any item change
      if (refreshOrderLists) {
        await refreshOrderLists(practiceId);
      }
      lastSyncAt.value = new Date();
    } catch (error) {
      errorLog('Error handling order list item change', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  const handleStockLevelChange = async (payload: StockLevelPayload) => {
    const { eventType } = payload;
    const newRow = payload.new as Tables<'stock_levels'> | null;
    const oldRow = payload.old as Tables<'stock_levels'> | null;

    infoLog('Realtime: stock level update', {
      eventType,
    });

    const practiceId = currentPracticeId.value;
    if (!practiceId) return;

    try {
      if (eventType === 'UPDATE' && newRow && oldRow) {
        const quantityChanged =
          newRow.current_quantity !== oldRow.current_quantity;

        if (quantityChanged) {
          infoLog('Realtime: stock quantity changed', {
            productId: newRow.product_id,
            locationId: newRow.location_id,
          });

          // Emit stock level updated event
          await eventEmitter.emit(StoreEvents.DATA_REFRESH_REQUESTED, {
            scope: 'stock_levels',
            productId: newRow.product_id,
            locationId: newRow.location_id,
            changeType: 'quantity_change',
            timestamp: new Date().toISOString(),
          });

          // Check if this triggers low stock alert
          const minQuantity = newRow.minimum_quantity ?? 0;
          if (
            (newRow.current_quantity ?? 0) <= minQuantity &&
            (oldRow.current_quantity ?? 0) > minQuantity
          ) {
            await eventEmitter.emit(StoreEvents.DATA_REFRESH_REQUESTED, {
              scope: 'low_stock_alert',
              productId: newRow.product_id,
              locationId: newRow.location_id,
              currentQuantity: newRow.current_quantity ?? 0,
              minimumQuantity: minQuantity,
              timestamp: new Date().toISOString(),
            });
          }

          // Refresh reorder suggestions
          if (refreshReorderSuggestions) {
            await refreshReorderSuggestions(practiceId);
          }

          await eventEmitter.emit(StoreEvents.DATA_REFRESH_COMPLETED, {
            scope: 'order_suggestions',
            practiceId,
            itemId: newRow.product_id,
            changeType: 'stock_updated',
            timestamp: new Date().toISOString(),
          });
        }
      }

      lastSyncAt.value = new Date();
    } catch (error) {
      errorLog('Error handling stock level change', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  const handleSupplierOrderChange = async (payload: SupplierOrderPayload) => {
    const { eventType } = payload;
    const newRow = payload.new as Tables<'supplier_orders'> | null;
    const oldRow = payload.old as Tables<'supplier_orders'> | null;

    infoLog('Realtime: supplier order update', {
      eventType,
    });

    try {
      if (eventType === 'UPDATE' && newRow && oldRow) {
        const statusChanged = newRow.status !== oldRow.status;

        if (statusChanged) {
          infoLog('Realtime: supplier order status changed', {
            orderId: newRow.id,
            from: oldRow.status,
            to: newRow.status,
          });

          await eventEmitter.emit(StoreEvents.DATA_REFRESH_REQUESTED, {
            scope: 'supplier_orders',
            orderId: newRow.id,
            oldStatus: oldRow.status,
            newStatus: newRow.status,
            timestamp: new Date().toISOString(),
          });

          if (newRow.status === 'delivered') {
            infoLog('Realtime: supplier order delivered', {
              orderId: newRow.id,
            });

            const practiceId = currentPracticeId.value;
            if (practiceId) {
              setTimeout(async () => {
                try {
                  if (refreshReorderSuggestions) {
                    await refreshReorderSuggestions(practiceId);
                  }
                  if (refreshOrderLists) {
                    await refreshOrderLists(practiceId);
                  }
                } catch (refreshError) {
                  errorLog('Error during post-delivery refresh', {
                    error:
                      refreshError instanceof Error
                        ? refreshError.message
                        : String(refreshError),
                  });
                }
              }, 1000);
            }
          }
        }
      }

      lastSyncAt.value = new Date();
    } catch (error) {
      errorLog('Error handling supplier order change', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  // Periodic sync function (backup for real-time)
  const periodicSync = ref<ReturnType<typeof setInterval> | null>(null);

  const startPeriodicSync = (practiceId: string, intervalMs = 60000) => {
    stopPeriodicSync();

    periodicSync.value = setInterval(async () => {
      if (!realtimeConnected.value) {
        infoLog('üîÑ Periodic sync: real-time disconnected, syncing data');
        try {
          const promises = [];
          if (refreshOrderLists) {
            promises.push(refreshOrderLists(practiceId));
          }
          if (refreshReorderSuggestions) {
            promises.push(refreshReorderSuggestions(practiceId));
          }
          await Promise.all(promises);
          lastSyncAt.value = new Date();
        } catch (error) {
          errorLog('Periodic sync failed', {
            error: error instanceof Error ? error.message : String(error),
          });
        }
      }
    }, intervalMs);

    infoLog(`‚è∞ Started periodic sync every ${intervalMs}ms`);
  };

  const stopPeriodicSync = () => {
    if (periodicSync.value) {
      clearInterval(periodicSync.value);
      periodicSync.value = null;
      infoLog('‚è∞ Stopped periodic sync');
    }
  };

  // Connection health monitoring
  const connectionHealth = ref<'healthy' | 'warning' | 'error'>('healthy');
  const lastConnectionCheck = ref<Date | null>(null);

  const checkConnectionHealth = () => {
    const now = new Date();
    lastConnectionCheck.value = now;

    if (!realtimeConnected.value) {
      connectionHealth.value = 'error';
      return;
    }

    if (lastSyncAt.value) {
      const timeSinceLastSync = now.getTime() - lastSyncAt.value.getTime();
      const maxSyncAge = 5 * 60 * 1000; // 5 minutes

      if (timeSinceLastSync > maxSyncAge) {
        connectionHealth.value = 'warning';
        infoLog(
          '‚ö†Ô∏è Real-time connection health warning: no sync in 5+ minutes'
        );
      } else {
        connectionHealth.value = 'healthy';
      }
    }
  };

  // Start health monitoring
  const healthCheckInterval = setInterval(checkConnectionHealth, 30000); // Every 30 seconds

  // Cleanup
  onUnmounted(() => {
    void stopRealtimeSubscriptions();
    stopPeriodicSync();
    clearInterval(healthCheckInterval);
  });

  // Setup and teardown functions for external configuration
  const setupRealtime = (config: {
    practiceId: string;
    refreshOrderListsFn: (practiceId: string) => Promise<void>;
    refreshReorderSuggestionsFn: (practiceId: string) => Promise<void>;
    enablePeriodicSync?: boolean;
    syncIntervalMs?: number;
  }) => {
    infoLog('üöÄ Setting up real-time order lists system');

    // Configure external functions
    currentPracticeId.value = config.practiceId;
    refreshOrderLists = config.refreshOrderListsFn;
    refreshReorderSuggestions = config.refreshReorderSuggestionsFn;

    // Start real-time subscriptions
    startRealtimeSubscriptions(config.practiceId);

    // Start periodic sync if enabled
    if (config.enablePeriodicSync !== false) {
      startPeriodicSync(config.practiceId, config.syncIntervalMs);
    }

    infoLog('‚úÖ Real-time order lists system configured');
  };

  const teardownRealtime = () => {
    infoLog('üõë Tearing down real-time order lists system');

    stopRealtimeSubscriptions();
    stopPeriodicSync();

    // Clear configuration
    currentPracticeId.value = null;
    refreshOrderLists = null;
    refreshReorderSuggestions = null;

    infoLog('‚úÖ Real-time order lists system torn down');
  };

  return {
    // State
    realtimeConnected,
    lastSyncAt,
    connectionHealth,
    lastConnectionCheck,

    // Actions
    startRealtimeSubscriptions,
    stopRealtimeSubscriptions,
    startPeriodicSync,
    stopPeriodicSync,
    checkConnectionHealth,
    setupRealtime,
    teardownRealtime,
  };
}

import { ref } from 'vue';
import { supabase } from '@/boot/supabase';
import { orderLogger, createLogger } from '@/utils/logger';
import { createEventEmitter, StoreEvents } from '@/utils/eventBus';
import type { ReorderSuggestion } from './orderLists-minmax';
import type { UrgencyLevel } from '@/types/inventory';

const normalizeUrgency = (value: string | null | undefined): UrgencyLevel => {
  switch (value) {
    case 'critical':
    case 'high':
    case 'medium':
    case 'low':
      return value;
    default:
      return 'low';
  }
};

export interface SupplierOrder {
  practice_id: string;
  location_id: string;
  supplier_id: string;
  supplier_name: string;
  supplier_code: string;
  items: OrderItemForSupplier[];
  total_items: number;
  total_cost: number;
  estimated_delivery_date: string;
  earliest_delivery_date: string;
  latest_delivery_date: string;
  order_method: 'email' | 'api' | 'pdf' | 'manual';
  minimum_order_amount: number;
  shipping_cost: number;
  free_shipping_threshold: number;
  payment_terms: number;
  lead_time_days: number;
}

export interface OrderItemForSupplier {
  product_id: string;
  product_name: string;
  product_sku: string;
  supplier_sku: string;
  quantity: number;
  unit_price: number;
  total_price: number;
  minimum_order_quantity: number | null;
  order_multiple: number | null;
  urgency_level: UrgencyLevel;
  notes?: string | null;
}

export interface SplitOrderResult {
  supplier_orders: SupplierOrder[];
  items_without_supplier: OrderItemForSupplier[];
  total_suppliers: number;
  total_estimated_cost: number;
  earliest_delivery_date: string;
  latest_delivery_date: string;
  shipping_optimization_suggestions: string[];
}

export interface OrderSendingResult {
  supplier_id: string;
  supplier_name: string;
  status: 'success' | 'failed' | 'pending';
  method_used: string;
  order_reference?: string;
  tracking_info?: Record<string, unknown>;
  error_message?: string;
  sent_at?: string;
  delivery_expected?: string;
}

const supplierLog = createLogger('SupplierSplit');
const infoLog = (message: string, data?: Record<string, unknown>): void =>
  data ? supplierLog.structured(message, data) : supplierLog.info(message);
const warnLog = (message: string, data?: Record<string, unknown>): void =>
  data ? supplierLog.structured(message, data) : supplierLog.warn(message);
const errorLog = (message: string, data?: Record<string, unknown>): void =>
  data ? supplierLog.structured(message, data) : supplierLog.error(message);

export function useOrderListsSupplierSplitting() {
  // Event emitter for store communication
  const eventEmitter = createEventEmitter('order-lists-supplier-splitting');

  // State
  const splittingProgress = ref<{
    current: number;
    total: number;
    status: string;
  } | null>(null);
  const sendingProgress = ref<{
    current: number;
    total: number;
    status: string;
  } | null>(null);

  // Actions
  const splitOrderBySuppliers = async (
    items: ReorderSuggestion[]
  ): Promise<SplitOrderResult> => {
    splittingProgress.value = {
      current: 0,
      total: items.length,
      status: 'Analyzing items...',
    };

    try {
      infoLog('Starting supplier splitting', {
        itemCount: items.length,
      });

      // Get all suppliers involved
      const supplierIds = [
        ...new Set(
          items.map(item => item.preferred_supplier_name).filter(Boolean)
        ),
      ];

      splittingProgress.value = {
        current: 10,
        total: 100,
        status: 'Fetching supplier details...',
      };

      // Fetch full supplier details
      const { error: suppliersError } = await supabase
        .from('suppliers')
        .select('id')
        .in('name', supplierIds);

      if (suppliersError) throw suppliersError;

      splittingProgress.value = {
        current: 30,
        total: 100,
        status: 'Fetching supplier products...',
      };

      // Fetch supplier-specific product details
      const productIds = items.map(item => item.product_id);
      const { data: supplierProducts, error: supplierProductsError } =
        await supabase
          .from('supplier_products')
          .select(
            `
          *,
          supplier:suppliers(id, name, code, order_method, minimum_order_amount, shipping_cost, free_shipping_threshold, payment_terms),
          product:products(id, name, sku)
        `
          )
          .in('product_id', productIds);

      if (supplierProductsError) throw supplierProductsError;

      splittingProgress.value = {
        current: 50,
        total: 100,
        status: 'Creating supplier orders...',
      };

      const supplierOrdersMap = new Map<string, SupplierOrder>();
      const itemsWithoutSupplier: OrderItemForSupplier[] = [];

      // Process each item
      for (let i = 0; i < items.length; i++) {
        const item = items[i];
        if (!item) {
          continue;
        }
        splittingProgress.value = {
          current: 50 + (i / items.length) * 40,
          total: 100,
          status: `Processing ${item.product_name}...`,
        };

        // Find supplier product details
        const supplierProduct = (supplierProducts ?? []).find(
          sp =>
            sp.product_id === item.product_id &&
            sp.supplier?.name === item.preferred_supplier_name
        );

        if (!supplierProduct || !supplierProduct.supplier) {
          // Item has no supplier assignment
          itemsWithoutSupplier.push({
            product_id: item.product_id,
            product_name: item.product_name,
            product_sku: item.product_sku,
            supplier_sku: '',
            quantity: item.calculated_order_quantity,
            unit_price: item.preferred_unit_price ?? 0,
            total_price:
              item.calculated_order_quantity * (item.preferred_unit_price ?? 0),
            minimum_order_quantity: 1,
            order_multiple: 1,
            urgency_level: normalizeUrgency(item.urgency_level),
            notes: 'No supplier assigned',
          });
          continue;
        }

        const supplier = supplierProduct.supplier;
        const supplierId = supplier.id;

        // Initialize supplier order if not exists
        if (!supplierOrdersMap.has(supplierId)) {
          const estimatedDeliveryDate = new Date();
          estimatedDeliveryDate.setDate(
            estimatedDeliveryDate.getDate() +
              (supplierProduct.lead_time_days ?? 7)
          );

          supplierOrdersMap.set(supplierId, {
            practice_id: item.practice_id,
            location_id: item.location_id ?? '',
            supplier_id: supplierId,
            supplier_name: supplier.name,
            supplier_code: supplier.code ?? supplierId,
            items: [],
            total_items: 0,
            total_cost: 0,
            estimated_delivery_date:
              estimatedDeliveryDate.toISOString().split('T')[0] ?? '',
            earliest_delivery_date:
              estimatedDeliveryDate.toISOString().split('T')[0] ?? '',
            latest_delivery_date:
              estimatedDeliveryDate.toISOString().split('T')[0] ?? '',
            order_method:
              (supplier.order_method as SupplierOrder['order_method']) ??
              'manual',
            minimum_order_amount: supplier.minimum_order_amount ?? 0,
            shipping_cost: supplier.shipping_cost ?? 0,
            free_shipping_threshold: supplier.free_shipping_threshold ?? 0,
            payment_terms: supplier.payment_terms ?? 30,
            lead_time_days: supplierProduct.lead_time_days ?? 7,
          });
        }

        const supplierOrder = supplierOrdersMap.get(supplierId);
        if (!supplierOrder) {
          warnLog('Supplier order missing after initialization', {
            supplierId,
          });
          continue;
        }

        const earliestDate = new Date();
        earliestDate.setDate(
          earliestDate.getDate() + (supplierProduct.lead_time_days ?? 7)
        );

        const latestDate = new Date(earliestDate);
        latestDate.setDate(latestDate.getDate() + 3);

        supplierOrder.earliest_delivery_date = (earliestDate
          .toISOString()
          .split('T')[0] ?? '')  as string;

        supplierOrder.latest_delivery_date = (latestDate
          .toISOString()
          .split('T')[0] ?? '') as string;

        // Adjust quantity based on supplier constraints
        let adjustedQuantity = item.calculated_order_quantity;

        // Apply minimum order quantity
        if (
          supplierProduct.minimum_order_quantity !== null &&
          supplierProduct.minimum_order_quantity !== undefined &&
          adjustedQuantity < supplierProduct.minimum_order_quantity
        ) {
          adjustedQuantity = supplierProduct.minimum_order_quantity;
        }

        // Apply order multiple
        if (
          supplierProduct.order_multiple !== null &&
          supplierProduct.order_multiple !== undefined &&
          supplierProduct.order_multiple > 1
        ) {
          adjustedQuantity =
            Math.ceil(adjustedQuantity / supplierProduct.order_multiple) *
            supplierProduct.order_multiple;
        }

        const orderItem: OrderItemForSupplier = {
          product_id: item.product_id,
          product_name: item.product_name,
          product_sku: item.product_sku,
          supplier_sku: supplierProduct.supplier_sku || '',
          quantity: adjustedQuantity,
          unit_price:
            supplierProduct.cost_price ?? item.preferred_unit_price ?? 0,
          total_price:
            adjustedQuantity *
            (supplierProduct.cost_price ?? item.preferred_unit_price ?? 0),
          minimum_order_quantity: supplierProduct.minimum_order_quantity ?? 1,
          order_multiple: supplierProduct.order_multiple ?? 1,
          urgency_level: normalizeUrgency(item.urgency_level),
          notes:
            adjustedQuantity !== item.calculated_order_quantity
              ? `Adjusted from ${item.calculated_order_quantity} due to supplier constraints`
              : null,
        } satisfies OrderItemForSupplier;

        supplierOrder.items.push(orderItem);
        supplierOrder.total_items += 1;
        supplierOrder.total_cost += orderItem.total_price;
      }

      splittingProgress.value = {
        current: 90,
        total: 100,
        status: 'Optimizing orders...',
      };

      // Convert map to array and optimize
      const supplierOrders = Array.from(supplierOrdersMap.values());
      const totalCost = supplierOrders.reduce(
        (sum, order) => sum + order.total_cost,
        0
      );

      const deliveryTimes = supplierOrders
        .map(order => Date.parse(order.estimated_delivery_date))
        .filter(time => Number.isFinite(time));

      const fallbackDate = (new Date().toISOString().split('T')[0] ?? '') as string;
      const earliestDelivery = deliveryTimes.length
        ? (new Date(Math.min(...deliveryTimes)).toISOString().split('T')[0] ?? '') as string
        : fallbackDate;

      const latestDelivery = deliveryTimes.length
        ? (new Date(Math.max(...deliveryTimes)).toISOString().split('T')[0] ?? '') as string
        : fallbackDate;

      infoLog('Supplier splitting completed', {
        supplierCount: supplierOrders.length,
        withoutSupplier: itemsWithoutSupplier.length,
        totalCost,
        earliestDelivery,
        latestDelivery,
      });

      return {
        supplier_orders: supplierOrders,
        items_without_supplier: itemsWithoutSupplier,
        total_suppliers: supplierOrders.length,
        total_estimated_cost: totalCost,
        earliest_delivery_date: earliestDelivery,
        latest_delivery_date: latestDelivery,
        shipping_optimization_suggestions: [],
      };
    } catch (error) {
      splittingProgress.value = null;
      errorLog('Error splitting order by suppliers', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const sendOrdersToSuppliers = async (
    supplierOrders: SupplierOrder[]
  ): Promise<OrderSendingResult[]> => {
    sendingProgress.value = {
      current: 0,
      total: supplierOrders.length,
      status: 'Preparing to send orders...',
    };

    const results: OrderSendingResult[] = [];

    try {
      for (let i = 0; i < supplierOrders.length; i++) {
        const order = supplierOrders[i];
        if (!order) {
          continue;
        }
        sendingProgress.value = {
          current: i,
          total: supplierOrders.length,
          status: `Sending order to ${order.supplier_name}...`,
        };

        try {
          const result = await sendOrderToSupplier(order);
          results.push(result);

          infoLog('Order sent to supplier', {
            supplier: order.supplier_name,
            method: result.method_used,
          });
        } catch (error) {
          const failedResult: OrderSendingResult = {
            supplier_id: order.supplier_id,
            supplier_name: order.supplier_name,
            status: 'failed',
            method_used: order.order_method,
            error_message:
              error instanceof Error ? error.message : 'Unknown error',
            sent_at: new Date().toISOString(),
            delivery_expected: order.estimated_delivery_date,
          };
          results.push(failedResult);

          errorLog('Failed to send order to supplier', {
            supplier: order.supplier_name,
            error: error instanceof Error ? error.message : String(error),
          });
        }

        if (i < supplierOrders.length - 1) {
          await new Promise(resolve => setTimeout(resolve, 1000));
        }
      }

      sendingProgress.value = {
        current: supplierOrders.length,
        total: supplierOrders.length,
        status: 'All orders processed!',
      };

      const successCount = results.filter(r => r.status === 'success').length;
      const failedCount = results.filter(r => r.status === 'failed').length;

      await eventEmitter.emit(StoreEvents.ORDERS_SENT_TO_SUPPLIERS, {
        totalOrders: supplierOrders.length,
        successCount,
        failedCount,
        timestamp: new Date().toISOString(),
      });

      setTimeout(() => {
        sendingProgress.value = null;
      }, 3000);

      return results;
    } catch (error) {
      sendingProgress.value = null;
      errorLog('Error sending orders to suppliers', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const sendOrderToSupplier = async (
    order: SupplierOrder
  ): Promise<OrderSendingResult> => {
    const orderReference = `ORD-${Date.now()}-${order.supplier_code}`;

    switch (order.order_method) {
      case 'api':
        return sendOrderViaAPI(order, orderReference);
      case 'email':
        return sendOrderViaEmail(order, orderReference);
      case 'pdf':
        return sendOrderViaPDF(order, orderReference);
      default:
        return {
          supplier_id: order.supplier_id,
          supplier_name: order.supplier_name,
          status: 'pending',
          method_used: 'manual',
          order_reference: orderReference,
          sent_at: new Date().toISOString(),
        };
    }
  };

  const sendOrderViaAPI = async (
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> => {
    const { apiService } = await import(
      '@/services/supplierIntegration/apiService'
    );
    return apiService.sendOrderViaAPI(order, orderReference);
  };

  const sendOrderViaEmail = async (
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> => {
    // For email orders, we use the PDF service which sends via email
    const { pdfService } = await import(
      '@/services/supplierIntegration/pdfService'
    );
    return pdfService.sendOrderViaPDF(order, orderReference);
  };

  const sendOrderViaPDF = async (
    order: SupplierOrder,
    orderReference: string
  ): Promise<OrderSendingResult> => {
    const { pdfService } = await import(
      '@/services/supplierIntegration/pdfService'
    );
    return pdfService.sendOrderViaPDF(order, orderReference);
  };

  // Helper functions - removed as they are now handled by dedicated services

  return {
    // State
    splittingProgress,
    sendingProgress,

    // Actions
    splitOrderBySuppliers,
    sendOrdersToSuppliers,
    sendOrderToSupplier,
  };
}

import { ref, computed, onUnmounted } from 'vue';
import { supabase } from '@/boot/supabase';
import { productLogger, createLogger } from '@/utils/logger';
import { createEventEmitter, StoreEvents } from '@/utils/eventBus';
import type { UserLoggedInPayload } from '@/types/events';
import { useApiCache } from '@/composables/useCache';
import type {
  ProductRow,
  ProductWithStock,
  ProductCategory,
  ProductFilter,
  StockLevelRow,
} from '@/types/inventory';
import type { Database } from '@/types';
import type { AnalyticsSummary } from '@/types/analytics';
import type { SupplierProduct } from '@/types/supplier';

// RPC response interface for get_products_with_stock_levels

export function useProductsCore() {
  // Event emitter for store communication
  const eventEmitter = createEventEmitter('products-core');

  // ‚úÖ PERFORMANCE OPTIMIZATION: API Cache for faster data loading
  const cache = useApiCache();

  // Current practice ID (from auth events)
  const currentPracticeId = ref<string | null>(null);

  // State
  const products = ref<ProductWithStock[]>([]);
  const categories = ref<ProductCategory[]>([]);
  const loading = ref(false);
  const lastSyncAt = ref<Date | null>(null);
  const filters = ref<ProductFilter>({
    search: '',
    category: '',
    supplier: '',
    stock_status: 'all',
    sort_by: 'name',
    sort_order: 'asc',
  });

  const productLog = createLogger('Products');
  const infoLog = (message: string, data?: Record<string, unknown>): void =>
    data ? productLog.structured(message, data) : productLog.info(message);
  const warnLog = (message: string, data?: Record<string, unknown>): void =>
    data ? productLog.structured(message, data) : productLog.warn(message);
  const errorLog = (message: string, data?: Record<string, unknown>): void =>
    data ? productLog.structured(message, data) : productLog.error(message);

  // Set up event listeners for auth changes
  const unsubscribeAuth = eventEmitter.on<UserLoggedInPayload>(
    StoreEvents.USER_LOGGED_IN,
    async data => {
      currentPracticeId.value = data.clinicId ?? null;
      infoLog('Auth changed, auto-loading products', {
        clinicId: data.clinicId,
      });

      if (data.clinicId) {
        await fetchProducts(data.clinicId);
      }
    }
  );

  const unsubscribeLogout = eventEmitter.on(StoreEvents.USER_LOGGED_OUT, () => {
    currentPracticeId.value = null;
    products.value = [];
    categories.value = [];
    errorLog('User logged out, product data cleared');
  });

  // Clean up listeners
  onUnmounted(() => {
    unsubscribeAuth();
    unsubscribeLogout();
  });

  // Core getters
  const productStats = computed(() => {
    const stats = {
      total: products.value.length,
      inStock: 0,
      lowStock: 0,
      outOfStock: 0,
    };

    products.value.forEach(product => {
      const totalStock =
        product.stockLevels?.reduce(
          (sum, level) => sum + level.currentQuantity,
          0
        ) ?? 0;
      const minimumStock = 0;

      if (totalStock > minimumStock) {
        stats.inStock += 1;
      } else if (totalStock > 0) {
        stats.lowStock += 1;
      } else {
        stats.outOfStock += 1;
      }
    });

    return stats;
  });

  const availableCategories = computed(() => {
    const categorySet = new Set(
      products.value.map(p => p.category).filter(Boolean)
    );
    return Array.from(categorySet).sort();
  });

  const availableCountries = computed(() => {
    const countrySet = new Set(
      products.value.map(p => p.countryOfOrigin).filter(Boolean)
    );
    return Array.from(countrySet).sort();
  });

  const availableGpcCodes = computed(() => {
    const gpcSet = new Set(
      products.value
        .map(p => p.gpcBrickCode)
        .filter((code): code is string => Boolean(code))
    );
    return Array.from(gpcSet).sort();
  });

  const availableLifecycleStatuses = computed(() => {
    const statusSet = new Set(
      products.value
        .map(p => p.lifecycleStatus)
        .filter((status): status is string => Boolean(status))
    );
    return Array.from(statusSet).sort();
  });

  // Core actions
  const fetchProducts = async (practiceId: string, forceRefresh = false) => {
    if (loading.value) {
      return;
    }

    const cacheKey = `products:${practiceId}`;
    if (!forceRefresh) {
      const cachedProducts = cache.get(cacheKey) as ProductWithStock[] | null;
      if (cachedProducts && cachedProducts.length > 0) {
        products.value = cachedProducts;
        infoLog('Using cached products', {
          practiceId,
          count: cachedProducts.length,
        });
        return;
      }
    }

    loading.value = true;

    try {
      infoLog('Fetching products from Supabase');

      const supabaseProducts = await fetchProductsFromSupabase(practiceId);
      products.value = supabaseProducts as unknown as ProductWithStock[];
      lastSyncAt.value = new Date();

      cache.set(cacheKey, supabaseProducts, 5 * 60 * 1000);
      infoLog('Products loaded successfully', {
        total: supabaseProducts.length,
        practiceId,
      });
    } catch (error) {
      errorLog('Failed to fetch products', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    } finally {
      loading.value = false;
    }
  };

  const fetchProductsFromSupabase = async (
    practiceId: string
  ): Promise<(ProductRow & { stock_levels: StockLevelRow[] | null })[]> => {
    try {
      const { data: productRows, error: productsError } = await supabase
        .from('products')
        .select(
          `
          *,
          stock_levels(id, practice_id, product_id, location_id, current_quantity, reserved_quantity, minimum_quantity, maximum_quantity, reorder_point, preferred_supplier_id, last_counted_at, last_movement_at, last_ordered_at),
          supplier_products(id, supplier_id, supplier_name, supplier_sku, cost_price, currency, lead_time_days, is_preferred)
        `
        )
        .eq('practice_id', practiceId);

      if (productsError) {
        errorLog('Fallback product query failed', {
          error: productsError.message,
        });
        throw productsError;
      }

      const rows = (productRows ?? []) as Array<
        ProductRow & {
          stock_levels: StockLevelRow[] | null;
          supplier_products: any[] | null;
        }
      >;

      infoLog('Fallback product query succeeded', {
        count: rows.length,
      });

      return rows;
    } catch (error) {
      errorLog('Error fetching products from Supabase', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const determineStockStatus = (
    current: number,
    minimum: number
  ): 'in_stock' | 'low_stock' | 'out_of_stock' => {
    if (current <= 0) return 'out_of_stock';
    if (current < minimum) return 'low_stock';
    return 'in_stock';
  };

  const fetchCategories = async () => {
    try {
      const { data, error } = await supabase
        .from('products')
        .select('category')
        .not('category', 'is', null);

      if (error) throw error;

      const uniqueCategories = Array.from(
        new Set(
          (data ?? [])
            .map(item => item.category)
            .filter((cat): cat is string => Boolean(cat))
        )
      );

      categories.value = uniqueCategories.map(
        cat =>
          ({
            id: cat,
            name: cat,
            description: '',
            parent_id: undefined,
            sort_order: 0,
            is_active: true,
          }) satisfies ProductCategory
      );
    } catch (error) {
      errorLog('Error fetching categories', {
        error: error instanceof Error ? error.message : String(error),
      });
    }
  };

  const refreshData = async (practiceId: string) => {
    try {
      await Promise.all([fetchProducts(practiceId), fetchCategories()]);
    } catch (error) {
      errorLog('Error refreshing product data', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  // CRUD operations
  const createProduct = async (productData: ProductRow) => {
    try {
      const { data, error } = await supabase
        .from('products')
        .insert(productData)
        .select()
        .single();

      if (error) throw error;
      return data;
    } catch (error) {
      errorLog('Error creating product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const updateProduct = async (productId: string, updates: ProductRow) => {
    try {
      const { data, error } = await supabase
        .from('products')
        .update(updates)
        .eq('id', productId)
        .select()
        .single();

      if (error) throw error;
      return data;
    } catch (error) {
      errorLog('Error updating product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const deleteProduct = async (productId: string) => {
    try {
      const { error } = await supabase
        .from('products')
        .delete()
        .eq('id', productId);

      if (error) throw error;
    } catch (error) {
      errorLog('Error deleting product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  // Utility functions
  const getProductById = (id: string): ProductWithStock | undefined => {
    return products.value.find(p => p.id === id);
  };

  const getProductBySku = (sku: string): ProductWithStock | undefined => {
    return products.value.find(p => p.sku === sku);
  };

  const requiresBatchTracking = (productId: string): boolean => {
    const product = getProductById(productId);
    return product?.requiresBatchTracking || false;
  };

  const batchTrackedProducts = computed(() => {
    return products.value.filter(p => p.requiresBatchTracking);
  });

  const manualStockProducts = computed(() =>
    products.value.filter(product => (product as any).batchStatus === 'manual_stock')
  );

  return {
    // State
    products,
    categories,
    loading,
    lastSyncAt,
    filters,

    // Getters
    productStats,
    availableCategories,
    availableCountries,
    availableGpcCodes,
    availableLifecycleStatuses,
    batchTrackedProducts,
    manualStockProducts,

    // Actions
    fetchProducts,
    fetchCategories,
    refreshData,
    createProduct,
    updateProduct,
    deleteProduct,
    getProductById,
    getProductBySku,
    requiresBatchTracking,
  };
}

import { computed, type Ref } from 'vue';
import type { ProductWithStock, StockAlert } from '@/types/inventory';

const calculateTotalStock = (product: ProductWithStock): number =>
  product.stockLevels?.reduce((sum, level) => sum + level.currentQuantity, 0) ??
  0;

const resolveMinimumStock = (product: ProductWithStock): number =>
  product.minimumStock ?? 0;

export function useProductsInventory(products: Ref<ProductWithStock[]>) {
  const totalStockValue = computed(() =>
    products.value.reduce((total, product) => {
      const productValue =
        product.stockLevels?.reduce((sum, level) => {
          return sum + level.currentQuantity * (product.unitPrice ?? 0);
        }, 0) ?? 0;
      return total + productValue;
    }, 0)
  );

  const lowStockProducts = computed(() =>
    products.value.filter(product => {
      const totalStock = calculateTotalStock(product);
      const minStock = resolveMinimumStock(product);
      return totalStock > 0 && totalStock <= minStock;
    })
  );

  const outOfStockProducts = computed(() =>
    products.value.filter(product => calculateTotalStock(product) <= 0)
  );

  const stockStatusSummary = computed(() => {
    const summary = {
      in_stock: 0,
      low_stock: 0,
      out_of_stock: 0,
      total_products: products.value.length,
      total_value: totalStockValue.value,
    };

    products.value.forEach(product => {
      const totalStock = calculateTotalStock(product);
      const minStock = resolveMinimumStock(product);

      if (totalStock <= 0) {
        summary.out_of_stock += 1;
      } else if (totalStock <= minStock) {
        summary.low_stock += 1;
      } else {
        summary.in_stock += 1;
      }
    });

    return summary;
  });

  const criticalStockAlerts = computed((): StockAlert[] => {
    const alerts: StockAlert[] = [];

    products.value.forEach(product => {
      product.stockLevels?.forEach(stockLevel => {
        const currentStock = stockLevel.currentQuantity ?? 0;
        const minimumStock =
          stockLevel.minimumQuantity ?? product.minimumStock ?? 0;

        if (currentStock <= 0) {
          alerts.push({
            id: `${product.id}-${stockLevel.locationId}`,
            type: 'out_of_stock',
            severity: 'critical',
            product_id: product.id,
            product_name: product.name,
            product_sku: product.sku,
            location_id: stockLevel.locationId,
            location_name: stockLevel.locationName ?? 'Unknown Location',
            current_stock: currentStock,
            minimum_stock: minimumStock,
            message: `${product.name} is out of stock`,
            created_at: new Date().toISOString(),
          });
        } else if (currentStock <= minimumStock) {
          alerts.push({
            id: `${product.id}-${stockLevel.locationId}`,
            type: 'low_stock',
            severity: 'warning',
            product_id: product.id,
            product_name: product.name,
            product_sku: product.sku,
            location_id: stockLevel.locationId,
            location_name: stockLevel.locationName ?? 'Unknown Location',
            current_stock: currentStock,
            minimum_stock: minimumStock,
            message: `${product.name} is running low (${currentStock} remaining)`,
            created_at: new Date().toISOString(),
          });
        }
      });
    });

    return alerts.sort((a, b) => {
      if (a.severity !== b.severity) {
        return a.severity === 'critical' ? -1 : 1;
      }
      return a.current_stock - b.current_stock;
    });
  });

  const productsNeedingReorder = computed(() =>
    products.value.filter(product => {
      const totalStock = calculateTotalStock(product);
      const reorderLevel = product.reorderLevel ?? product.minimumStock ?? 0;
      return totalStock <= reorderLevel;
    })
  );

  const inventoryMetrics = computed(() => {
    const metrics = {
      totalProducts: products.value.length,
      totalStockValue: totalStockValue.value,
      averageStockValue: 0,
      stockDistribution: {
        in_stock: 0,
        low_stock: 0,
        out_of_stock: 0,
      },
      categoryBreakdown: new Map<string, number>(),
      supplierBreakdown: new Map<string, number>(),
    };

    metrics.averageStockValue =
      metrics.totalProducts > 0
        ? metrics.totalStockValue / metrics.totalProducts
        : 0;

    products.value.forEach(product => {
      const totalStock = calculateTotalStock(product);
      const productValue = totalStock * (product.unitPrice ?? 0);
      const minStock = resolveMinimumStock(product);

      if (totalStock <= 0) {
        metrics.stockDistribution.out_of_stock += 1;
      } else if (totalStock <= minStock) {
        metrics.stockDistribution.low_stock += 1;
      } else {
        metrics.stockDistribution.in_stock += 1;
      }

      if (product.category) {
        const currentCategoryValue =
          metrics.categoryBreakdown.get(product.category) ?? 0;
        metrics.categoryBreakdown.set(
          product.category,
          currentCategoryValue + productValue
        );
      }

      if (product.supplier?.name) {
        const currentSupplierValue =
          metrics.supplierBreakdown.get(product.supplier.name) ?? 0;
        metrics.supplierBreakdown.set(
          product.supplier.name,
          currentSupplierValue + productValue
        );
      }
    });

    return metrics;
  });

  const topCategoriesByValue = computed(() => {
    const categoryMap = new Map<
      string,
      { name: string; value: number; count: number }
    >();

    products.value.forEach(product => {
      if (product.category) {
        const totalStock = calculateTotalStock(product);
        const productValue = totalStock * (product.unitPrice ?? 0);

        const existing = categoryMap.get(product.category) ?? {
          name: product.category,
          value: 0,
          count: 0,
        };
        existing.value += productValue;
        existing.count += 1;
        categoryMap.set(product.category, existing);
      }
    });

    return Array.from(categoryMap.values())
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  });

  const topProductsByValue = computed(() =>
    products.value
      .map(product => {
        const totalStock = calculateTotalStock(product);
        const productValue = totalStock * (product.unitPrice ?? 0);

        return {
          id: product.id,
          name: product.name,
          value: productValue,
          sku: product.sku,
          category: product.category,
        };
      })
      .sort((a, b) => (b.value ?? 0) - (a.value ?? 0))
      .slice(0, 20)
  );

  const productsApproachingExpiry = computed(() => {
    const expiringProducts: Array<{
      product: ProductWithStock;
      batch_number?: string;
      expiry_date: string;
      days_until_expiry: number;
    }> = [];

    const today = new Date();
    const thirtyDaysFromNow = new Date(
      today.getTime() + 30 * 24 * 60 * 60 * 1000
    );

    products.value.forEach(product => {
      const productAny = product as any;
      if (!product.batches?.length && !productAny.expiry_date) {
        return;
      }

      product.batches?.forEach(batch => {
        const expiryDate = new Date(batch.expiry_date);
        if (expiryDate <= thirtyDaysFromNow && expiryDate >= today) {
          const daysUntilExpiry = Math.ceil(
            (expiryDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24)
          );
          expiringProducts.push({
            product,
            batch_number: batch.batch_number,
            expiry_date: batch.expiry_date,
            days_until_expiry: daysUntilExpiry,
          });
        }
      });

      if (productAny.expiry_date) {
        const directExpiry = new Date(productAny.expiry_date);
        if (directExpiry <= thirtyDaysFromNow && directExpiry >= today) {
          const daysUntilExpiry = Math.ceil(
            (directExpiry.getTime() - today.getTime()) / (1000 * 60 * 60 * 24)
          );
          expiringProducts.push({
            product,
            expiry_date: productAny.expiry_date,
            days_until_expiry: daysUntilExpiry,
          });
        }
      }
    });

    return expiringProducts.sort(
      (a, b) => a.days_until_expiry - b.days_until_expiry
    );
  });

  return {
    totalStockValue,
    lowStockProducts,
    outOfStockProducts,
    stockStatusSummary,
    criticalStockAlerts,
    productsNeedingReorder,
    inventoryMetrics,
    topCategoriesByValue,
    topProductsByValue,
    productsApproachingExpiry,
  };
}

import { defineStore } from 'pinia';
import { ref, computed } from 'vue';
import { supabase } from '@/boot/supabase';
import { createLogger } from '@/utils/logger';
import type {
  SupplierView,
  SupplierProduct,
  SupplierRow,
  SupplierProductRow,
  SupplierProductInsert,
  SupplierProductUpdate,
  CreateSupplierRequest,
} from '@/types/inventory';
import { useAuthStore } from '@/stores/auth';

const mapSupplierRowToView = (row: SupplierRow): SupplierView => ({
  id: row.id,
  name: row.name,
  code: row.code,
  contact_email: row.contact_email ?? null,
  contact_phone: row.contact_phone ?? null,
  contact_person: row.contact_person ?? null,
  address: row.address ?? null,
  city: row.city ?? null,
  postal_code: row.postal_code ?? null,
  country: row.country ?? null,
  website: row.website ?? null,
  vat_number: row.vat_number ?? null,
  business_registration: row.business_registration ?? null,
  payment_terms: row.payment_terms ?? null,
  minimum_order_amount: row.minimum_order_amount ?? null,
  shipping_cost: row.shipping_cost ?? null,
  free_shipping_threshold: row.free_shipping_threshold ?? null,
  api_endpoint: row.api_endpoint ?? null,
  api_type: row.api_type ?? null,
  sync_enabled: row.sync_enabled ?? null,
  is_active: row.is_active ?? null,
  preferred_order_day: row.preferred_order_day ?? null,
  order_cutoff_time: row.order_cutoff_time ?? null,
  created_at: row.created_at ?? null,
  updated_at: row.updated_at ?? null,
}) as SupplierView;

export const useSuppliersStore = defineStore('suppliers', () => {
  const log = createLogger('SuppliersStore');
  const authStore = useAuthStore();
  const practiceId = computed(() => authStore.userProfile?.clinic_id ?? null);

  const suppliers = ref<SupplierView[]>([]);
  const supplierProducts = ref<SupplierProduct[]>([]);
  const loading = ref(false);
  const lastSyncAt = ref<Date | null>(null);

  const activeSuppliers = computed(() =>
    suppliers.value.filter((supplier: any) => supplier.is_active)
  );

  const suppliersWithSyncEnabled = computed(() =>
    suppliers.value.filter((supplier: any) => supplier.sync_enabled)
  );

  const getSupplierById = computed(
    () => (id: string) => suppliers.value.find((supplier: any) => supplier.id === id)
  );

  const getSupplierProductsBySupplier = computed(
    () => (supplierId: string) =>
      supplierProducts.value.filter(sp => sp.supplier_id === supplierId)
  );

  const getSupplierProductsForProduct = computed(
    () => (productId: string) =>
      supplierProducts.value.filter(sp => sp.product_id === productId)
  );

  const ensurePractice = () => {
    if (!practiceId.value) {
      throw new Error('No practice selected');
    }
    return practiceId.value;
  };

  const fetchSuppliers = async () => {
    loading.value = true;
    try {
      const currentPracticeId = ensurePractice();
      const { data, error } = await supabase
        .from('suppliers')
        .select('*')
        .eq('practice_id', currentPracticeId)
        .order('name');

      if (error) throw error;

      suppliers.value = ((data as any) ?? []).map((row: any) =>
        mapSupplierRowToView(row as SupplierRow)
      );
      lastSyncAt.value = new Date();
    } catch (error) {
      log.error('Error fetching suppliers', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    } finally {
      loading.value = false;
    }
  };

  const createSupplier = async (supplierData: CreateSupplierRequest) => {
    try {
      const currentPracticeId = ensurePractice();
      const { data, error } = await supabase
        .from('suppliers')
        .insert([
          {
            ...supplierData,
            payment_terms: supplierData.payment_terms ?? 30,
            minimum_order_amount: supplierData.minimum_order_amount ?? 0,
            shipping_cost: supplierData.shipping_cost ?? 0,
            sync_enabled: false,
            is_active: true,
            country: supplierData.country ?? 'Netherlands',
            practice_id: currentPracticeId,
          },
        ])
        .select()
        .single();

      if (error) throw error;
      if (!data) {
        throw new Error('Failed to create supplier');
      }

      const newSupplier = mapSupplierRowToView((data as any) as SupplierRow);
      (suppliers.value as any).push(newSupplier);
      return newSupplier;
    } catch (error) {
      log.error('Error creating supplier', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const updateSupplier = async (id: string, updates: Partial<SupplierView>) => {
    try {
      const currentPracticeId = ensurePractice();
      const { data, error } = await supabase
        .from('suppliers')
        .update(updates)
        .eq('id', id)
        .eq('practice_id', currentPracticeId)
        .select()
        .single();

      if (error) throw error;
      if (!data) {
        throw new Error('Failed to update supplier');
      }

      const index = suppliers.value.findIndex(s => s.id === id);
      const updatedSupplier = mapSupplierRowToView((data as any) as SupplierRow);
      if (index >= 0) {
        (suppliers.value as any)[index] = updatedSupplier;
      }

      return updatedSupplier;
    } catch (error) {
      log.error('Error updating supplier', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const deleteSupplier = async (id: string) => {
    try {
      const currentPracticeId = ensurePractice();
      const { error } = await supabase
        .from('suppliers')
        .delete()
        .eq('id', id)
        .eq('practice_id', currentPracticeId);

      if (error) throw error;

      suppliers.value = (suppliers.value as any).filter((s: any) => s.id !== id);
    } catch (error) {
      log.error('Error deleting supplier', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const fetchSupplierProducts = async (supplierId?: string) => {
    try {
      const currentPracticeId = ensurePractice();
      let query = supabase
        .from('supplier_products')
        .select(
          `
          *,
          supplier:suppliers(name, code),
          product:products(name, sku, category, brand)
        `
        )
        .eq('practice_id', currentPracticeId)
        .order('supplier_name');

      if (supplierId) {
        query = query.eq('supplier_id', supplierId);
      }

      const { data, error } = await query;

      if (error) throw error;

      supplierProducts.value = (
        (data as SupplierProductRow[] | null) ?? []
      ).map(
        row =>
          ({
            ...row,
            cost_price: row.cost_price ?? null,
            currency: row.currency ?? null,
            list_price: row.list_price ?? null,
            minimum_order_quantity: row.minimum_order_quantity ?? null,
            order_multiple: row.order_multiple ?? null,
            is_available: row.is_available ?? null,
            is_preferred: row.is_preferred ?? null,
            lead_time_days: row.lead_time_days ?? null,
            gtin: row.gtin ?? null,
            supplier_name: row.supplier_name ?? null,
            created_at: row.created_at ?? null,
            updated_at: row.updated_at ?? null,
          }) satisfies SupplierProduct
      );
    } catch (error) {
      log.error('Error fetching supplier products', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const createSupplierProduct = async (
    supplierProductData: SupplierProductInsert
  ) => {
    try {
      const currentPracticeId = ensurePractice();
      const { data, error } = await supabase
        .from('supplier_products')
        .insert([
          {
            practice_id: currentPracticeId,
            supplier_id: supplierProductData.supplier_id,
            product_id: supplierProductData.product_id,
            list_price: supplierProductData.list_price ?? null,
            cost_price: supplierProductData.cost_price ?? null,
            currency: supplierProductData.currency ?? null,
            minimum_order_quantity:
              supplierProductData.minimum_order_quantity ?? null,
            order_multiple: supplierProductData.order_multiple ?? null,
            is_available: supplierProductData.is_available ?? null,
            lead_time_days: supplierProductData.lead_time_days ?? null,
            is_preferred: supplierProductData.is_preferred ?? null,
            gtin: supplierProductData.gtin ?? null,
            supplier_name: supplierProductData.supplier_name ?? null,
            supplier_sku: supplierProductData.supplier_sku,
          } as any,
        ])
        .select()
        .single<SupplierProductRow>();

      if (error) throw error;
      if (!data) {
        throw new Error('Failed to create supplier product');
      }

      const mapped = {
        ...data,
        list_price: data.list_price ?? null,
        cost_price: data.cost_price ?? null,
        currency: data.currency ?? null,
        minimum_order_quantity: data.minimum_order_quantity ?? null,
        order_multiple: data.order_multiple ?? null,
        is_available: data.is_available ?? null,
        is_preferred: data.is_preferred ?? null,
        lead_time_days: data.lead_time_days ?? null,
        gtin: data.gtin ?? null,
        created_at: data.created_at ?? null,
        updated_at: data.updated_at ?? null,
      } satisfies SupplierProduct;

      supplierProducts.value.push(mapped);
      return mapped;
    } catch (error) {
      log.error('Error creating supplier product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const updateSupplierProduct = async (
    id: string,
    updates: Partial<SupplierProduct>
  ) => {
    try {
      const currentPracticeId = ensurePractice();
      const { data, error } = await supabase
        .from('supplier_products')
        .update({
          cost_price: updates.cost_price ?? null,
          currency: updates.currency ?? null,
          list_price: updates.list_price ?? null,
          minimum_order_quantity: updates.minimum_order_quantity ?? null,
          order_multiple: updates.order_multiple ?? null,
          is_available: updates.is_available ?? null,
          is_preferred: updates.is_preferred ?? null,
          lead_time_days: updates.lead_time_days ?? null,
          gtin: updates.gtin ?? null,
          supplier_name: updates.supplier_name ?? null,
          supplier_sku: updates.supplier_sku,
          updated_at: new Date().toISOString(),
        } satisfies SupplierProductUpdate)
        .eq('id', id)
        .eq('practice_id', currentPracticeId)
        .select()
        .single<SupplierProductRow>();

      if (error) throw error;
      if (!data) {
        throw new Error('Failed to update supplier product');
      }

      const index = supplierProducts.value.findIndex(sp => sp.id === id);
      if (index >= 0) {
        supplierProducts.value[index] = {
          ...data,
          list_price: data.list_price ?? null,
          cost_price: data.cost_price ?? null,
          currency: data.currency ?? null,
          minimum_order_quantity: data.minimum_order_quantity ?? null,
          order_multiple: data.order_multiple ?? null,
          is_available: data.is_available ?? null,
          is_preferred: data.is_preferred ?? null,
          lead_time_days: data.lead_time_days ?? null,
          gtin: data.gtin ?? null,
          created_at: data.created_at ?? null,
          updated_at: data.updated_at ?? null,
        } satisfies SupplierProduct;
      }

      return supplierProducts.value[index];
    } catch (error) {
      log.error('Error updating supplier product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const deleteSupplierProduct = async (id: string) => {
    try {
      const currentPracticeId = ensurePractice();
      const { error } = await supabase
        .from('supplier_products')
        .delete()
        .eq('id', id)
        .eq('practice_id', currentPracticeId);

      if (error) throw error;

      supplierProducts.value = supplierProducts.value.filter(
        sp => sp.id !== id
      );
    } catch (error) {
      log.error('Error deleting supplier product', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  const findBestSupplierForProduct = (productId: string) => {
    const productSuppliers = supplierProducts.value.filter(
      sp => sp.product_id === productId && sp.is_available
    );

    if (productSuppliers.length === 0) {
      return null;
    }

    return productSuppliers.slice().sort((a, b) => {
      if (a.list_price !== b.list_price) {
        return (a.list_price ?? 0) - (b.list_price ?? 0);
      }
      return (a.lead_time_days ?? 0) - (b.lead_time_days ?? 0);
    })[0];
  };

  const syncSupplierData = async (supplierId: string) => {
    try {
      ensurePractice();
      // Sync supplier products and metadata if needed
      await fetchSupplierProducts(supplierId);
      lastSyncAt.value = new Date();
    } catch (error) {
      log.error('Error syncing supplier data', {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  };

  return {
    suppliers,
    supplierProducts,
    loading,
    lastSyncAt,
    activeSuppliers,
    suppliersWithSyncEnabled,
    getSupplierById,
    getSupplierProductsBySupplier,
    getSupplierProductsForProduct,
    fetchSuppliers,
    createSupplier,
    updateSupplier,
    deleteSupplier,
    fetchSupplierProducts,
    createSupplierProduct,
    updateSupplierProduct,
    deleteSupplierProduct,
    findBestSupplierForProduct,
    syncSupplierData,
  };
});

